				}
			};

			scwin.gridInvntPsn_change = function(row, col, colId) {
				if(colId === "syspayno"){
					// 소연심의 담당자 조회
			        scwin.search_dept_manager();
			    }
			    if (colId == "blong_clsf") {
			        // 발명자 그리드 셋팅
			        scwin.set_gridInvntPsn(row);
			    }			
			};

			scwin.radioSjc_onchange = function(info) {
				const type = info.newValue;

				tabContainer1.hideTab(1);
				tabContainer1.hideTab(2);
				if (/PAT030/.test(type)) { // 상표
				    group11.hide() // 기술적특징
				    tabContainer1.showTab(2);
				} else if (/PAT040/.test(type)) { // 디자인
				    group11.hide() // 기술적특징
				    tabContainer1.showTab(1);
				} else {
				    group11.show("table");
				}

			};

			scwin.word_count = function(e) {
				const textAreaType = $p.getComponentById(e.target.id).getUserData('userData1');
				const alertText = $p.getComponentById(e.target.id).getUserData('userData2') || '';
				let limitLen = $p.getComponentById(e.target.id).getAttribute("maxlength");
				let msglen = 0;

				switch (textAreaType) {
				    case "textareaTechField": // 배경(종래)기술
				    	msglen = textareaTechField.getValue().length;
				    	inputCount01.setValue(msglen);
				        break;
				     case "textareaSolveTask": // 과제해결수단
				    	msglen = textareaSolveTask.getValue().length;
				    	inputCount03.setValue(msglen);
				        break;
				     case "textareaTransNm": // 기술적 특징 상세한 설명
				    	msglen = textareaTransNm.getValue().length;
				    	inputCount04.setValue(msglen);
				        break;
				     case "textareaInvntEfct": // 발명의 효과는
				    	msglen = textareaInvntEfct.getValue().length;
				    	inputCount05.setValue(msglen);
				        break;
				     case "textareaIndstUseClsf": // 발명을 하게된 경위
				    	msglen = textareaIndstUseClsf.getValue().length;
				    	inputCount02.setValue(msglen);
				        break;
				     case "textareaRelatInvnt": // 관련특허
				    	const textLength = textareaRelatInvnt.getValue().length;
				    	inputCount06.setValue(textLength);
				        break;
				    default:
				        com.alert("정보가 존재하지 않습니다.");
				}

				if (msglen > limitLen - 1) {
				    com.alert(alertText + " " + limitLen + "자를 초과할 수 없습니다.");
				}
			};

