			scwin.download_file = function() {
				let save = "DftFreeInvnt";
				let real = encodeURIComponent("자유발명신고서.hwp");	
				const url = "/SvcAttachDown.do?fileName=/pat/"+save+"&realName="+real+"&bizKind=OLD";
				window.open(url, '_blank').focus();	
			};

			scwin.save_onclick = function(e) {

				if( !scwin.pre_save() ) return;

				const isApproval = $p.getComponentById(e.target.id).getUserData('userData1') || "false";
				dma_save.set("approval", isApproval);
				scwin.param.state = $p.getComponentById(e.target.id).getUserData('userData2'); // state

				let confirmMessage = "저장";

				// isApproval이 "true" (문자열)인 경우
				if (isApproval === "true") {
				    confirmMessage = "결재";
				}
				// isApproval이 "false" (문자열)이면서 state가 특정 값인 경우
				else if (isApproval === "false") {
				    if (scwin.param.state === "10000") {
				        confirmMessage = "임시저장";
				    } else if (scwin.param.state === "10030") {
				        confirmMessage = "저장";
				    }
				}

				com.confirm(confirmMessage + " 하시겠습니까?", function(ret) {
                    if (ret) {
                    	com.file.transport(scwin.save);
                    }
               });
			};

			scwin.save = function(attachObj) {

			    const approvYn = dma_save.get("approval");
			    const sn = com.getLoginInfo("sn");
			    const uid = com.getLoginInfo("uid");
			    const serviceId = com.getLoginInfo("serviceId");
			    const state = scwin.param.state;
			    const invntSyspayno = scwin.param.invntSyspayno;

			    // 1. 특허 마스타 등록
			    dma_save.set("req_no", outputReqNo.getValue()); // 의뢰번호
			    dma_save.set("pat_clsf", radioSjc.getValue()); // 특허구분
			    dma_save.set("invnt_clsf", radioInvntClsf.getValue()); // 발명구분
			    dma_save.set("tech_clsf", selectboxTechClsf.getValue()); // 기술분류
			    dma_save.set("state", state); // 상태
			    dma_save.set("invnt_krn_nm", inputInvntKrnNm.getValue()); // 발명국문명칭
			    dma_save.set("invnt_syspayno", invntSyspayno); // 발명시스템개인번호
			    dma_save.set("charg_syspayno", inputChrgSysNo.getValue()); // 담당시스템개인번호
			    dma_save.set("regst_syspayno", sn); // 등록시스템개인번호

			    if (approvYn) {
			        dma_save.set("invnt_draft_ymd", com.getServerDateTime("yyyyMMdd")); // 발명기안일자
			        dma_save.set("invnt_wrte_syspayno", sn); // 발명작성시스템개인번호
			    }
			    // 기술적 특징
			    if (/PAT070|PAT020/.test(radioSjc.getValue())) {
			        dma_save.set("trans_nm", textareaTransNm.getValue()); // 직무발명의기술적특징 ==> 상세한 설명
			        dma_save.set("tech_field", textareaTechField.getValue()); // 기술적 특징 ==> 배경기술
			        dma_save.set("solve_task", textareaSolveTask.getValue()); // 기술적 특징 과제해결수단 
			        dma_save.set("relat_invnt", textareaRelatInvnt.getValue()); // 관련특허 
			        dma_save.set("invnt_efct", textareaInvntEfct.getValue()); // 기술적특징 발명의 효과
			    }
			    dma_save.set("indst_use_clsf", textareaIndstUseClsf.getValue()); // 발명을 하게된 경위

			    // 첨부파일
			    dma_save.set("plan_attach_file_no", attachObj.upload_A); // 도면첨부파일
			    dma_save.set("attach_file_no_1", attachObj.upload_B); // 관련자료 첨부(기술설명서)		     
			    dma_save.set("attach_file_no_2", attachObj.upload_C); // 자유발명신고서

			    // interface
			    dma_save.set("endpoints", uid); // 로그인 아이디
			    dma_save.set("ui_id", serviceId);

			    // 6. 특허 구분
			    switch (radioSjc.getValue()) {
			        case "PAT040":
			            // 디자인
			            dma_tabData.set("req_no", inputDesnReqNo.getValue());
			            dma_tabData.set("pre_desn_ex", radioPredesnEx.getValue()); // 선행디자인유무
			            dma_tabData.set("pre_desn_nm", inputPreDesnNm.getValue()); // 선행디자인명
			            dma_tabData.set("desn_detls_desc", strReplace(textareaDesnDetlsDesc.getValue())); // 디자인상세설명
			            dma_tabData.set("attach_file_no_1", attachObj.upload_D); // 디자인 첨부도면
			            break;

			        case "PAT030":
			            // 상표
			            dma_tabData.set("req_no", inputTmReqNo.getValue());
			            dma_tabData.set("tm_mean", inputTmMean.getValue()); // 상표의미
			            dma_tabData.set("tm_desc", strReplace(textareaTmDesc.getValue())); // 상표설명
			            dma_tabData.set("appont_goods", strReplace(textareaAppontGoods.getValue())); // 지정상품
			            dma_tabData.set("attach_file_no", attachObj.upload_E); // 첨부파일
			            break;
			    }

			    com.execute(sbm_svcpatdftinvm01);
			};

			scwin.sbm_svcpatdftinvm01_done = function(e) {
				const reqNo = e.responseJSON.elData.req_no;
			    scwin.param.reqNo = reqNo;
			    outputReqNo.setValue(reqNo);

			    dlt_del_smitVend.removeAll();
				dlt_del_invntPsn.removeAll();
				dlt_del_design_attach.removeAll();

				const approvYn = dma_save.get("approval");

			    if (approvYn === "true") {
			        scwin.approval();
			    } else {
			    	scwin.search();
			        if (scwin.param.state === "10030" && scwin.param.aprState < "XAD020") {
			            com.confirm(reqNo + " 저장되었습니다. \n결재신청하시겠습니까?", function(ret) {
			                if (ret) {
			                	scwin.approval();
			                }
			           });
			        } else {
			            com.alert("저장되었습니다.");
			        }
			    }
			};

			scwin.approval = function() {
				const uid = com.getLoginInfo("uid");
				const sn = com.getLoginInfo("sn");
				const serviceId = com.getLoginInfo("serviceId");
				const approvYn = dma_save.get("approval");

				const attachFileNo_A = dma_form.get("plan_attach_file_no");  // 도명첨부
				const attachFileNo_B = dma_form.get("attach_file_no_1");  // 기술설명서
				const attachFileNo_C = dma_form.get("attach_file_no_2");  // 자율발명신고서
				const attachFileNo_D = dma_form.get("attach_file_no_tab2");  // 디자인탭
				const attachFileNo_E = dma_form.get("attach_file_no_tab3");  // 상표탭

				dma_approval.setEmpty();
				dma_approval.setJSON(dma_form.getJSON());

				dma_approval.set("login_user_id", uid);
				dma_approval.set("endpoint", uid);
				dma_approval.set("req_no", outputReqNo.getValue());
				dma_approval.set("reporter_id", sn);
				dma_approval.set("ui_id", serviceId);
				dma_approval.set("manager_id", inputChrgSysNo.getValue());
				dma_approval.set("apr_state", scwin.param.aprState);
				dma_approval.set("info_cd", inputInvntKrnNm.getValue());
				dma_approval.set("ext1_cd", "");
				dma_approval.set("ext2_cd", "");
				dma_approval.set("ext3_cd", "");
				dma_approval.set("result", "");

				dma_approval.set("pat_clsf_nm", radioSjc.getText());
				dma_approval.set("tech_clsf", selectboxTechClsf.getText());
				dma_approval.set("vend_type", gridSmitVend.getCellData(0, "vend_type"));

				dma_approval.set("fileList_A", attachFileNo_A); // 기술설명서
				dma_approval.set("fileList_B", attachFileNo_B); // 도면첨부
				dma_approval.set("fileList_C", attachFileNo_C); // 자유발명신고서

				if(radioSjc.getValue() == "PAT040"){ // 디자인
					dma_approval.set("fileList_D", attachFileNo_D); // 첨부도면
				}else if(radioSjc.getValue() == "PAT030"){
					// 첨부파일
					dma_approval.set("fileList_E", attachFileNo_E);
				}

				com.execute(sbm_svcpatdftinvapr);
			};

			scwin.sbm_svcpatdftinvapr_done = function(e) {
				const reqNo = e.responseJSON.elData.req_no;
				com.movePageMenu("A001", { "reqNo" : reqNo});
				//scwin.search();
			};


			scwin.pre_save = function() {
			    if (radioSjc.getValue() === '') {
			        com.alert("권리구분을 선택해 주십시오.");
			        return false;
			    }

			    if (radioInvntClsf.getValue() === "") {
			        com.alert("발명구분을 선택해 주십시오.");
			        return false;
			    }

			    // 2. 발명 기본 정보	
			    if (selectboxTechClsf.getValue() === "") { // 기술분류
			        com.alert("기술분류를 선택해 주십시오.");
			        tabContainer1.setSelectedTabIndex(0);
			        selectboxTechClsf.focus();
			        return false;
			    }

			    if (inputInvntKrnNm.getValue() === "") { // 발명의명칭(발명국문명칭)
			        com.alert("발명의 명칭을 입력해 주십시오.");
			        tabContainer1.setSelectedTabIndex(0);
			        inputInvntKrnNm.select();
			        return false;
			    }

			    if (gridInvntPsn.getRowCount() === 0) {
			        com.alert("직무 발명자를 입력해 주시기 바랍니다.");
			        tabContainer1.setSelectedTabIndex(0);
			        return false;
			    }

			    if (inputChrgSysNo.getValue().trim() === "") {
			        com.alert("소연심의 담당자를 선택하여 주시기 바랍니다.");
			        tabContainer1.setSelectedTabIndex(0);
			        return false;
			    }

			    if (gridSmitVend.getRowCount() === 0) {
			        com.alert("출월인을 입력바랍니다.");
			        return false;
			    }

			    for (var i = 0; i < dlt_smitVend.getTotalRow(); i++) {
			        if (dlt_smitVend.getRowJSON(i).stake_rt === "") {
			            com.alert("출원인의 '권리지분'을 입력바랍니다.");
			            return false;
			        }

			        if (dlt_smitVend.getRowJSON(i).vend_nm === "") {
			            com.alert("출원인의 '업체명(성명)'을 입력바랍니다.");
			            return false;
			        }

			        if (dlt_smitVend.getRowJSON(i).vend_type === '1') {
			            if (dlt_smitVend.getRowJSON(i).charg === "") {
			                com.alert("출원인의 '대표자'를 입력바랍니다.");
			                return false;
			            }

			            if (dlt_smitVend.getRowJSON(i).contac_loc === "") {
			                com.alert("출원인의 '대표번호'를 입력바랍니다.");
			                return false;
			            }
			        }
			    }

			    var mainInvntCnt = 0;
			    for (var x = 0; x < dlt_invntPsn.getTotalRow(); x++) {
			        var invntPsnClsf = dlt_invntPsn.getRowJSON(x).invnt_psn_clsf; // 주 발명자여부
			        var blongClsf = dlt_invntPsn.getRowJSON(x).blong_clsf; // 소속구분				

			        //주발명자 체크는 내부인 중에서 선택될 수 있도록 저장기능 보완 요청 : 20210408 진소영
			        if (invntPsnClsf === "1" && blongClsf === "PDT020") {
			            com.alert("주발명자는 내부인 중에서만 선택 가능합니다.");
			            return false;
			        }

			        // 소속구분 공백체크	
			        if (blongClsf === "") {
			            com.alert("[" + (x + 1) + "]행의 발명자 소속구분이 지정되지 않았습니다.");
			            tabContainer1.setSelectedTabIndex(0);
			            return false;
			        }

			        if (blongClsf === "PDT010" && dlt_invntPsn.getRowJSON(x).syspayno === "") {
			            com.alert("내부인인 경우에는 조회 버튼을 통하여 입력하여 주십시오.");
			            return false;
			        }

			        if (blongClsf === "PDT020") {
			            if (dlt_invntPsn.getRowJSON(x).nm_krn === "") {
			                com.alert("성명을 입력하여 주십시오.");
			                return false;
			            }

			            if (dlt_invntPsn.getRowJSON(x).blong_nm === "") {
			                com.alert("소속을 입력하여 주십시오.");
			                return false;
			            }
			        }

			        // 주 발명자 지정	
			        if (invntPsnClsf === "1") {
			            mainInvntCnt++;
			            invntSyspayno = dlt_invntPsn.getRowJSON(x).syspayno;
			        }
			    }

			    if (mainInvntCnt === 0) {
			        com.alert("주발명자를 선택하여 주시기 바랍니다.");
			        tabContainer1.setSelectedTabIndex(0);
			        return false;
			    }

			    //if(roleCode.indexOf("1016118") > 0 || sn === "20130576") return true; // 지식재산권 시스템관리자	  
			    if (scwin.param.state != "10000") {
			        if (textareaIndstUseClsf.getValue() === "") {
			            com.alert("발명을 하게된 경위를 입력해 주시기 바랍니다.");
			            tabContainer1.setSelectedTabIndex(0);
			            return false;
			        }

			        if (!/PAT030|PAT040/.test(radioSjc.getValue())) { // 상표, 디자인						
			            if (textareaTechField.getValue() === "") { // 기술적 특징 배경기술
			                com.alert("배경(종래)기술을 입력해 주시기 바랍니다.");
			                tabContainer1.setSelectedTabIndex(0);
			                return false;
			            }

			            if (textareaSolveTask.getValue() === "") {
			                com.alert("과제해결수단을 입력해 주시기 바랍니다.");
			                tabContainer1.setSelectedTabIndex(0);
			                return false;
			            }

			            if (textareaTransNm.getValue() === "") {
			                com.alert("기술적 특징 > 상세한 설명을 입력해 주시기 바랍니다.");
			                tabContainer1.setSelectedTabIndex(0);
			                return false;
			            }

			            if (textareaInvntEfct.getValue() === "") {
			                com.alert("발명의 효과를 입력해 주시기 바랍니다.");
			                tabContainer1.setSelectedTabIndex(0);
			                return false;
			            }

			            if (com.file.action.getTotalFileCount(upload_B.getID()) < 1) {
			                com.alert("기술설명서를 첨부해 주시기 바랍니다.");
			                return false;
			            }
			            radioInvntClsf.getValue()

			            if (com.file.action.getTotalFileCount(upload_C.getID()) < 1) {
			                if (radioInvntClsf.getValue() === "PBT030") {
			                    com.alert("자유발명 신고서를 첨부해 주시기 바랍니다.");
			                    return false;
			                }
			            }
			        }

			        // 3. 디자인 
			        if (radioSjc.getValue() === "PAT040") {
			            // 첨부 파일 확인	
			            if (com.file.action.getTotalFileCount(upload_D.getID()) < 1) {
			                com.alert("[디자인탭]에서 도면을 첨부해 주시기 바랍니다.");
			                tabContainer1.setSelectedTabIndex(1);
			                return false;
			            }

			        }

			        // 4. 상표	
			        if (radioSjc.getValue() === "PAT030") {
			            if (inputTmMean.getValue() === "") {
			                com.alert("상표명을 입력해 주십시오.");
			                tabContainer1.setSelectedTabIndex(2);
			                inputTmMean.select();
			                return false;
			            }

			            if (com.file.action.getTotalFileCount(upload_E.getID()) < 1) {
			                com.alert("상표 관련 파일을 첨부해 주시기 바랍니다.");
			                tabContainer1.setSelectedTabIndex(2);
			                return false;
			            }

			            if (textareaTmDesc.getValue() === "") {
			                com.alert("상표의 간단한 설명을 입력해 주십시오.");
			                tabContainer1.setSelectedTabIndex(2);
			                textareaTmDesc.focus();
			                return false;
			            }

			            if (textareaAppontGoods.getValue() === "") {
			                com.alert("지정상품을 입력해 주십시오.");
			                tabContainer1.setSelectedTabIndex(2);
			                textareaAppontGoods.focus();
			                return false;
			            }
			        }
			    }

			    return true;
			};

			scwin.approval_delete = function() {
			    com.confirm("자유발명신고서를 삭제하시겠습니까?", function(ret) {
			        if (ret) {
			        	const sn = com.getLoginInfo("sn");
					    const reqNo = outputReqNo.getValue();
					    dma_delete.set("req_no", reqNo);
					    dma_delete.set("syspayno", sn);

					    com.execute(sbm_svcpatdftinvd01, {
							successCallback : function(e) {
								com.alert("삭제하였습니다");
								com.movePageMenu("A005");
							}
						});
			        }
			   });
			};

			/**
			 * 탭 변경
			 */
			scwin.tab_onchange = function(tabID, idx, userTabID) {
				if(idx === 1){ // 디자인 내역 탭
					if(!com.file.isCreated(upload_D.getID())) com.file.create(upload_D.getID(), {fileKey: com.getLoginInfo("serviceId"), mode: 'edit'});
				}
				if(idx === 2){ // 상표 내역 탭
					if(!com.file.isCreated(upload_E.getID())) com.file.create(upload_E.getID(), {fileKey: com.getLoginInfo("serviceId"), mode: 'edit'});
				}
			};

			scwin.btn_print_onclick = function(e) {
				var node = "req_no="+ outputReqNo.getValue();
				com.oz.openPopup({
					ozrCode : "I012", // PatDftFreeInvDcr.ozr
					param: node
			    });
			};

			scwin.movePage = function(){
				var pageId = com.getParameter().openerTabId;
				com.moveList(pageId);
			};

			scwin.onpageunload = function() {

			};
