head.script:

			scwin.param = {};
			scwin.onpageload = function() {
				// 첨부파일 컴포넌트 초기화
                com.file.init();     

				scwin.param.appr = "N";
				scwin.param.docuClsf = "I06";
				scwin.param.aprState = "";

				// 폼 변수 선언
				scwin.param.draft_syspaynm = com.getParameter().draft_syspaynm || "";
				scwin.param.draft_empno = com.getParameter().draft_empno || "";
				scwin.param.draft_syspayno = com.getParameter().draft_syspayno || "";
				scwin.param.dp_clsf = com.getParameter().dp_clsf || "";
				scwin.param.apr_state = com.getParameter().apr_state || "";
				scwin.param.start_draft_ymd = com.getParameter().start_draft_ymd || "";
				scwin.param.end_draft_ymd = com.getParameter().end_draft_ymd || "";
				scwin.param.dp_req_no = com.getParameter().dp_req_no || "";


				const param2 = com.getParameter().param2 || "";
				const param4 = com.getParameter().param4 || "";

				if (param4 == "apr") {
				    if (param2 != "") {
				        scwin.param.dp_req_no = param2;
				    }
				    btn_move_list.hide();
				}

				if (scwin.param.dp_req_no == "") {
				    btn_del.hide();
				}

				draft_syspaynm.setValue(com.getLoginInfo("cn"));

				if (scwin.param.dp_req_no && scwin.param.dp_req_no != "") {
				    scwin.search();
				} else {
				    com.file.create(upload_A.getID(), { fileKey: com.getLoginInfo("serviceId"), mode: 'edit', target: "IPMS" });
				}
			};

			scwin.search = function() {
			    dma_search.set("dp_req_no", scwin.param.dp_req_no);
			    com.execute(sbm_svcpatipdrndr01);
			};

			scwin.sbm_svcpatipdrndr01_done = function(e) {

				let dataCollections = [dma_draftInfo, dlt_smipAppLst];

				dataCollections.forEach(collection => {
				    com.trimDataCollection(collection);
				});

			    scwin.param.aprState = dma_draftInfo.get("apr_state");
			    const attachFileNo_A = dma_draftInfo.get("attach_file_no");
			    com.file.create(upload_A.getID(), { fileKey: attachFileNo_A ? attachFileNo_A : com.getLoginInfo("serviceId"), mode: 'edit', target: "IPMS" });
			};

			scwin.smitapp_popup_open = function(row, col, colId) {

				if(colId !== "btn_smitapp") return;

			    const data = {};

			    com.pop.open(biz.pat.pop.TYPE.RELATED_SMITAPP_NO, data, function(ret) { // PAT_POP_I110_08P.xml

				    const retData = {
					    req_no: ret.req_no,
					    mngmt_no: ret.mngmt_no,
					    smitapp_no: ret.smitapp_no,
					    invnt_syspayno: ret.syspayno,
					    invnt_empno: ret.empno,
					    invnt_nm: ret.nm,
					    invnt_krn_nm: ret.invnt_krn_nm,
					    smitapp_ymd: ret.smitapp_ymd
					};

					dlt_smipAppLst.setRowJSON(row, retData, true);

			    });

			};

			scwin.delete_click = function() {
			    const dpReqNo = dp_req_no.getValue();

			    if (dpReqNo === "") {
			        com.alert("신청서가 존재하지 않습니다.");
			        return;
			    }

			    if (scwin.param.aprState >= "XAD020") {
			        com.alert("결재진행중이므로 삭제할 수 없습니다.");
			        return;
			    }

			    com.confirm("신청서를 삭제하시겠습니까?", function(ret) {
			        if (ret) {
				        dma_delete.set("req_no", dpReqNo);
					    dma_delete.set("docu_clsf", scwin.param.docuClsf);

					    com.file.deleteAll(scwin.delete_process);
			        }
			    });
			};


			scwin.delete_process = function() {
			    com.execute(sbm_svcpatipdrndd01, {
			        successCallback: function(e) {
			            com.alert(scwin.param.dp_req_no + " 삭제 하였습니다");
			            if (scwin.param.param4 == "apr") {
			                // 결재진행함 이동
			                com.movePageMenu("A005");
			            } else {
			                scwin.movePage();
			            }
			        }
			    });
			};


			scwin.save_click = function(e) {
			    if (!scwin.pre_save()) return;

			    com.confirm("저장 하시겠습니까?", function(ret) {
			        if (ret) {
				        scwin.param.appr = $p.getComponentById(e.target.id).getUserData('userData1');
					    const uid = com.getLoginInfo("uid");
					    const sn = com.getLoginInfo("sn");
					    const serviceId = com.getLoginInfo("serviceId");

					    dma_save.set("dp_req_no", scwin.param.dp_req_no); // id
					    dma_save.set("syspayno", sn); // sn
					    dma_save.set("dp_clsf", dp_clsf.getValue());
					    dma_save.set("dp_resn", dp_resn.getValue());
					    dma_save.set("ui_id", serviceId); // 화면 번호				
					    dma_save.set("endpoint", uid); // 신청자 ID
					    dma_save.set("docu_clsf", "I06"); // 문서구분  
					    dma_save.set("state_cd", "P0611"); // 상태
					    dma_save.set("apprYn", scwin.param.appr); // 상태

					    com.file.transport(scwin.save);
			        }
		        })
			};

			scwin.save = function(attachObj) {
				dma_save.set("attach_file_no", attachObj.upload_A);

				com.execute(sbm_svcpatipdrndm01, {
			        successCallback: function(e) {

			            const dpReqNo = e.responseJSON.elData.dp_req_no;
			            scwin.param.dp_req_no = dpReqNo; 

			            if(scwin.param.appr === "Y"){
				            scwin.approval(attachObj);
			            }else{
			            	com.alert ("저장 되었습니다.");
			            }

			        }
			    });
			};

			scwin.pre_save = function() {
			    if (dp_clsf.getValue() === "") {
			        alert("신청구분은 필수 선택 사항 입니다.");
			        return false;
			    }

			    if (dlt_smipAppLst.getTotalRow() < 1) {
			        com.alert("선출원 목록은 최소 1건 이상 존재 하여야 합니다.");
			        return false;
			    }
			    return true;
			};


			scwin.approval = function(attachObj) {

				com.confirm("결재 하시겠습니까?", function(ret) {
			        if (ret) {				        
				        const uid = com.getLoginInfo("uid");
					    const sn = com.getLoginInfo("sn");
					    const cn = com.getLoginInfo("cn");
					    const serviceId = com.getLoginInfo("serviceId");

					    var invntPsn = dlt_smipAppLst.getRowJSON(0).invnt_syspayno; // 주발명자
					    var invntPsnNm = dlt_smipAppLst.getRowJSON(0).invnt_nm;
					    var invnt = dlt_smipAppLst.getRowJSON(0).invnt_krn_nm;
					    var smitno = dlt_smipAppLst.getRowJSON(0).smitapp_no;

					    dma_approval.set("login_user_id", uid);
					    dma_approval.set("req_no", scwin.param.dp_req_no);
					    dma_approval.set("reporter_id", sn);
					    dma_approval.set("ui_id", serviceId);
					    dma_approval.set("apr_state", "XAD010");
					    dma_approval.set("manager_id", invntPsn);
					    dma_approval.set("info_cd", invnt);
					    dma_approval.set("ext2_cd", invntPsnNm);
					    dma_approval.set("ext1_cd", "");
					    dma_approval.set("main_key", scwin.param.dp_req_no);
					    dma_approval.set("researcher_id", invntPsn);
					    dma_approval.set("fileList_A", attachObj.upload_A);

					    dma_approval.set("wri_nm", cn);
					    dma_approval.set("dp_clsf_nm", dp_clsf.getValue() == "PPT030" ? "우선권 주장 출원" : "분할출원");
					    dma_approval.set("dp_resn", dp_resn.getValue());

					    com.execute(sbm_svcpatipdrndapr, {
						    successCallback: function(e) {
						    	const reqNo = e.responseJSON.elData.req_no;
						    	com.movePageMenu("A001", { "reqNo" : reqNo});
						    }
						});
			        }
			    });


			};

			scwin.update_grid_row = function(e) {
			    const type = $p.getComponentById(e.target.id).getUserData('userData1');
			    type ? com.addRow(gr) : com.deleteRow(gr);
			};

			scwin.movePage = function(e){
				const pageId = com.getParameter().openerTabId;
				const data = {};
				com.moveList(pageId, data);
			};

			scwin.btn_print_onclick = function(e) {
				const node = "";
				com.oz.openPopup({
					ozrCode : "IA53",
					param: node
			    });
			};

			scwin.onpageunload = function() {

			};
