	scwin.onpageload = function() {
		// 초기화 
		com.file.init();
		btn_close.hide();
		grp_selt.hide();
		grp_intgr_mngmt_msg.hide();

		// 사업조정회의 심의여부 관리
		btn_conf.hide();
		spn_conf_req_no.hide();
		ibx_conf_req_no.hide();
		sbx_consider_clsf.hide();
		btn_excp_base.hide(); // 사업조정 제외기준

		// 사후결재 사유란
		btn_afterApr.hide();
		// 선행연구과제
		grp_prePrj.hide();

		// 엔터키 이벤트
		com.setEnterKeyEvent(grp_emp, scwin.btn_resch_rspns_onclick);

		// 1001001 연구관리관리자, 1001201 연구관리담당, 1001114 연구관리시스템관리자
		if(/1001001|1001201|1001114/.test(scwin.loginInfo.roleCode)){
			scwin.globVar.usrAdmFlag = "ADMN";
		}else{
			scwin.globVar.usrAdmFlag = "USER";
		}

		// 사업공고방(포털 게시판)에서 신규 입력 용도로 호출했을 경우...
		// => 사업공고번호의 값이 ""이 아닐 경우에 crudTag 의 값을 "I"로 설정한다.
		scwin.globVar.bsnsNoticeNo = WebSquare.net.getParameter("bsns_notice_no");
		dma_search.set("bsns_cd_ofic", WebSquare.net.getParameter("bsns_cd_1")); // 사업코드부처
		dma_search.set("bsns_cd_bsns", WebSquare.net.getParameter("bsns_cd_2")); // 사업코드사업
		dma_search.set("bsns_cd_detls_bsns", WebSquare.net.getParameter("bsns_cd_3"));	// 사업코드세부사업

		// parameter
		var param = com.getParameter();
		if(param.param4 == "apr"){
			dma_search.set("bsns_req_no", param.req_no);	
		}else{
			dma_search.set("bsns_req_no", param.bsns_req_no);
		}

		scwin.globVar.openerId = param.openerId;

		// 팝업으로 화면 호출시
		if(param.popTag == "P"){
			btn_list.hide();
