			btn_close.show("");	
		}

		// 공통코드
		var codeOptions = [ { code : "RAG", compID : "sbx_selt_rst"},
							{ code : "RIC", compID : "sbx_threefive"},
							{ code : "RAL", compID : "grd_pare:reschr_clsf"}, 
							{ code : "RAM", compID : "grd_pare:psnexpns_clsf"},
							{ code : "RBK", compID : "cbx_tax_free_itm"}, 
							{ code : "RBL", compID : "cbx_tax_itm"},
							{ code : "RCN", compID : "sbx_note_type_cd"}, 
							{ code : "RII", compID : "sbx_consider"},
							{ code : "RIH", compID : "sbx_consider_clsf"},
							{ code : "RDK", compID : "grd_equipamt:intgr_mngmt_unit"} ];//cbx_intgr_mngmt_unit,
		com.setCmnCd(codeOptions, function(){
			if(dma_search.get("bsns_req_no") == ""){
				scwin.init();
			}else{
				scwin.search();	
			}
		});	

	};

	scwin.onpageunload = function() {

