	 /*
     * 신규 입력시 초기 셋팅
     */ 
	scwin.init = function() {
		dma_mast.set("cud_type", "C");
		dma_mast.set("req_state", "RAF001"); // RAF001 신청
		dma_mast.set("prj_regst_yn", "N"); // 과제등록 여부
		dma_mast.set("req_psn", scwin.loginInfo.sn);
		dma_mast.set("selt_score", 0);

		dma_mast.set("resch_rspns_main", scwin.loginInfo.sn);
		dma_mast.set("resch_rspns_main_empno", scwin.loginInfo.empno);
		dma_mast.set("resch_rspns_main_nm", scwin.loginInfo.cn);		

		// 1) 연구비 초기화
		ibx_tot_resch_amt.setValue("0");
		ibx_suply_value.setValue("0");
		ibx_tax_amt.setValue("0");
		ibx_goods_amt.setValue("0");

		// 2) 첨부파일
		com.file.create(upload_A.getID(), {fileKey: scwin.globVar.typeCode, mode: "edit"}, function(){
			com.file.create(upload_B.getID(), {fileKey: scwin.globVar.typeCode, mode: "edit"}); // 사업공고_(보안)첨부파일ID
		}); // 사업계획서_(중간)첨부파일ID


		// 3) 간접비율 가져오기(필요없어서 주석 2023.01.09.)
		//scwin.gfnIndrcRt(com.getServerDateTime());

		// 4) 사업공고방(포털 게시판)에서 신규 입력 용도로 호출되었을 경우...
		//    사업구분([관계부처] [사업분류] [세부사업명] [내/외부] [전문기관])의 값을 조회한 후, 화면에 표시한다.
		if (scwin.globVar.bsnsNoticeNo != "") {		
			com.execute(sbm_selectBsnsClsf, {								
				successCallback : function(e) {
					dlt_bsnsClsf.setJSON(e.responseJSON.resBsnsCodeVoList);

					for(var i=0; i<dlt_bsnsClsf.getRowCount(); i++){
						if(dlt_bsnsClsf.getCellData(i, "depth") == "1"){
							dma_mast.set("bsns_cd_ofic", dlt_bsnsClsf.getCellData(i, "bsns_cd")); // 사업코드부처
							dma_mast.set("bsns_cd_ofic_nm", dlt_bsnsClsf.getCellData(i, "bsns_nm"));
						}else if(dlt_bsnsClsf.getCellData(i, "depth") == "2"){
							dma_mast.set("bsns_cd_bsns", dlt_bsnsClsf.getCellData(i, "bsns_cd"));
							dma_mast.set("bsns_cd_bsns_nm", dlt_bsnsClsf.getCellData(i, "bsns_nm"));
						}else if(dlt_bsnsClsf.getCellData(i, "depth") == "3"){
							dma_mast.set("bsns_cd_detls_bsns", dlt_bsnsClsf.getCellData(i, "bsns_cd")); // 사업코드사업
							dma_mast.set("bsns_cd_detls_bsns_nm", dlt_bsnsClsf.getCellData(i, "bsns_nm"));

							dma_mast.set("bsns_cd_inout", dlt_bsnsClsf.getCellData(i, "bsns_cd_inout"));
							dma_mast.set("bsns_cd_inout_nm", dlt_bsnsClsf.getCellData(i, "bsns_cd_inout_nm"));
							dma_mast.set("bsns_cd_exclsv_agncy", dlt_bsnsClsf.getCellData(i, "bsns_cd_exclsv_agncy")); // 사업코드전담기관
							dma_mast.set("bsns_cd_exclsv_agncy_nm", dlt_bsnsClsf.getCellData(i, "bsns_cd_exclsv_agncy_nm"));
							dma_mast.set("oh_absrp_rt", dlt_bsnsClsf.getCellData(i, "oh_absrp_rt")); // OH흡수율
						}
					}

					scwin.setBsnsCdNm();
				}
			});
		}	

		// 5) 사업신청 예산 비목해소기준(IG0701) 템플릿 목록을 조회한다.		
		com.execute(sbm_selectBugtTemplate, {
			successCallback : function(e){
				dlt_bugt.setJSON(e.responseJSON.resAppBugtVoList);
			}
		});
	}

	/*
     * 결재상태와 권한에 따라 화면제어
     */
	scwin.setArrangeObj = function() {							
		var usrAdmFlag = scwin.globVar.usrAdmFlag;
		console.log("==========> usrAdmFlag    : " + scwin.globVar.usrAdmFlag);

		if (usrAdmFlag == "ADMN") {  // 사용자유형이 관리자 및 연구담당자 일때...
			if (dma_mast.get("apr_state") != "XAD100") {
				grp_selt.hide();
			} else {
				grp_selt.show();

				if (sbx_selt_rst.getValue().trim() == ""){
			  		sbx_selt_rst.setDisabled( false );
			  	} else{
			  		sbx_selt_rst.setDisabled( true );
			  	}

				btn_save.setDisabled(false);				
			}
		} else {  // 사용자유형이 과제책임자 및 연구참여자 또는 기타사용자 일때...
			grp_selt.hide();
		}		

		// 결재상태에 따른 각종 버튼 및 오브젝트들 disable 및 invisible 처리
		if(dma_mast.get("apr_state") >= "XAD020") {
			sbx_secrt_prj_ex.setDisabled(true);      // 보안등급분류 Object disable 처리
			btn_bsnsPop.hide();             // 사업분류선택 버튼 invisible
		}

		if(usrAdmFlag == "ADMN"  || usrAdmFlag == "USERA") {  // 사용자유형이 관리자 및 연구담당자 또는 연구책임자 일때...
			btn_print.setDisabled(false);
			btn_bsnsPop.show("");
		}else if(/USERC|USERB/.test(usrAdmFlag)){
			btn_bsnsPop.hide();
		}	

		// 20160315 선정결과 입력된 건은 통프 호출하지 않는다. 더불어 선정에서 취소 또는 탈락으로 변경할 수 없도록 한다.
		if (sbx_selt_rst.getValue().trim() == "") {
			scwin.globVar.saveAprYn = "Y";
		} else {
			scwin.globVar.saveAprYn = "N";
		}

		// 선정결과 수정시 체크위해 셋팅
		scwin.globVar.bfSeltRst = sbx_selt_rst.getValue();	
	};

