	};

	/*
     * 연구기간 유효성 체크
     */
	scwin.validatePrd = function(flag, agColPosStr){
		var startYmd ="";
		var sMsgStr = "현단계 연구기간";
		// 알림 메시지 문자열 선언
		sMsgStr = (flag ==1)? "총연구기간" : "당해년도 연구기간";

		switch(flag){
			case 1 : startYmd = ica_tot_resch_prd_orign_ymd.getValue(); break;
			case 2 : startYmd = ica_thyr_resch_prd_orign_ymd.getValue(); break;
			case 3 : startYmd = ica_step_resch_prd_orign_ymd.getValue(); break;
		}
		var clsYmd = "";
		switch(flag){
			case 1 : clsYmd = ica_tot_resch_prd_cls_ymd.getValue(); break;
			case 2 : clsYmd = ica_thyr_resch_prd_cls_ymd.getValue(); break;
			case 3 : clsYmd = ica_step_resch_prd_cls_ymd.getValue(); break;
		}

		var obj;
		if(agColPosStr == "orign_ymd") {
			sMsgStr = sMsgStr + "의 시작일을 확인하세요."; 
			obj = (flag == 1)? ica_tot_resch_prd_orign_ymd : ica_thyr_resch_prd_orign_ymd;
		}else if (agColPosStr == "cls_ymd"){
			sMsgStr = sMsgStr + "의 종료일을 확인하세요.";
			obj = (flag == 1)? ica_tot_resch_prd_cls_ymd : ica_thyr_resch_prd_cls_ymd;
		}

		if(startYmd != "" && clsYmd != ""){
			if(startYmd > clsYmd){
				com.alert(sMsgStr);
				obj.focus();
				return false;
			}
		}

		if(flag == 2){ // 당해년도 연구기간 체크일 경우				
			if(agColPosStr == undefined || agColPosStr == "orign_ymd") {
				if(dma_mast.get("tot_resch_prd_orign_ymd") > dma_mast.get("thyr_resch_prd_orign_ymd")){
					alert("당해년도 연구기간의 시작일은 총연구기간의 시작일 이전일 수 없습니다.");
					return false;
				}
			}

			if(agColPosStr == undefined || agColPosStr == "cls_ymd") {
				if(dma_mast.get("thyr_resch_prd_cls_ymd") > dma_mast.get("tot_resch_prd_cls_ymd")){
					alert("당해년도 연구기간의 종료일은 총연구기간의 종료일 이후일 수 없습니다.");
					return false;
				}
			}
		}else if(flag == 3){ 
			if(agColPosStr == undefined || agColPosStr == "orign_ymd") {
				if(dma_mast.get("tot_resch_prd_orign_ymd") > dma_mast.get("step_resch_prd_orign_ymd")){
					alert("현단계 연구기간의 시작일은 총연구기간의 시작일 이전일 수 없습니다.");
					return false;
				}
			}

			if(agColPosStr == undefined || agColPosStr == "cls_ymd") {
				if(dma_mast.get("step_resch_prd_cls_ymd") > dma_mast.get("tot_resch_prd_cls_ymd")){
					alert("현단계 연구기간의 종료일은 총연구기간의 종료일 이후일 수 없습니다.");
					return false;
				}
			}
		}

		return true;
	};

	/*
     * 총 연구기간(From-To)의 연수와 개월수 및 연차수 계산
     */
	scwin.calcTotReschPrdTerm = function(agColPosStr, flag) {	
		// 총 연구기간(From-To)의 연수와 개월수 및 연차수 계산
		var startYmd = (flag==1)? ica_tot_resch_prd_orign_ymd.getValue() : ica_step_resch_prd_orign_ymd.getValue();
		var clsYmd = (flag==1)? ica_tot_resch_prd_cls_ymd.getValue() : ica_step_resch_prd_cls_ymd.getValue();
		scwin.getCalYearMonth(startYmd, clsYmd, flag);		
	};

	/*
     * 당해년도 연구기간(From-To)의 연수와 개월수 및 연차수 계산
     */
	scwin.calcThyrReschPrdTerm = function(agColPosStr) {
		// 각 연차별 연구기간(From-To)의 연수와 개월수 계산
		scwin.getCalYearMonth(ica_thyr_resch_prd_orign_ymd.getValue(), ica_thyr_resch_prd_cls_ymd.getValue(), 2);	

		if (agColPosStr == "orign_ymd") {
			// 입력한 각 연차별 연구기간(From-To) 값을 각 연구비 그리드의 해당 컬럼에 세팅
			for (var i = 0; i < dlt_bugt.getRowCount(); i++) {
				dlt_bugt.setCellData(i, "start_ymd", ica_thyr_resch_prd_orign_ymd.getValue());
			}	

			// 참여연구원 참여일자 셋팅
			for (var i = 0; i < dlt_pare.getRowCount(); i++) {
				dlt_pare.setCellData(i, "attnce_prd_orign_ymd", ica_thyr_resch_prd_orign_ymd.getValue());
			}		
		}else{
			// 연구비 그리드의 종료일자 셋팅
			for (var i = 0; i < dlt_bugt.getRowCount(); i++) {
				dlt_bugt.setCellData(i, "cls_ymd", ica_thyr_resch_prd_cls_ymd.getValue());
		 		dlt_bugt.setCellData(i, "anal", 1);
			}

			// 참여연구원 참여일자 셋팅
			for (var i = 0; i < dlt_pare.getRowCount(); i++) {				
				dlt_pare.setCellData(i, "attnce_prd_cls_ymd", ica_thyr_resch_prd_cls_ymd.getValue());
			}
		}
	};		

	/*
     * 년, 개월 수 구하기
     */ 
	scwin.getCalYearMonth = function(fromYmd,toYmd, flag) {
		if(fromYmd == "" || toYmd == "") return;

		var fromMon = com.parseInt(fromYmd.substring(4, 6));
		var toMon = com.parseInt(toYmd.substring(4, 6));
		var fromDay = com.parseInt(fromYmd.substring(6, 8));
		var toDay = com.parseInt(toYmd.substring(6, 8));

		// 년도 차이 구하기
		var calYear = (parseInt(toYmd.substring(0, 4)) - parseInt(fromYmd.substring(0, 4)));
		// 월 차이구하기
		var calMonth = toMon - fromMon;
		var months = 0;

		if(calMonth == 0 ) { // 같은 달일 경우
			if((fromDay - toDay) == 1 ) { // 종료일자가 시작일자 하루 전인 경우
				months = calYear * 12 + calMonth;
			} else {
				months = calYear * 12 + calMonth + 1;
			}
		} else if (calMonth < 0 ) { // 시작 달이 더 큰 경우
			months = calYear * 12 + calMonth + 1;
			calMonth = 0;
		} else { // 시작 달이 작은 경우
			months = calYear * 12 + calMonth+ 1 ;
		}

		if(flag == 1){ // 총연구기간
			ibx_tot_prd_term_year.setValue(Math.floor(months/12));
			ibx_tot_prd_term_month.setValue(months%12);

			// 총년차
			if (Number(ibx_tot_prd_term_month.getValue()) > 0) {
				ibx_tot_resch_prd_term.setValue(Number(ibx_tot_prd_term_year.getValue()) + 1);  // 총 연구기간(From-To)의 연차수
			} else {
				ibx_tot_resch_prd_term.setValue(ibx_tot_prd_term_year.getValue()); // 총 연구기간(From-To)의 연차수
			}			
		}else if(flag == 3){ // 현단계연구기간
			ibx_step_prd_term_year.setValue(Math.floor(months/12));
			ibx_step_prd_term_month.setValue(months%12);
		}else{
			ibx_thyr_prd_term_year.setValue(Math.floor(months/12));
			ibx_thyr_prd_term_month.setValue(months%12);
		}
	};

	 /*
     * 연구장비 통합 관리비 한도금액 검색
     */ 
	scwin.getLimitAmt = function() {
		// 연구장비통합관리비 한도금액 조회
		// TO DO 2021.10.19. 추가
		var sysPayNo = "";
		for(var i=0; i < dlt_pare.getRowCount(); i++){
			if(dlt_pare.getCellData(i, "reschr_clsf") == "RAL001"){
				sysPayNo = dlt_pare.getCellData(i, "syspayno");
				break;
			}
		}

		if(sysPayNo == ""){
			if(dma_mast.get("resch_rspns_main").trim() == ""){
				alert("연구책임자를 입력하세요.");
				return;
			}else{
				sysPayNo = dma_mast.get("resch_rspns_main");
			}
		}

		dma_search.set("syspayno", sysPayNo);
		dma_search.set("intgr_mngmt_unit", cbx_intgr_mngmt_unit.getValue());
		dma_search.set("req_no", dma_search.get("bsns_req_no"));		
		com.execute(sbm_getLimitAmt, {
			successCallback : function(e) {
				dma_search.set("syspayno", "");
				dlt_equipAmt.setJSON(e.responseJSON.resEquipLimitAmtVoList);

				if(dlt_equipAmt.getRowCount() == 1){
					dlt_equipAmt.setCellData(0, "distr_rt", 100);
				}
			}
		});
	};

	/*
     * 과세구분 셋팅
     */ 
	scwin.getTaxCd = function() {
		var bsnsCdInout = dma_mast.get("bsns_cd_inout").trim();
		if(bsnsCdInout == "RHA001" || bsnsCdInout == "RHA003"){ // 정부수탁, 민간수탁
			var taxFreeItm = cbx_tax_free_itm.getValue();
			var taxItm = cbx_tax_itm.getValue();
			if (taxFreeItm == "") {
				if (taxItm == "") {
					com.alert("과세항목 또는 비과세항목 중 한개이상 체크해야 합니다.");
					return false;
				}

				dma_mast.set("tax_cd", "FTX001"); // 과세		
			}else {
				if (taxItm != ""){
					com.alert("과세항목과 비과세항목을 동시에 선택할 수 없습니다.");
					return false;
				}

				dma_mast.set("tax_cd", "FTX002");  // 비과세
			}

			// 민간수탁일 경우 의뢰기관기업체여부가 Y이면 과세, 그렇지 않은 경우 위의 기준 준수(20150605)
			if(bsnsCdInout =="RHA003"){ // 민간수탁
				if (radioCmpyYn.getValue() == "") {
					com.alert("[의뢰기관기업체여부]를 선택하세요.");
					return false;
				}

				if (radioCmpyYn.getValue() == "Y") {
					dma_mast.set("tax_cd", "FTX001"); // 과세		
				}
			}
		}else {
			dma_mast.set("tax_cd", "FTX002");  // 비과세
		}
		return true;
	};

	/*
     * 연구비 계산
     */
	scwin.calReschAmt = function() {
		var totReschAmt = parseInt(ibx_tot_resch_amt.getValue());
		var totGoodsAmt = parseInt(grd_bugt.getFooterData("cmpy_goods_sum")) + parseInt(grd_bugt.getFooterData("contpat_goods_sum"));

		if(dma_mast.get("tax_cd") == "FTX001") {
			var suplyValue = Math.round((totReschAmt - totGoodsAmt) * 10 /11);
			ibx_suply_value.setValue(suplyValue);
			ibx_tax_amt.setValue((totReschAmt - totGoodsAmt) - suplyValue);
			ibx_goods_amt.setValue(totGoodsAmt);
		}else {
			ibx_suply_value.setValue(totReschAmt - totGoodsAmt);
			ibx_tax_amt.setValue(0);
			ibx_goods_amt.setValue(totGoodsAmt);
		}
	};

	/*
     * 사업조정제외기준 팝업 오픈
     */
	scwin.threeFivePopup = function(flag) {
		// Post 방식 Data 정의
		var data = {
			"three_five_clsf" : dma_mast.get("threefive_clsf"),
			"consider_clsf" : dma_mast.get("consider_clsf"),
			"flag" : flag
		};
		var popOption = biz.res.pop.TYPE.THREE_FIVE;

		// 3책 5공 제외 사업일 경우 팝업 이름 변경 
		if ( flag == "thr"){ 
			popOption.OPTION.popupName = "3책5공 제외사업 기준"; 
		}else{
			popOption.OPTION.popupName = "사업조정 제외기준"; 
		}

		biz.res.pop.open( popOption, data, function(ret){
			if (ret) {
				if (flag == "thr") {
					dma_mast.set("threefive_clsf", ret.threefive_clsf);
				} else if(flag == "con") {
					dma_mast.set("consider_clsf", ret.threefive_clsf);
					sbx_consider_clsf.setValue(ret.threefive_clsf);					
				} else {
					dma_mast.set("threefive_clsf", "");
					dma_mast.set("consider_clsf", "");
				}
			}
		});
	};

	/*
     * 사업분류 셋팅
     */ 
	scwin.setBsnsCdNm = function(){
		var bsnsCdNm = "[관계부처] " + dma_mast.get("bsns_cd_ofic_nm").trim() + " " 
						+ "[사업분류] " + dma_mast.get("bsns_cd_bsns_nm").trim() + " " 
						+ "[내역사업] " + dma_mast.get("bsns_cd_detls_bsns_nm").trim() + "<br>" 
						+ "[내/외부] " + dma_mast.get("bsns_cd_inout_nm").trim() + " " 
						+ "[전문기관] " + dma_mast.get("bsns_cd_exclsv_agncy_nm").trim() + "";
		ibx_bsns_cd.setValue(bsnsCdNm);
	};

	/*
     * 조회
     */ 
	scwin.search = function() {		
		dma_search.set("syspayno", scwin.loginInfo.sn);

		com.execute(sbm_selectBsnsApp, {
			successCallback : function(e) {

				// 3.0 개발 이전의 데이터는 공백을 구분자로 사용하고 있어 checkbox에 세팅이 되지 않고있음. 따라서 공백 구분자를 , 로 변경
				dma_mast.set("tax_free_item", dma_mast.get("tax_free_item").replaceAll(" ", ","));
				dma_mast.set("tax_item", dma_mast.get("tax_item").replaceAll(" ", ","));
				dma_mast.set("intgr_mngmt_unit", dma_mast.get("intgr_mngmt_unit").replaceAll(" ", ","));	
				if(cbx_intgr_mngmt_unit.getValue() != ""){
					grp_intgr_mngmt_msg.show("");
				}			
				// 사업구분 셋팅
				scwin.setBsnsCdNm();
				// 연구기간 년월 셋팅
				scwin.getCalYearMonth(dma_mast.get("tot_resch_prd_orign_ymd"), dma_mast.get("tot_resch_prd_cls_ymd"), 1);
				scwin.getCalYearMonth(dma_mast.get("step_resch_prd_orign_ymd"), dma_mast.get("step_resch_prd_cls_ymd"), 3);
				scwin.getCalYearMonth(dma_mast.get("thyr_resch_prd_orign_ymd"), dma_mast.get("thyr_resch_prd_cls_ymd"), 2);		

				// 20170426 사업조정회의 심의여부 항목, 제외기준추가
				if(dma_mast.get("consider") == "RII001"){
					ibx_conf_req_no.setValue(dma_mast.get("consider_clsf")); // 사업조정회의신청번호
				}else if(dma_mast.get("consider") == "RII003"){
					sbx_consider_clsf.setValue(dma_mast.get("consider_clsf")); // 사업조정회의 제외기준
				}

				// 사업조정회의 관련 컴포넌트 셋팅
				scwin.setConsider(dma_mast.get("consider"));

				// 결재상태에 따른 사업계획서(중간)첨부파일 영역 enable 처리
				var vMode = "edit";
				if (dma_mast.get("apr_state") >= "XAD020") {
					vMode = "view";
				}
				var attachFileNo_A = dma_mast.get("middle_attach_file"); // 사업계획서_(중간)첨부파일ID
				var attachFileNo_B = dma_mast.get("bsns_notice_attach_file_no"); // 사업공고_(보안)첨부파일ID				
				var attachFileNo_C = dma_mast.get("selt_attach_file_no");

				var objs = [
					{uploadId:upload_A.getID(), fileKey : (attachFileNo_A == "")? scwin.globVar.typeCode : attachFileNo_A, mode: vMode}, // 사업계획서_(중간)첨부파일ID
					{uploadId:upload_B.getID(), fileKey : (attachFileNo_B == "")? scwin.globVar.typeCode : attachFileNo_B, mode: vMode}, // 사업공고_(보안)첨부파일ID		
					{uploadId:upload_C.getID(), fileKey : (attachFileNo_C == "")? scwin.globVar.typeCode : attachFileNo_C, mode: "edit"}
				];
				com.file.createMulti(objs, objs.shift(), function() {
				});

				// 권한 부여
				if(scwin.globVar.usrAdmFlag != "ADMN"){
					if(dma_etc.get("reschr_clsf") == "RAL001"){ // 연구책임자
						scwin.globVar.usrAdmFlag = "USERA";
					}else if(dma_etc.get("reschr_clsf") == "RAL002"){ // 참여연구원
						scwin.globVar.usrAdmFlag = "USERB";
					}else{
						scwin.globVar.usrAdmFlag = "USERC";
					}

					if(dma_mast.get("regst_syspayno") ==  scwin.loginInfo.sn){
						scwin.globVar.usrAdmFlag = "USERA";
					}
				}

				// 내외구분에 따른 컴포넌트 셋팅
				scwin.setComp(); 
				// 버튼 제어
				com.biz.disabledButtons(dma_mast.get("apr_state"), dma_mast.get("bsns_req_no"));
				// 결재상태와 권한에 따라 화면제어
				scwin.setArrangeObj();

				// 간접비율 셋팅(2023.03.02. 임시저장시 연구기간을 입력하지 않는 경우가 있어서)
				if(dma_mast.get("step_resch_prd_orign_ymd") != ""){		
					txt_indrc_rt_bas.setValue(biz.res.getIndrcRt(dma_mast.get("bsns_cd_inout"), dma_mast.get("bsns_cd_ofic"), dma_mast.get("step_resch_prd_orign_ymd")));					
				}
				// 연구비 그리드에서 금액 Summary 정보를 계산하여 표시한다.
				scwin.calcGridAmtSummary();  

				// 선행연구과제조회
				if (rdo_pre_plan_prj.getValue() == "Y") {
					com.execute(sbm_selectPrePrjDetail, {
						successCallback : function(e){
							dlt_prePrj.setJSON(e.responseJSON.resAppPrePrjVoList);
						}
					});
				}

				// 참여연구원내역조회
				// 참여연구원이 로그인한 후 참여연구원 내역 조회시 자기 자신것만 표시하기 위한 조건절 세팅 스크립트 
				if(scwin.globVar.usrAdmFlag == "USERB" || scwin.globVar.usrAdmFlag == "USERC") {
					dma_search.set("syspayno", scwin.loginInfo.sn);
				}else{
					dma_search.set("syspayno", "");
				}

				com.execute(sbm_selectPare, {
					successCallback : function(e) {
						dma_search.set("syspayno", "");
						dlt_pare.setJSON(e.responseJSON.resAppPareVoList);
						// 조회건수
						tbx_result.setValue(dlt_pare.getRowCount());

						if(dlt_pare.getRowCount() > 0){
							ibx_resch_rspns_main_empno.setDisabled(true);		
							btn_resch_rspns.setDisabled(true);
						}

						if(/RHA001|RHA002/.test(dma_mast.get("bsns_cd_inout"))){						
							if(cbx_intgr_mngmt_unit.getValue() != ""){
								btn_limAmt.setDisabled(false);

								// 연구장비 통합 관리비 한도금액 검색
								scwin.getLimitAmt();
							}
						}
					}
				});				

				// 정부수탁, 민간수탁 과제의 경우만 해당. 사후결재기능 추가함에 따라 사후결재 입력데이터 조회
				if (/RHA001|RHA003/.test(dma_mast.get("bsns_cd_inout"))) {
					if (dma_mast.get("req_ymd") > dma_mast.get("thyr_resch_prd_orign_ymd")) {
						if(e.responseJSON.elData.postAprVo) {	// 사후결재 사유가 존재할 경우에 사유결재 조회 버튼 표시
							btn_afterApr.show("");
						}
					}
				}

			}
		});	
	};

	/*
     * 사업구분에 따른 컴포넌트 제어(clsf - 사업분류 최초선택시 new)
     */ 
	scwin.setComp = function(clsf) {
		var bsnsCdInout = dma_mast.get("bsns_cd_inout");
		txt_over.setStyle("display", "none");

		if (/RHA002|RHA004/.test(bsnsCdInout)){ // RHA002  내부연구,  RHA004  자체연구
			groupTax.hide();
			group44.hide();

			// 수행과제 보안등급 분류 및 심사기준 (내부연구(기관주요), 자체연구만) 20170926
			btn_secu.show("");
			radioCmpyYn.setValue(""); // 의뢰기관 기업체 여부

			rdo_part_clsf.setDisabled(true); // 과제 참여 유형
			sbx_threefive.setDisabled(true); // 3책5공 해당여부
			sbx_prj_card_clsf.setDisabled(true); // 과제구분
			sbx_consider.setDisabled(true);

			if(clsf == "new" || sbx_threefive.getValue() == ""){ // 사업분류 선택시
				dma_mast.set("threefive", "RIC002"); // 제외					
				// 3책5공제외사업기준
				dma_mast.set("threefive_clsf", "RIE005"); // 내부사업
				dma_mast.set("part_clsf", "RCA017");

				// 2023.01.31. 권정숙 요청(내부과제 또는 자체연구일 경우 자동 셋팅)
				dma_mast.set("prj_card_clsf", (bsnsCdInout == "RHA002")? "E": "N");
				dma_mast.set("consider", "RII003");	// 사업조정회의 심의여부- 제외
				dma_mast.set("consider_clsf", "RIH002"); // 사업조정회의 제외기준 :내부(기본사업,자체연구)사업
				sbx_consider_clsf.setValue("RIH002");	

				scwin.sbx_consider_onviewchange('');			
			}

		} else {
			if(clsf == "new"){ // 사업분류 선택시
				dma_mast.set("prj_card_clsf", "");	
				dma_mast.set("consider", "");	
				dma_mast.set("consider_clsf", ""); 
				sbx_consider_clsf.setValue("");

				scwin.sbx_consider_onviewchange('');
			}

			groupTax.show();
			sbx_prj_card_clsf.setDisabled(false); // 과제구분

			if (bsnsCdInout == "RHA003") { // 민간수탁
				group44.show();
				txt_over.setStyle("display", "");
				sbx_threefive.setDisabled(true); // 3책5공 해당여부

				if(clsf == "new" || sbx_threefive.getValue() == ""){ // 사업분류 최초 선택시
					dma_mast.set("threefive", "RIC002");					
					// 3책5공제외사업기준
					dma_mast.set("threefive_clsf", "RIE006"); // 민간수탁사업

					if(dma_mast.get("bsns_cd_ofic") == "014000000"){ // 관계부처가 민간기업일 경우 의뢰기관 기업체여부 = Y
						radioCmpyYn.setValue("Y");
					}else{
						radioCmpyYn.setValue("N");
					}
				}
			} else { // 정부수탁
				group44.hide();
				radioCmpyYn.setValue("");

				sbx_threefive.setDisabled(false); // 3책5공 해당여부

				if(clsf == "new"){
					dma_mast.set("threefive", "");					
					// 3책5공제외사업기준
					dma_mast.set("threefive_clsf", "");
				}
			}
			// 보안등급
			btn_secu.hide();
		}		

		// 통합관리단위 셋팅
		if(/RHA002|RHA001/.test(bsnsCdInout)){ // RHA002  내부연구,  RHA001  정부수탁
			cbx_intgr_mngmt_unit.setDisabled(false);
			btn_limAmt.setDisabled(false);

			if(bsnsCdInout == "RHA002"){
				cbx_intgr_mngmt_unit.setItemDisabled(0 , true);
				cbx_intgr_mngmt_unit.setItemDisabled(1 , true);
			}else{
				cbx_intgr_mngmt_unit.setItemDisabled(1 , true);
				cbx_intgr_mngmt_unit.setItemDisabled(2 , true);
			}
		}else{
			cbx_intgr_mngmt_unit.setDisabled(true);
			btn_limAmt.setDisabled(true);
		}
	};

	/*
     * 연구비 그리드의 합계금액
     */
	scwin.calcGridAmtSummary = function() {
		var cashAmtSum  = 0 ;  // 행별 현금 합계(=정부출연금+기업현금+지방정부현금+대학현금+기타현금)
		var totCashAmt  = 0 ; // 행별 현금 합계(누적)
		var totGoodsAmt  = 0 ; // 행별 현금 합계(누적)

		var nFae021Sum = 0; // 대비목(명)의 값이 '직접비' 인 경우
		var nFae031Sum = 0; // 대비목(명)의 값이 '간접비' 인 경우
		var nFae041Sum = 0; // 대비목(명)의 값이 '위탁연구개발비' 인 경우

		for (var i = 0; i < dlt_bugt.getRowCount(); i++) {
			cashAmtSum  = Number(dlt_bugt.getCellData(i, "cash_tot_amt"));  // 현금합계

			totCashAmt  = totCashAmt + cashAmtSum;   // 현금 합계 누계
			totGoodsAmt = totGoodsAmt + Number(dlt_bugt.getCellData(i, "goods_tot_amt"));  // 현물합계 누계

			if (dlt_bugt.getCellData(i, "bugt_item") == "RHQ013") {
				spn_req_amt_sum.setValue(cashAmtSum); 
			}

			if(dlt_bugt.getCellData(i, "itm_nm") == "직접비"){
				nFae021Sum += cashAmtSum;
			}else if(dlt_bugt.getCellData(i, "itm_nm") == "간접비"){
				nFae031Sum += cashAmtSum;
			}else if(dlt_bugt.getCellData(i, "itm_nm") == "위탁연구개발비"){
				nFae041Sum += cashAmtSum;
			}
		}		

		// 연구비 현금합계
		txt_cash_amt_sum.setValue(totCashAmt);  
		// 연구비 현물합계
		ibx_goods_amt.setValue(totGoodsAmt);

		// 간접비편성액
		tbx_indicost_amt.setValue(nFae031Sum); 

		console.log("nFae021Sum === 직접비 === >> " + nFae021Sum);
		if(nFae021Sum > 0){
			// 연구장비비 통합관리비 비율계산(20210908 TO DO)
			var reqAmtSum = Number(spn_req_amt_sum.getValue());
			if(reqAmtSum >= 0){			
				var equipFeeRt = reqAmtSum / nFae021Sum * 10000;
				spn_req_rt.setValue(Math.round(equipFeeRt)/100);
			}

			// 정부수탁 :간접비 기준편성액 = 직접비 * 간접비율 * 100 : 1000미만에서 버림
			// 민간수탁: 원단위(소수점이 나오는 경우, 소수점 첫째자리에서 올림하여 원단위)
			if (dma_mast.get("bsns_cd_inout") == "RHA003"){
				var indicostAmt = Math.ceil(nFae021Sum * Number(txt_indrc_rt_bas.getValue()) / 100); 	
			}else{
				var indicostAmt = Math.floor(nFae021Sum * Number(txt_indrc_rt_bas.getValue()) / 100000) * 1000; 	
			}

		  	tbx_indicost_amt_bas.setValue(indicostAmt); 	

		  	// 간접비 편성비율
		  	var nChkRate = (nFae031Sum / nFae021Sum) * 100; 

			if(dma_mast.get("bsns_cd_inout") == "RHA001"){	// 정부수탁 올림
				txt_indrc_rt.setValue( ((Math.ceil(nChkRate * 1000)) / 1000)); 

			}else if (dma_mast.get("bsns_cd_inout") == "RHA003"){	
				txt_indrc_rt.setValue( ((Math.round(nChkRate * 1000)) / 1000));  
			}
		}
	};

	/*
     * 사업조정회의 심의 여부 셋팅
     */
	scwin.setConsider = function(consider){
		if(consider == "RII002" || consider == ""){ // 2018.08.20. N-> 조정중으로 변경			
			btn_conf.hide();
			spn_conf_req_no.hide();
			ibx_conf_req_no.hide();

			btn_excp_base.hide(); // 사업조정 제외기준
			sbx_consider_clsf.hide();
		} else if (consider == "RII001"){ // Y
			// 사업조정회의가 Y일 경우 사업조정회의 검색
			btn_conf.show("");
			spn_conf_req_no.show("");
			spn_conf_req_no.setValue("사업조정회의신청번호");
			ibx_conf_req_no.show("");

			btn_excp_base.hide();
			sbx_consider_clsf.hide();		
		} else if (consider == "RII003"){ // 제외			
			btn_conf.hide();
			spn_conf_req_no.show("");
			spn_conf_req_no.setValue("사업조정회의 제외기준");	
			ibx_conf_req_no.hide();

			btn_excp_base.show(""); // 사업조정 제외기준
			sbx_consider_clsf.show("");			
		}
	};

	/*
     * 저장 전 체크
     */
	scwin.preSave = function(){
		if(dlt_pare.getRowCount() == 0){
			alert("참여연구원이 존재하지 않습니다. \n [참여연구원등록]버튼을 클릭하여 사전등록한 내역을 선택하세요. ");
			return false;
		}

		if(cbx_intgr_mngmt_unit.getValue() == ""){
			if(WebSquare.util.parseInt(spn_req_amt_sum.getValue(), 0) > 0){
				alert("연구장비비 통합관리단위를 선택하지않을 경우 '연구시설장비비(특례)'를 입력할 수 없습니다.");
				return false;
			}
		}else if(cbx_intgr_mngmt_unit.getValue() != ""){
			if(WebSquare.util.parseInt(spn_req_amt_sum.getValue(), 0) == 0){
				alert("연구장비비 통합관리단위를 선택했을 경우 '연구시설장비비(특례)'금액을 입력하셔야합니다.");
				return false;
			}
		}

		var validData = [
			{dataObj : dma_mast
			   , valInfoArr :[
				    { id : "bsns_cd_ofic", mandatory : true},
					{ id : "bsns_cd_bsns", mandatory : true},
					{ id : "bsns_cd_detls_bsns", mandatory : true},
					{ id : "bsns_cd_inout", mandatory : true},
					{ id : "bsns_cd_exclsv_agncy", mandatory : true},	
					{ id : "resch_rspns_main", mandatory : true},
					{ id : "prj_nm", mandatory : true}
				]
			  , isCheckModified : false
			},
			{dataObj : dlt_pare
			  , valInfoArr :[
				    { id : "reschr_clsf", mandatory : true}
				   ,{ id : "psnexpns_clsf", mandatory : true } 				   
			    ]
			   , isCheckModified : false
			}
		];

		if (!com.validateData(validData)) return false;	

		return true;
	};

	/*
     * 저장
     */
	scwin.save = function(){
		if(dma_mast.get("secu01_yn") == "Y" || dma_mast.get("secu02_yn") == "Y" || dma_mast.get("secu03_yn") == "Y" || dma_mast.get("secu04_yn") == "Y"){
			sbx_secrt_prj_ex.setValue("Y");
		}else{
			sbx_secrt_prj_ex.setValue("N");
		}

		if(sbx_consider.getValue() == "RII001"){
			dma_mast.set("consider_clsf", ibx_conf_req_no.getValue());
		}

		com.file.transport( function(attachObj){
			debugger;
			dma_mast.set("middle_attach_file", attachObj.upload_A);
			dma_mast.set("bsns_notice_attach_file_no", attachObj.upload_B);
			var attaches = [];
			if(attachObj.upload_A !="") attaches.push(attachObj.upload_A);
			if(attachObj.upload_B !="") attaches.push(attachObj.upload_B);
			dma_mast.set("validAttachNo", attaches.join(","));				

			com.execute(sbm_saveData, {
				successCallback : function(e) { // 성공 콜백
					if(scwin.globVar.aprYn == "Y"){
						// 결재화면 이동
						com.movePageMenu("A001", { "reqNo" :  dma_mast.get("bsns_req_no")});
					}else{
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function(){
							// 조회
							scwin.search();
						});
					}
				}
			});
		});	
	};

	/*
     * 과제선정 전 체크
     */
	scwin.preSeltRst = function(){
		// 20160315 결과가 선정이었다가 취소 및 탈락으로 수정불가능
		if(scwin.globVar.bfSeltRst == "RAG001" && scwin.globVar.bfSeltRst != dma_mast.get("selt_rst")){
			alert("결과가 선정이었다가 취소 및 탈락으로 수정할 수 없습니다.");
			return false;
		}

		if(sbx_selt_rst.getValue() == ""){
			alert("선정여부를 입력하세요.");
			return false;
		}

		if(sbx_selt_rst.getValue() == "RAG001" && ica_selt_ymd.getValue() == ""){
			alert("선정일자를 입력해 주세요.");
			return false;
		}

		return true;
	};

	/*
     * 과제선정
     */
	scwin.saveSeltRst = function(){
		com.file.transport( function(attachObj){
			dma_mast.set("selt_attach_file_no", attachObj.upload_C);
			dma_mast.set("validAttachNo", attachObj.upload_C);	
			debugger;
			var rowData = dlt_pare.getMatchedJSON("reschr_clsf", "RAL001", true)[0];
			dma_mast.set("resch_rspns_main_mail", rowData.email);		

			// 최초선정이거나 선정결과가 탈락 또는 취소인경우 통프호출
			if(scwin.globVar.saveAprYn == "Y" || /RAG002|RAG003/.test(sbx_selt_rst.getValue())){
				dma_mast.set("approval", "true");
			}

			com.execute(sbm_saveSelt, {
				successCallback : function(e) { // 성공 콜백
					alert("선정결과를 등록하였습니다.");
					dma_mast.set("mail_send_yn", e.responseJSON.mail_send_yn);		

					// 결재화면 이동
					com.movePageMenu("A004",{});			
				}
			});
		});	
	};

	/**
	 * 사후결재 확인
	 */
	scwin.confirmPostApr = function(){
		var data = {
			req_no : dma_mast.get("bsns_req_no"),
			docu_id : "R11",
			syspayno : dma_mast.get("resch_rspns_main"),
			action_ymd : dma_mast.get("thyr_resch_prd_orign_ymd")
		};

		com.biz.confirmPostApr(data, function(ret){
			debugger;
			if(ret){
				scwin.checkApr();
			}
		});
	};

	/**
	 * 결재신청전 간접비율 체크
	 */
	scwin.checkIndrcRt = function(){
		var ymd = ica_step_resch_prd_orign_ymd.getValue();		

		var msg = "";
		var retFlag = "";
		if(dma_mast.get("bsns_cd_inout") == "RHA001"){ // 정부수탁			
			if(ymd >= "20191231" && ymd <= "20211231"){
				msg = "현 단계연구기간 시작일이 2019.12.31 ~ 2021.12.31." + msg;
			}else if(ymd >= "20220101" && ymd <= "20221220"){
				msg = "현 단계연구기간 시작일이 2022.01.01 ~ 2022.12.20." + msg;
			}else if(ymd >= "20221221"){
				msg = "현 단계연구기간 시작일이 2022.12.21.일 이후" + msg;
			}

			// 2023.03.29.아래의 경우에는 간접비 체크하지 않음(XC2202300C32)
			// (연구회 융합연구사업인 경우 인건비와 연구수당, 간접비와 우리 원에 제공하고 나머지 직접비는 파견기관에서 집행하게 되어 있어서 간접비가 미달된 거처럼 보여지고 제한되고 있음.)
			if(dma_mast.get("bsns_cd_ofic") == "039000000" // 과학기술정보통신부
					&& dma_mast.get("bsns_cd_exclsv_agncy") == "RHB008" // 국가과학기술연구회
					&& dma_mast.get("bsns_cd_bsns") == "039008000" // 융합연구사업
					&& dma_mast.get("bsns_cd_detls_bsns") == "039008004"){ // 융합연구단사업
				return { retFlag : retFlag, msg: msg};
			}

			if(Number(txt_indrc_rt.getValue()) > Number(txt_indrc_rt_bas.getValue())){
				msg += "인 과제의 간접비 비율은 \n" + txt_indrc_rt_bas.getValue() + "% 인데 초과되었습니다. \n 간접비는 초과가 불가하니 다시 확인하여 주시기 바랍니다.";	
				retFlag = "N";

			}else if(Number(txt_indrc_rt.getValue()) < Number(txt_indrc_rt_bas.getValue())){
				msg += "인 과제의 간접비 비율은 \n" + txt_indrc_rt_bas.getValue() + "% 인데 미달되었습니다. \n 간접비 미달관련 심의여부(연구업무규정[별표2]확인)를 확인하여 주시기 바랍니다.";
				retFlag = "Y";
			}
		}else if(dma_mast.get("bsns_cd_inout") == "RHA003"){ // 민간수탁
			if(dma_mast.get("bsns_cd_ofic") == "014000000"){  // 민간기업
				msg = "민간수탁사업 중 (기업수탁, 단기용역)의 ";
			}else if(dma_mast.get("bsns_cd_ofic") == "034000000"){ // 공공기관
				msg = "민간수탁사업 중 (공공수탁)의 ";
			}else if(dma_mast.get("bsns_cd_ofic") == "020000000"){ // 공공기관
				msg = "민간수탁사업 중 (지자체수탁)의 ";
			}

			if(Number(txt_indrc_rt.getValue()) < Number(txt_indrc_rt_bas.getValue())){
				msg += "간접비 비율은 \n" + txt_indrc_rt_bas.getValue() + "% 인데 미달되었습니다. \n 간접비 미달관련 심의여부(연구업무규정[별표2]확인)를 확인하여 주시기 바랍니다.";
				retFlag = "Y";
			}
		}

		return { retFlag : retFlag, msg: msg};
	};

	/**
	 * 결재신청전 체크
	 */
	scwin.preAprSave = async function(){	
		// 01.과세여부 체크
		if(!scwin.getTaxCd()) return;		

		// 02.연구비 계산
		scwin.calReschAmt();
		// 예외자 가지고 옴
		const values = await com.getExceptAwait('XEK040', 'RB42');
		// 03. 참여연구원 체크
		var vRestrNmList = []; // 참여연구원 참여제한 여부 체크		
		for(var i=0; i< dlt_pare.getRowCount(); i++){
			if(dlt_pare.getCellData(i, "reschr_clsf") == "RAL001"){
				dma_mast.set("resch_rspns_main", dlt_pare.getCellData(i, "syspayno"));
			}

			debugger;
			// 내부과제이거나 예외자가 아니면 참여제한(2023.04.13. 예외자조회 공통화(com.getExceptAwait))
			if(dma_mast.get("bsns_cd_inout") == "RHA002" ||
				values.some(function(elt) {
					return elt != dlt_pare.getCellData(i, "syspayno");
				})){
				if(dlt_pare.getCellData(i, "restr_yn") == "Y"){
					vRestrNmList.push(dlt_pare.getCellData(i, "nm"));
				}
			}

			// 20160623 연구재단과제(RHB040 한국연구재단)는 연구자등록번호 필수임
			// 20180321 개인번호 없는경우 검증제외
			if(dma_mast.get("bsns_cd_exclsv_agncy") == "RHB040" && dlt_pare.getCellData(i, "syspayno").substring(0, 4) != "9999"){
				if(dlt_pare.getCellData(i, "respersonno") == ""){
					alert(dlt_pare.getCellData(i, "nm") + "님의 연구자등록번호를 EIP 개인정보에 등록하여 주시기 바랍니다.");
					grd_pare.setFocusedCell(i, "respersonno");
					return false;
				}

				if(dlt_pare.getCellData(i, "respersonno").length != 8){
					alert("연구자등록번호는 8숫자입니다.  등록번호가 맞는지 확인하신후 EIP 개인정보를 수정하여 주시기 바랍니다.");
					grd_pare.setFocusedCell(i, "respersonno");
					return false;
				}
			}			
		}

		if(vRestrNmList.length > 0){
			alert(vRestrNmList.join(",") + " 참여연구원은 참여제한 중에 있습니다.");
			return;
		}

		if(com.file.action.getTotalFileCount(upload_A.getID()) < 1){
			alert("사업계획서를 첨부하여 주시기 바랍니다.");
			return false;
		}

		if(rdo_pre_plan_prj.getValue() == "Y"){
			if(dlt_prePrj.getRowCount() == 0){
				alert("선행연구과제를 등록해주세요.");
				return false;
			}	

			if(grd_prePrj.getFooterData("contr_rt_sum") != 100){
				alert("선행연구과제 기여비율의 합을 100으로 맞춰 입력하십시오.");
				return false;
			}

			if(dlt_prePrj.getRowCount() > 3){
				alert("선행연구과제는 최대 3개까지 입력가능합니다.");
				return false;
			}

			for(var i=0; i<dlt_prePrj.getRowCount(); i++){
				if(dlt_prePrj.getCellData(i,"contr_rt") == "" || dlt_prePrj.getCellData(i,"contr_rt") == "0"){
					alert("선행연구과제 기여비율을 입력하십시오.");
					grd_prePrj.setFocusedCell(i, "contr_rt", true);
					return false;
				}
			}

			// 과제중복 체크
			var accnts = dlt_prePrj.getColData("accnt_no");
			if(accnts.some(function(el){return accnts.indexOf(el) != accnts.lastIndexOf(el);})){
				alert("선행연구과제가 중복으로 입력되었습니다.");
				grd_prePrj.setFocusedCell(i, "accnt_no", true);
				return false;
			}
			// 기여율 중복 체크
			var contrRts = dlt_prePrj.getColData("contr_rt");
			if(contrRts.some(function(el){return contrRts.indexOf(el) != contrRts.lastIndexOf(el);})){
				alert("선행연구과제 간 동일한 기여율은 입력하실 수 없습니다.");
				grd_prePrj.setFocusedCell(i, "contr_rt", true);
				return false;
			}			
		}

		if(dma_mast.get("bsns_cd_inout") == "RHA001"){
			//사업공고문 필수체크 추가 20210322
			if(com.file.action.getTotalFileCount(upload_B.getID()) < 1){
				alert("정부수탁 과제인 경우, 3책5공 해당여부 확인을 위하여 사업공고문은 필수입니다. 사업공고문을 첨부하여 주시기 바랍니다.");
				return false;	
			}
		}

		// 필수값 체크
		if(!com.validateData(dma_mast, [
			    { id : "tot_resch_prd_orign_ymd", mandatory : true},
				{ id : "tot_resch_prd_cls_ymd", mandatory : true},
				{ id : "step_resch_prd_orign_ymd", mandatory : true},
				{ id : "step_resch_prd_cls_ymd", mandatory : true},		
				{ id : "cur_step", mandatory : true},		
				{ id : "tot_resch_amt", mandatory : true},
				{ id : "prj_card_clsf", mandatory : true},
				{ id : "part_clsf", mandatory : true},
				{ id : "wrk_clsf", mandatory : true},
				{ id : "total_res_amt", mandatory : true},
				{ id : "threefive", mandatory : true},
				{ id : "intrn_use_ex", mandatory : true},
				{ id : "note_use_ex", mandatory : true},				
				{ id : "prj_eng_nm", mandatory : true},
				{ id : "dev_goal", mandatory : true},
				{ id : "kwd_krchar", mandatory : true},
				{ id : "kwd_eng", mandatory : true},
				{ id : "consider", mandatory : true},
				{ id : "thyr_resch_prd_orign_ymd", mandatory : true},
				{ id : "thyr_resch_prd_cls_ymd", mandatory : true},
			], false)) return false;	

		// 20170926 수행과제 보안등급 분류 및 심사기준 선택기능 추가에 따른 검증추가(내부연구(기관주요), 자체연구만 해당)
		if(/RHA002|RHA004/.test(dma_mast.get("bsns_cd_inout"))){
			if(dma_mast.get("secu01_yn") == "" && dma_mast.get("secu02_yn") == "" && dma_mast.get("secu03_yn") == "" && dma_mast.get("secu04_yn") == ""){
				alert("수행과제 보안등급 분류 및 심사기준을 선택해 주세요.\n[심사기준]버튼 클릭!");
				return false;
			}
		}

		// 2021.09.15. 신청 %는 수정집접비(연구과제의 직접비중 '현물부담액'과 '위탁연구개발비'를 제외한 직접비, 기본사업비인 경우 '연구개발장비 시스템구축비'도 제외)의 10% 이내
		if(Number(spn_req_rt.getValue()) > 10){
			alert("연구장비비 통합관리비는 수정직접비의 10% 이내여야 합니다.");
			return false;
		}

		if(cbx_intgr_mngmt_unit.getValue() == ""){
			if(WebSquare.util.parseInt(spn_req_amt_sum.getValue(), 0) > 0){
				alert("연구장비비 통합관리단위를 선택하지않을 경우 '연구시설장비비(특례)'를 입력할 수 없습니다.");
				return false;
			}
		}else if(cbx_intgr_mngmt_unit.getValue() != ""){
			if(WebSquare.util.parseInt(spn_req_amt_sum.getValue(), 0) == 0){
				alert("연구장비비 통합관리단위를 선택했을 경우 '연구시설장비비(특례)'금액을 입력하셔야합니다.");
				return false;
			}
		}

		if(sbx_note_use_ex.getValue() == "Y"){
			if(sbx_note_type_cd.getValue() == ""){
				alert("연구노트 구분을 선택하세요");
				sbx_note_type_cd.focus();
				return false;
			}
		}else if(sbx_note_use_ex.getValue() == "N"){
			if(sbx_note_type_cd.getValue() != ""){
				alert("연구노트 사용유무가 [Y]인 경우 연구노트 구분을 선택할 수 없습니다.");
				sbx_note_type_cd.focus();
				return false;
			}
		}

		if(dma_mast.get("tot_resch_prd_orign_ymd") > dma_mast.get("tot_resch_prd_cls_ymd")){
			alert("총 연구기간의 시작일이 종료일보다 큽니다.");
			return false;
		}

		// 당해년도 연구기간 체크 및 총연구기간 비교 체크
		scwin.validatePrd(2);
		// 현단계 연구기간 체크 및 총연구기간 비교 체크
		scwin.validatePrd(3);

		if(sbx_threefive.getValue() == "RIC002"){ // RIC002 제외
			if(dma_mast.get("threefive_clsf") == ""){
				alert("3책5공 해당여부가 [제외]일 경우 3책5공 제외사업기준을 선택하여야 합니다.");
				scwin.threeFivePopup("thr");
				return false;
			}
		}

		if(sbx_consider.getValue() == "RII003"){ // RII003 제외
			if(dma_mast.get("consider_clsf") == ""){
				alert("사업조정회의 심의여부가 [제외]일 경우 사업조정 제외기준을 선택하여야 합니다.");
				scwin.threeFivePopup("con");
				return false;
			}
		}else if(sbx_consider.getValue() == "RII001"){ // Y
			if(ibx_conf_req_no.getValue() == ""){
				alert("사업조정 심의여부가 Y일 경우 사업조정회의는 필수선택사항입니다. \n 궁금하신점은 사업전략실에 문의하시기 바랍니다.");
				scwin.openBsnsConfPop();
				return false;
			}
		}		

		for(var i=0; i<dlt_pare.getRowCount(); i++){
			// XC22014014C8 20150223
			// - 대상 : 내부1 행정부문
			// - 프로젝트의 참여연구원 현금 계상 불가 (현물 제외)
			if(dlt_pare.getCellData(i, "job_clsf") == "HAE030" && dlt_pare.getCellData(i, "psnexpns_clsf") == "RAM001"){ // HAE030 행정부문
				if(Number(dlt_pare.getCellData(i, "cash_amt")) > 0){
					alert("[" + dlt_pare.getCellData(i, "nm") + "]님은 직무구분이 [행정부문]으로 현금계상이 불가합니다.");
					return false;
				}
			}
		}

		return true;		
	};

	/**
	 * 결재신청전 체크
	 */
	scwin.checkApr = async function(){
		if(!scwin.preSave()) return;

		const isExcept = await scwin.preAprSave();
		if(!isExcept) return;

		const isExcept1 = await com.isExceptAwait('XEK070', 'RB42', textboxBsnsReqNo.getValue());

		// 연구비-간접비 비율 계산 및 체크
		if(!isExcept1){
			var obj = scwin.checkIndrcRt();
		}
		else{
			scwin.sendApr();
		}

		if(obj.retFlag != ""){
			com.alert(obj.msg, function(ret){				
				if(obj.retFlag =="Y"){					
					scwin.sendApr();
				}
			});
		}else{
			scwin.sendApr();
		}
	};

	/**
	 * 결재신청
	 */
	scwin.sendApr = function(){
		scwin.globVar.aprYn = "Y";
		dma_mast.set("approval", "true");
		dma_mast.set("req_state", "RAF002");

		// 결재신청을 위한 셋팅
		// 총연구기간
		var setTotReschPrdTerm = com.formatDate(dma_mast.get("tot_resch_prd_orign_ymd","dash")) + " ~ " + com.formatDate(dma_mast.get("tot_resch_prd_cls_ymd","dash"))
								+ " ( " + ibx_tot_prd_term_year.getValue() + "년 " + ibx_tot_prd_term_month.getValue() + "개월 )";
		dma_etc.set("tot_resch_prd_term", setTotReschPrdTerm);

		// 현단계 연구기간
		var setCurReschPrdTerm = com.formatDate(dma_mast.get("step_resch_prd_orign_ymd","dash")) + " ~ " + com.formatDate(dma_mast.get("step_resch_prd_cls_ymd","dash"))
								+ " ( " + ibx_step_prd_term_year.getValue() + "년 " + ibx_step_prd_term_month.getValue() + "개월 ) " + ibx_cur_step.getValue() + "단계";
		dma_etc.set("cur_resch_prd_term", setCurReschPrdTerm); 

		// 당해년도 연구기간
		var setYearReschPrdTerm = com.formatDate(dma_mast.get("thyr_resch_prd_orign_ymd","dash")) + " ~ " + com.formatDate(dma_mast.get("thyr_resch_prd_cls_ymd","dash"))
								+ " ( " + ibx_thyr_prd_term_year.getValue() + "년 " + ibx_thyr_prd_term_month.getValue() + "개월 )";
		dma_etc.set("year_resch_prd_term", setYearReschPrdTerm);


		// 참여연구원 인건비 합계
		dma_etc.set("psnexpns_month_unit_price_sum", grd_pare.getFooterData("psnexpns_month_unit_price_sum"));
		dma_etc.set("cash_amt_sum", grd_pare.getFooterData("cash_amt_sum"));
		// 연구비 셋팅
		var getVal01 = com.formatDate(dlt_bugt.getCellData(0, "start_ymd","dash")) + " ~ " + com.formatDate(dlt_bugt.getCellData(0, "cls_ymd","dash"));
		dma_etc.set("bugt_smmry_1", " ( 당해년도 : " + getVal01 + ")");

		var overStr = (dma_mast.get("bsns_cd_inout") == "RHA003")? "이상)" : "이하)";
		var getVal02 = "현금합계 : "   + txt_cash_amt_sum.getFormatValue()  + "   " +
					   "부가세액 : "   + ibx_tax_amt.getFormattedValue()  + "   " +
					   "간접비 : "     + txt_indrc_rt.getValue() + "% (기준 : " + txt_indrc_rt_bas.getValue() + overStr + "     " +
					   "간접비 편성액 : " + tbx_indicost_amt.getFormatValue() + "(기준 : " + tbx_indicost_amt_bas.getFormatValue() + " " + overStr;
		dma_etc.set("bugt_smmry_2", getVal02);
		dma_etc.set("gov_cntrbamt_sum", grd_bugt.getFooterData("gov_cntrbamt_sum"));
		dma_etc.set("cmpy_cash_sum", grd_bugt.getFooterData("cmpy_cash_sum"));
		dma_etc.set("cmpy_goods_sum", grd_bugt.getFooterData("cmpy_goods_sum"));

		var etcSum = Number(grd_bugt.getFooterData( "contpat_goods_sum" )) 
					+ Number(grd_bugt.getFooterData( "local_gov_cash_sum" )) 
					+ Number(grd_bugt.getFooterData( "univ_cash_sum" )) 
					+ Number(grd_bugt.getFooterData( "etc_cash_sum" ));
		dma_etc.set("etc_amt_sum", etcSum);
		dma_etc.set("tot_amt_sum", grd_bugt.getFooterData("tot_amt_sum"));		

		scwin.save();
	};

	/*
     * 사업조정회의 팝업 오픈
     */
	scwin.openBsnsConfPop = function(){
		biz.res.pop.open(biz.res.pop.TYPE.BSNS_ADJST, "", function(ret) {
			if(ret){
				ibx_conf_req_no.setValue(ret.req_no);
			}
		});
	};	

	/*
     * 페이지 이동
     */
	scwin.movePage = function(data){
		var pageId = "";
		if(scwin.globVar.openerId){
			pageId = scwin.globVar.openerId;
		}else{
			if(scwin.globVar.usrAdmFlag == "ADMN"){
				pageId = "RB41";
			}else{
				pageId = "JB41";
			}
		}

		com.moveList(pageId, data);
	};	

