	/**
	*  사업분류 버튼 클릭 이벤트
	*/
	scwin.btn_bsnsPop_onclick = function(e) {
		biz.res.pop.open(biz.res.pop.TYPE.SEL_BSNS, {}, function(ret) {
			if (ret) {
				dma_mast.set("bsns_cd_inout", ret.bsns_cd_inout);
				dma_mast.set("bsns_cd_ofic", ret.bsns_cd_ofic);
				dma_mast.set("bsns_cd_exclsv_agncy", ret.bsns_cd_exclsv_agncy);
				dma_mast.set("bsns_cd_bsns", ret.bsns_cd_bsns);
				dma_mast.set("bsns_cd_detls_bsns", ret.bsns_cd_detls_bsns);
				dma_mast.set("bsns_cd_ofic_nm", ret.bsns_cd_ofic_nm);
				dma_mast.set("bsns_cd_bsns_nm", ret.bsns_cd_bsns_nm);
				dma_mast.set("bsns_cd_detls_bsns_nm", ret.bsns_cd_detls_bsns_nm);
				dma_mast.set("bsns_cd_inout_nm", ret.bsns_cd_inout_nm);
				dma_mast.set("bsns_cd_exclsv_agncy_nm", ret.bsns_cd_exclsv_agncy_nm);
				dma_mast.set("oh_absrp_rt", ret.oh_absrp_rt);

				scwin.setBsnsCdNm();

				// 간접비율
				if(ica_step_resch_prd_orign_ymd.getValue() != ""){
					txt_indrc_rt_bas.setValue(biz.res.getIndrcRt(dma_mast.get("bsns_cd_inout"), dma_mast.get("bsns_cd_ofic"), dma_mast.get("step_resch_prd_orign_ymd")));
				}

				// 내외구분에 따른 컴포넌트 제어
				scwin.setComp("new");
			}
		});
	};

	/**
