			scwin.secrtPopup();
		}
	};

	/**
	 * 선행과제내역 그리드 계정버튼 클릭 이벤트
	 */
	scwin.grd_prePrj_oncellclick = function(row,col,colId) {
		if (colId == "btn_accnt") {
			var data = {
				"clsf" : "prePrj",
				"syspayno" : scwin.loginInfo.sn,
				"empno" : scwin.loginInfo.empno,
				"nm" : scwin.loginInfo.cn,
				"ref_yn" : "Y"
			}

			com.pop.open( com.pop.TYPE.ACNNO, data, function(ret){
				if(ret){					
					if(dlt_prePrj.getMatchedIndex("accnt_no", ret.accnt_no, true).length >0) return;
					dlt_prePrj.setCellData(row, "accnt_no", ret.accnt_no);
					dlt_prePrj.setCellData(row, "prj_nm", ret.accnt_no_nm);
					dlt_prePrj.setCellData(row, "accnt_rspns_nm", ret.accnt_rspns_nm);
					var totYrYmd = ret.start_ymd.substring(0, 4) + "-" + ret.start_ymd.substring(4, 6) + "-" + ret.start_ymd.substring(6, 8) + " ~"
								+ ret.cls_ymd.substring(0, 4) + "-" + ret.cls_ymd.substring(4, 6) + "-" + ret.cls_ymd.substring(6, 8);
					dlt_prePrj.setCellData(row, "tot_yr_ymd", totYrYmd); 
				}
			});
		}
	};

	// 선행과제 추가
	scwin.btn_prePrj_add_onclick = function(e) {
		com.addRow(grd_prePrj);
	};

	// 선행과제 삭제
	scwin.btn_prePrj_del_onclick = function(e) {
		var rowIdxArr = dlt_prePrj.getCheckedIndex("chk");

		if (rowIdxArr.length == 0) {
			com.alert("삭제할 행을 선택하세요.");
			return;		
		}

		rowIdxArr.reverse().forEach(function(idx) {
			dlt_prePrj.removeRow(idx);
		});
	};

	// 과제 참여 유형 RadioBox 이벤트
	scwin.rdo_part_clsf_onviewchange = function(info) {
		if(dma_mast.get("bsns_cd_inout") == ""){
			alert("사업구분을 먼저 선택해주십시오.");
			this.setValue("");
			return false;
		}

		if(dma_mast.get("bsns_cd_inout") == "RHA001"){
			if (this.getValue() == "RCA020") {
				alert("[위탁과제]인 경우 3책5공은 [제외]처리되며, 기준은 [위탁연구과제]로 등록됩니다.");
				sbx_threefive.setValue( "RIC002" ); // 3책5공 해당여부 제외
				sbx_threefive.setReadOnly( true );
				dma_mast.set("threefive_clsf", "RIE008" );
			}else{
				sbx_threefive.setValue( "" ); 
				dma_mast.set("threefive_clsf", "" );
				sbx_threefive.setReadOnly( false );
			}
		}
	};	

	/*
     * 총연구기간 시작일자 변경 이벤트
     */
	scwin.ica_tot_resch_prd_orign_ymd_onviewchange = function(info) {	
		if(!scwin.validatePrd(1, "orign_ymd")) return;

		scwin.calcTotReschPrdTerm("orign_ymd", 1);
	};

