		// 몇년, 몇월
		scwin.calcThyrReschPrdTerm("cls_ymd");		
	};

	/*
     * 사업계획서 작성 참고자료 클릭 이벤트
     */
	scwin.btn_downCheck_onclick = function(e) {
		var url = "https://neip.kitech.re.kr/#w2xPath=/ui/board/boardView.xml?itemId=120065516289&boardId=100000000195";
		window.open(url, "_blank", "width=1024, height = 768, toolbar=no, menubar=no, scrollbars=yes, resizable=yes" );
	};

	/*
     * 연구노트 사용유무 변경이벤트
     */	
	scwin.sbx_note_use_ex_onchange = function(info) {
		if(this.getValue() == "Y"){
			// 사업구분이 내부과제일 경우 연구노트사용유무가 Y일 경우 연구노트 구분은 "전자"로 자동셋팅(수정불가) - 2023.01.09.
			if(dma_mast.get("bsns_cd_inout") == "RHA002"){
				sbx_note_type_cd.setValue("RCN002");
				sbx_note_type_cd.setDisabled( true );
			}else{
				sbx_note_type_cd.setDisabled( false );
			}
		}else{
			sbx_note_type_cd.setValue("");
			sbx_note_type_cd.setDisabled( true );
		}
	};

	/*
     * 3책5공 해당여부 변경 이벤트
     */
	scwin.sbx_threefive_onviewchange = function(info) {
		var partClsf = rdo_part_clsf.getValue(); // 과제참여유형
		var wrkClsf = sbx_wrk_clsf.getValue(); // 중소기업 참여유형
		// RIC002 제외
		// RCA018 협동기관, RCA019 공동(참여)기관
		// RWC03 협동/공동(참여)/위탁 
		if(this.getValue() == "RIC002" && wrkClsf == "RWC03" && /RCA018|RCA019/.test(partClsf)){
			com.alert("3책5공이 제외인 경우 우리원과 중소기업\n참여유형이 모두 [협동]기관이 될 수 없습니다.\n다시 선택하여주시길바랍니다.");
			sbx_threefive.setValue( "" );
			inputThreeFiveClsf.setValue( "" );
			sbx_threefive.setDisabled(false);
		}

		if(this.getValue() == "RIC002"){
			btn_expt_bsns.setDisabled(false);
		}else{
			btn_expt_bsns.setDisabled(true);
		}
	};

	/*
     * 제외사업기준 버튼 클릭이벤트
     */
	scwin.btn_expt_bsns_onclick = function(e) {
		if ( sbx_threefive.getValue() == "RIC002" ) {
			scwin.threeFivePopup("thr");
		} else if ( sbx_threefive.getValue() == "RIC001" ) {
			com.alert("3책5공 해당여부가 대상으로 되어 있어 제외사업기준을 선택하실 필요가 없습니다. ");
			return;
		} else {
			com.alert("3책5공 해당여부를 먼저 선택하세요.");
			return;
		}
	};	

	/*
     * 사업조정회의 심의여부 변경이벤트
     */
	scwin.sbx_consider_onviewchange = function(info) {
		var value = sbx_consider.getValue();

		// 사업조정회의 관련 컴포넌트 셋팅
		scwin.setConsider(value);

		if(value == "RII002"){ // 2018.08.20. N-> 조정중으로 변경
			// 사업조정회의관련
			ibx_conf_req_no.setValue("");
			com.alert("사업신청 1개월 전 사업조정회의 검토필요(각 사업지원실 문의)");
		} else if (value == "RII001"){ // Y
			// 사업조정회의가 Y일 경우 사업조정회의 검색			
		} else if (value == "RII003"){ // 제외
			ibx_conf_req_no.setValue("");			
		}		
	};

	/*
     * 사업조정회의 버튼 클릭 이벤트
     */
	scwin.btn_conf_onclick = function(e) {
		if ( sbx_consider.getValue() == "RII001" ) {
			// 사업조정회의 팝업 오픈
			scwin.openBsnsConfPop();
		} else {
			com.alert("사업조정회의 심의여부가 [Y]일 경우만 가능합니다.");
			return;
		}
	};

	/*
     * 사업조정제외기준 버튼 클릭 이벤트
     */
	scwin.btn_excp_base_onclick = function(e) {
		if ( sbx_consider.getValue() == "RII003" ) {
			scwin.threeFivePopup("con");
		} else {
			com.alert("사업조정회의 심의여부가 [제외]일 경우만 가능합니다.");
			return;
		}
	};

	/*
     * 참여연구원 등록 버튼 클릭 이벤트
     */
	scwin.btn_partPsn_onclick = function(e) {
		var data = {
			syspayno : scwin.loginInfo.sn,
			empno : scwin.loginInfo.empno,
			nm : scwin.loginInfo.cn,
			bsns_req_no : dma_mast.get("bsns_req_no").trim() 
		};

		biz.res.pop.open(biz.res.pop.TYPE.SEL_ATTENCE, data, function(ret) {
			if(ret){				
				// 사전등록 신청번호 셋팅
				dma_mast.set("req_no", ret.req_no);
				dma_mast.set("dept_cd", ret.dept_cd);
				dma_mast.set("dept_new_ymd", ret.dept_new_ymd);

				// 참여연구원 조회
				dma_search.set("req_no", ret.req_no);
				dma_search.set("syspayno", "");
				dma_search.set("apprvl_yn", "Y");
				dma_search.set("src_clsf", "PARE_MNG");

				com.execute(sbm_selectAttnceBsnsReq, {
					successCallback : function(e) {
						dlt_pare.setJSON(e.responseJSON.resAppPareVoList);

						// 연구책임자 셋팅
						var rowData = dlt_pare.getMatchedJSON("reschr_clsf", "RAL001", true)[0];
						dma_mast.set("resch_rspns_main", rowData.syspayno);
						dma_mast.set("resch_rspns_main_empno", rowData.empno);
						dma_mast.set("resch_rspns_main_nm", rowData.nm);		

						ibx_resch_rspns_main_empno.setDisabled(true);		
						btn_resch_rspns.setDisabled(true);

						dma_search.set("src_clsf", "");
					}
				});
			}
		});
	};

	/**
	 * 한도금액 조회 버튼 클릭 이벤트
	 */
	scwin.btn_limAmt_onclick = function(e) {
		if(cbx_intgr_mngmt_unit.getValue() == ""){
			com.alert("연구장비비 통합관리단위를 선택하세요.");
			return;
		}

		if (dlt_pare.getRowCount() > 0 ){ // 연구책임자가 있어야 조회가능			
			scwin.getLimitAmt();
		}

	};	

	/**
	 * 연구비합계 변경 이벤트
	 */
	scwin.ibx_tot_resch_amt_onviewchange = function(info) {
		if(info.newValue == "" || info.newValue == "0") return;

		// 관계부처 체크
		if(dma_mast.get("bsns_cd_ofic").trim().length < 1){
			com.alert("사업분류를 선택해 주세요.");
			btn_bsnsPop.focus();
			ibx_tot_resch_amt.setValue(0);
			return;
		}

		// 총연구기간 체크
		if (ica_tot_resch_prd_orign_ymd.getValue().length < 1) {
			com.alert("총 연구기간을 입력해 주세요.");
			ica_tot_resch_prd_orign_ymd.focus();
			ibx_tot_resch_amt.setValue(0);
			return;
		}

		// 당해연구기간 체크
		if (ica_thyr_resch_prd_orign_ymd.getValue().length < 1) {
			com.alert("당해년도 연구기간을 입력해 주세요.");
			ica_thyr_resch_prd_orign_ymd.focus();
			ibx_tot_resch_amt.setValue(0);
			return;
		}

		var totReschAmt = com.parseInt(ibx_tot_resch_amt.getValue());

		// 20170410 공급가액 및 부가세 자동계산되지 않도록 요청(박민주)
		if(scwin.getTaxCd()){
			scwin.calReschAmt();
		}else{
			this.setValue(0);
		}		
	};

	/**
	 * 선정평가위원회 클릭 이벤트
	 */
	scwin.btn_apcCommit_onclick = function(e) {
		var data = {
			bsns_req_no : dma_mast.get("bsns_req_no"),
			cmitt_clsf : "REX001", // REX001 선정
		};

		biz.res.pop.open(biz.res.pop.TYPE.APC_CMT, data, function(ret) {
			if(ret){
				ica_selt_ymd.setValue( ret.selt_ymd );
			}
		});
	};

	/**
	 * 사업비청구서바로가기 버튼 클릭 이벤트
