			, v_req_ymd_to : dma_mast.get("req_ymd")
		}
		com.openTabMenu("RB91", addParm, function(ret){
		});
	};

	/*
     * 연구비 그리드 변경 이벤트
     */ 
	scwin.grd_bugt_onviewchange = function(info) {
		if(ibx_tot_resch_amt.getValue() == ""){
			alert("연구비합계금액을 입력하세요.");
			return;	
		}

		var row = info.rowIndex;
		var cashAmtSum = Number(dlt_bugt.getCellData(row, "gov_cntrbamt"))  // 정부출연금
		 			   + Number(dlt_bugt.getCellData(row, "cmpy_cash"   ))  // 기업현금
		 			   + Number(dlt_bugt.getCellData(row, "local_gov_cash"))  // 지방정부현금
					   + Number(dlt_bugt.getCellData(row, "univ_cash"   ))  // 대학현금
					   + Number(dlt_bugt.getCellData(row, "etc_cash"    ));  // 기타현금

		if(dlt_bugt.getCellData(row, "bugt_item") == "RHQ013"){
			if(cashAmtSum > 0){
				if(cbx_intgr_mngmt_unit.getValue() == ""){
					alert("연구장비비 통합관리단위를 선택하세요.");
					cbx_intgr_mngmt_unit.focus();
					dlt_bugt.setCellData(row, "gov_cntrbamt", 0);
					return;
				}

				var distrRtSum = 0;
				for(var i=0; i<dlt_equipAmt.getRowCount(); i++){
					if(dlt_equipAmt.getCellData(i, "distr_rt") == "" || dlt_equipAmt.getCellData(i, "distr_rt") == 0){
//						alert("연구장비비 통합관리단위별 배분비율을 입력하세요.");
//						dlt_bugt.setCellData(row, "gov_cntrbamt", 0);
//						return;
						// 연구장비비 통합관리단위를 1개만 선택할 수 있기때문에 무조건 배분비율은 100으로 고정 (2023.01.11.)
						dlt_equipAmt.setCellData(i, "distr_rt", 100);
					}

					// 연구장비비 통합관리단위를 1개만 선택할 수 있기때문에 연구시설장비비(특례)를 분리할 필요없음(2023.01.11.)
					//distrRtSum += Number(dlt_equipAmt.getCellData(i, "distr_rt"));
					// 배분비율대로 계산
					//var tmpAmt = cashAmtSum * Number(dlt_equipAmt.getCellData(i, "distr_rt")) /100;	

					if(cashAmtSum > Number(dlt_equipAmt.getCellData(i, "accm_ramt_amt"))){
						alert("연구시설장비비(특례) 배분금액 [" + cashAmtSum + "]이 적립한도잔액 [ " + dlt_equipAmt.getCellDisplayData( i , "accm_ramt_amt" ) + " ]원을 초과하였습니다.");
						dlt_bugt.setCellData(row, "gov_cntrbamt", 0);
						return;
					}

					dlt_equipAmt.setCellData(i, "req_amt", cashAmtSum);
				}

				// 배분비율의 합이 100이 되도록 체크
//				if(distrRtSum != 100){
//					alert("연구장비통합관리비 '통합관리단위'별 배분비율의 합은 100이어야 합니다.");
//					dlt_bugt.setCellData(row, "gov_cntrbamt", 0);
//					return;
//				}
			}else{
				for(var i=0; i<dlt_equipAmt.getRowCount(); i++){
					dlt_equipAmt.setCellData(i, "req_amt", 0);
				}
			}
		}			

		var goodsAmtSum = Number(dlt_bugt.getCellData(row, "cmpy_goods"    ))  // 기업현물
					+ Number(dlt_bugt.getCellData(row, "contpat_goods" ));  // 상대국현물

		dlt_bugt.setCellData(row, "cash_tot_amt", cashAmtSum);
		dlt_bugt.setCellData(row, "goods_tot_amt", goodsAmtSum);
		dlt_bugt.setCellData(row, "tot_amt", cashAmtSum + goodsAmtSum);

		// 합계금액 구하기
		scwin.calcGridAmtSummary();

		// 연구장비 통합관리비특례 및 협약비율 셋팅
		for(var i=0; i<dlt_equipAmt.getRowCount(); i++){
			dlt_equipAmt.setCellData(i, "req_amt_sum", spn_req_amt_sum.getValue());
			dlt_equipAmt.setCellData(i, "req_rt", spn_req_rt.getValue());
		}
	};

	/*
     * 저장 버튼 클릭 이벤트
     */
	scwin.btn_save_onclick = function(e) {
		if(dma_mast.get("apr_state") == "XAD100"){
			if(!scwin.preSeltRst()) return;
			// 선정결과 저장
			scwin.saveSeltRst();
		}else{					
			// 저장전 필수 체크
			if(!scwin.preSave()) return;

			com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret){
				if(ret){
					scwin.save();
				}
			});
		}
	};

