	scwin.btn_list_onclick = function(e) {
		scwin.movePage();
	};

	/*
     * 결재신청 버튼 클릭 이벤트
     */
	scwin.btn_apr_onclick = function(e) {
		var data={
			dept_cd : dma_mast.get("dept_cd"),	
			dept_new_ymd : dma_mast.get("dept_new_ymd"),	
		};

		com.biz.getResMan(data, function(ret){
			if(ret == undefined || ret.syspayno.trim() == ""){
				alert("소속부서별 연구관리담당자 기준정보에 문제가 있습니다. 해당 연구관리담당자에게 문의하시기 바랍니다.");
				return;
			}

			dma_etc.set("charg_syspayno", ret.syspayno);

			// 정부수탁, 민간수탁 과제의 경우만 해당. 사후결재기능 추가  20171205(이민기 행정원 요청, 김상희팀장 확인)
			if(/RHA001|RHA003/.test(dma_mast.get("bsns_cd_inout"))){
				scwin.confirmPostApr();
			}else{
				scwin.checkApr();
			}	
		});	
	};

	/*
     * 중소기업 참여유형 변경 이벤트
     */
	scwin.sbx_wrk_clsf_onviewchange = function(info){
		if(info.newValue == "") return;

		if(dma_mast.get("bsns_cd_inout") == ""){
			alert("사업구분을 먼저 선택해주십시오.");
			this.setValue("");
			return;
		}

		if(rdo_part_clsf.getValue() == ""){
			alert("과제참여유형을 먼저 선택해주십시오.");
			this.setValue("");
			return;
		}		

		var partClsf = rdo_part_clsf.getValue();
		var totalResAmt = Number(ibx_total_res_amt.getValue());
		var totReschTerm = Number(ibx_tot_resch_prd_term.getValue());
		var divResAmt = totalResAmt / totReschTerm;			

		// RCA020 위탁기관, RCA028 총괄+주관기관, RCA017 주관기관, RCA018	협동기관, RCA019	공동(참여)기관
		// RWC02 주관, RWC03 협동/공동(참여)/위탁
		// [중소기업참여]가 없음이 아니고 [정부수탁]이며 [위탁과제]가 아닌 경우
		if(dma_mast.get("bsns_cd_inout") == "RHA001" && partClsf != "RCA020"){	
			if(!ibx_total_res_amt.getValue()|| !ibx_tot_resch_prd_term.getValue()){
				alert("[총 연구기간]과 [총 연구비]를 먼저 입력해주십시오.");
				this.setValue("");
				return;
			}

			if(this.value == "RWC01"){
				// [중소기업참여]가 없음이며, 5000만원 이하인 경우
				if(divResAmt <= 50000000){
					if(confirm("총 연구비/년차가 [5000만원 이하]이며 중소기업 [미참여] 시,\n사업공고문 3책5공 [제외]기준\n [소규모 연구개발과제] 구분으로 선택하시겠습니까?\n\n[사업공고문에 '3책 5공 제외'가 있는 경우] '확인' 클릭,\n[사업공고문에 '3책 5공 제외'가 없는 경우] '취소' 클릭 ")){
						sbx_threefive.setValue( "RIC002" ); // 3책5공 해당여부 제외
						dma_mast.set("threefive_clsf", "RIE004" );  
			    	}
				}
			}else if(this.value == "RWC02"){
				if(/RCA028|RCA017/.test(partClsf)){
					alert("우리원과 중소기업 참여유형이 모두 [주관]기관이 \n될 수 없습니다. 다시 선택하여주시길바랍니다.");
					this.setValue("");						
				}
			}else if(this.value == "RWC03"){
				if(sbx_threefive.getValue() == "RIC002" && /RCA018|RCA019/.test(partClsf)){
					alert("3책5공이 제외인 경우 우리원과 중소기업\n참여유형이 모두 [협동]기관이 될 수 없습니다.\n다시 선택하여주시길바랍니다.");
					this.setValue("");
				}					
			}

			 // [과제참여유형]이 [총괄 + 주관], [주관]아니고, [중소기업참여]가 [주관]인 경우 OR  [과제참여유형]이 [총괄 + 주관], [주관]이며, [중소기업참여]가 [협동/공동(참여)/위탁]인 경우
			if((info.value == "RWC03" && /RCA028|RCA017/.test(partClsf)) || (info.value == "RWC02" && /RCA018|RCA019/.test(partClsf))){
				if (divResAmt <= 300000000) {
					if(!confirm("우리 원이 주관 또는 중소기업이 주관으로 참여하고,\n총 연구비/년차가 [3억원 이하]인 경우, 중소기업 \n공동기술개발과제로 [3책5공 제외]기준이 될 수 \n있습니다. 사업공고문에 3책5공 제외로 되어 있습니까?\n\n[사업공고문에 '3책 5공 제외'가 있는 경우] '확인' 클릭,\n[사업공고문에 '3책 5공 제외'가 없는 경우] '취소' 클릭")){
						sbx_threefive.setValue( "RIC001" ); // 3책5공 해당여부 제외
					}else{
						sbx_threefive.setValue( "RIC002" ); // 3책5공 해당여부 제외
						dma_mast.set("threefive_clsf", "RIE003" );   // 제외사업기준 [중소기업 공동기술개발과제]
						sbx_threefive.setReadOnly( false );
					}
				}
			}
		}
	};

	/*
     * 총연구비 변경 이벤트
     */
	scwin.ibx_total_res_amt_onviewchange = function(info) {
