		scwin.calcTotReschPrdTerm("cls_ymd", 3);
	};	

	/*
     * 통합관리단위 변경이벤트
     */	
	scwin.cbx_intgr_mngmt_unit_onviewchange = function(info) {
		if(cbx_intgr_mngmt_unit.getValue() != ""){
			grp_intgr_mngmt_msg.show("");
			btn_limAmt.trigger("click");
		}else{
			if(WebSquare.util.parseInt(spn_req_amt_sum.getValue()) > 0){
				alert("연구시설장비비(특례) 금액이 0보다클 경우 연구장비비 통합관리단위를 선택해제할 수 없습니다.");
				this.setValue(info.value);
				return;
			}
			grp_intgr_mngmt_msg.hide();
			dlt_equipAmt.removeAll();
			dlt_equipAmt.reform();
		}
	};

	/*
     * 인쇄버튼 클릭 이벤트
     */
	scwin.btn_print_onclick = function(e) {
		var node= "bsns_req_no=" + dma_mast.get("bsns_req_no");
		com.oz.openPopup({
			ozrCode : "R026",  
			param: node
        });
	};

	/*
     * 삭제 버튼 클릭 이벤트
     */
	scwin.btn_del_onclick = function(e) {
		com.confirm("message.xom.wq.022", function(ret) {
			if(ret) {
				com.file.deleteAll(function(){
					com.execute(sbm_deleteData, {
						successCallback : function(e) {
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function(){
								if(com.getParameter("param4") == "apr"){
									com.movePageMenu("A010");
								}else{
									scwin.movePage({});
								}	
							});
						}
					});
				});
			}
		});
	};

	/*
     * 연구책임자 조회 클릭 이벤트
     */
	scwin.btn_resch_rspns_onclick = function(e) {
		// 2023.03.02. 참여연구원등록없이 임시저장가능하도록 연구책임자 직접입력하게 기능 추가
		var data={
			empno : ibx_resch_rspns_main_empno.getValue(),
			work_clsf : "HAG010",
			ref_yn : (ibx_resch_rspns_main_empno.getValue() == "") ? "Y" : "N"
		};

		com.pop.open(com.pop.TYPE.HUM, data, function(ret){			
			dma_mast.set("resch_rspns_main", ret.syspayno);
			dma_mast.set("resch_rspns_main_empno", ret.empno);	
			dma_mast.set("resch_rspns_main_nm", ret.nm);			
		});
	};
