head.script:
 
    /****************************************************
 	 * 화면 설명 : 프로젝트 예산 편성
 	 * 변경 이력 : 2023-10-24	김상미	최초 작성
 	 * 서비스아이디 : R176
 	 ****************************************************/  

 	scwin.data = {};   
 	 	
 	/**
	 * 페이지 로딩 이벤트
	 */  	       
	scwin.onpageload = function() {
		var param = com.getParameter();
		console.log(param);
		scwin.data.service_id = "R176";
		scwin.data.doc_id ="R24";	
		scwin.data.approval = false; //결재신청 여부		
		scwin.data.today = com.getServerDateTime();
		dma_static.set("grid_data_edit", "N");
		dma_static.set("apr_flag", "N");	
		// 공통코드
		var codeOptions = [ { code : "RDC", compID : "sbx_state"}, //예산상태
							{ code : "XAD", compID : "sbx_apr_state"} //결재상태
		               ];		
		com.setCmnCd(codeOptions, function() {
		});	

		
		// 첨부파일 컴포넌트 초기화
		com.file.init();
		// 첨부파일
		com.file.create(upload_A.getID(), {fileKey: scwin.data.service_id, mode: 'edit'});

		com.biz.checkRole({ serviceId : scwin.data.service_id, roleCodes : com.getLoginInfo('roleCode')}, function(e) {
			scwin.data.isRoleYn = e.isRoleYn;
		});	
		
		
		scwin.data.roleCode = com.getLoginInfo('roleCode');
		
		com.biz.disabledButtons(dma_info.get("apr_state"), dma_info.get("req_no"));		
		
		grp_buget_none.hide();
		grp_hidden.hide();
		grp_state_info.hide();
		btn_accnt_sel.hide();
		btn_reg_bbl.hide();
		
		scwin.initPage(param);	
	};
	
	scwin.onpageunload = function() {
		
	};

	
	/**
     * 화면 초기화
     */	
	scwin.initPage = function(param) {
		dma_param.setJSON(param);
		
		ibx_cryfwd_amt.setValue(0);
		ibx_incom_amt.setValue(0);
		ibx_insd_tech_sup_incom.setValue(0);
		ibx_total_amt.setValue(0);
		
		if (scwin.data.roleCode.indexOf("1001202") != -1 ) {
			btn_cfyfwd_amt.show("");
			ibx_cryfwd_amt.setDisabled(false);			
		} else {
			btn_cfyfwd_amt.hide();
		}
		
		if (dma_param.get("xml_id") == "JF32") {//과제관리>과제수행>프로젝트예산편성관리
			ibx_trans_amt.setDisabled(true);
		} else {
			if (scwin.data.roleCode.indexOf("1001257") != -1 || scwin.data.roleCode.indexOf("1001216") != -1 || 
				(scwin.data.roleCode.indexOf("1001217") != -1 && (tbx_accnt_no.getValue().substr(0,2)=="PA" ||tbx_accnt_no.getValue().substr(0,2)=="PD"))) {
				ibx_cryfwd_amt.setDisabled(false);
				ibx_trans_amt.setDisabled(false);
			} else if (scwin.data.roleCode.indexOf("1001255") != -1 && tbx_accnt_no.getValue().substr(0,2)=="PD") {
				ibx_cryfwd_amt.setDisabled(false);
				ibx_trans_amt.setDisabled(false);			
			} else if (scwin.data.roleCode.indexOf("1001257") != -1 && tbx_accnt_no.getValue().substr(0,2)=="PX") {
				ibx_cryfwd_amt.setDisabled(false);
				ibx_trans_amt.setDisabled(false);			
			} else {
				ibx_trans_amt.setDisabled(true);	
			}
		}	

		if (dma_param.get("param4") == "apr") {
			if (dma_param.get("req_no") != "") {
				dma_static.set("cud_tag", "U");
				scwin.searchAccntNo();	
			} else {
				btn_accnt_sel.show("");
				btn_accnt_sel.trigger("click");
			}
		} else {
			com.initDataMap(dma_search);	
			dma_static.set("accnt_no", dma_param.get("g_accnt_no"));
			dma_static.set("itm_solve_base", dma_param.get("g_itm_solve_base"));
			scwin.search();
		}

	
	};	
	
	/**
     * 신청번호로 계정번호 조회 실행
     */		
	scwin.searchAccntNo = function() {
		// dataMap 초기화
		com.initDataMap(dma_search);	
				
		dma_search.set("req_no", dma_param.get("req_no"));	

		com.execute(sbm_selectAccntNoByReqNo, {
			successCallback : function(e) { // 성공 콜백
				console.log(e.responseJSON);
				var resObj = e.responseJSON.elData;
				dma_static.set("accnt_no", resObj.accnt_no);
				dma_static.set("itm_solve_base", resObj.itm_solve_base);
				debugger;
				scwin.search();	
			}
		});		
	};
	
	/**
     * 조회 실행
     */		
	scwin.search = function() {
		// dataMap 초기화
		com.initDataMap(dma_search);	
				
		dma_search.set("accnt_no", dma_static.get("accnt_no"));
		dma_search.set("itm_solve_base", dma_static.get("itm_solve_base"));	

		com.execute(sbm_selectPrjAccntBugtDetail, {
			successCallback : function(e) { // 성공 콜백
				console.log("scwin.search==>" , e.responseJSON.elData);
				dma_master.setJSON(e.responseJSON.elData.masterVo);
				dma_info.setJSON(e.responseJSON.elData.infoVo);
				dlt_exec_bugt_detail.setJSON(e.responseJSON.elData.listVo, false);			
				scwin.postSearch();
			}
		});		
	};

	/**
     * 조회 실행 후처리
     */		
	scwin.postSearch = function() {
		dma_static.set("accnt_no", dma_info.get("accnt_no"));
		
		var accntNoNm = "" + dma_master.get("tax_cd_nm") + "" +  dma_master.get("accnt_no_nm");
		tbx_accnt_no_nm.setValue(accntNoNm);
		
		dma_static.set("exec_bugt_seq_max", dma_info.get("exec_bugt_seq"));
		
		if (scwin.data.roleCode.indexOf("1001217") != -1 && (tbx_accnt_no.getValue().substr(0,2)=="PA" ||tbx_accnt_no.getValue().substr(0,2)=="PD")) {
			ibx_cryfwd_amt.setDisabled(false);
			ibx_trans_amt.setDisabled(false);
		} else if (scwin.data.roleCode.indexOf("1001255") != -1 && tbx_accnt_no.getValue().substr(0,2)=="PD") {
			ibx_cryfwd_amt.setDisabled(false);
			ibx_trans_amt.setDisabled(false);			
		} else if (scwin.data.roleCode.indexOf("1001257") != -1 && tbx_accnt_no.getValue().substr(0,2)=="PX") {
			ibx_cryfwd_amt.setDisabled(false);
			ibx_trans_amt.setDisabled(false);			
		}
		
		var cryfwdAmt = parseInt(ibx_cryfwd_amt.getValue());
		var totalAmt = cryfwdAmt+ parseInt(ibx_incom_amt.getValue()) + parseInt(ibx_insd_tech_sup_incom.getValue()) - parseInt(ibx_trans_amt.getValue());
		ibx_total_amt.setValue(totalAmt);	
	
		var vDspMsg01  = "";   // 실행예산차수와 실행예산상태명 표시용 변수
		var vLoopIdx   = 0;
		var aprStateDspStr = "";		
	
		if (dma_info.get("apr_state") != "") {
			aprStateDspStr = "(" + sbx_apr_state.getText() + ")";
		}
		//첨부파일 세팅
		var vMode = "edit";
		if (dma_info.get("apr_state") >= "XAD020") {
			vMode = "view";
		}				
		var attachFileNo = dma_info.get("attach_file_no").trim();
		com.file.create(upload_A.getID(), {fileKey: (attachFileNo == "") ? scwin.data.service_id :attachFileNo, mode: vMode});
		
		if (dma_info.get("state").trim() == "" || dma_info.get("exec_bugt_seq") == "0") {
			grp_buget_info.hide();
			grp_buget_none.show("");

			for (var i = 1; i < 30; i++) {
				vLoopIdx = i + 1;
				vLoopIdx = "0" + vLoopIdx;
				vLoopIdx = vLoopIdx.substring(vLoopIdx.length - 2, 3);
				grd_exec_bugt_detail.setColumnVisible("bugt_item_amt_" + vLoopIdx, false);
			}

			grd_exec_bugt_detail.setColumnReadOnly("bugt_item_amt_01", false);

			dma_static.set("edit_exec_bugt_seq", "01");  // 에디트할 실행예산차수를 설정
			dma_static.set("cud_tag", "I");		
				
		} else {
			tbx_curr_bugt_seq.setValue("[" + dma_info.get("exec_bugt_seq") + "차]");
			tbx_apr_state_dsp.setValue(sbx_state.getText() + aprStateDspStr);

			if (dma_info.get("state").trim() == "RDC005") {
				// 4. 실행예산 상태가 "RDC005"(예실반영)인 경우...
				// 다음 차수 실행예산에 대해 입력 모드로 설정한다.
				for (var i = 0; i < 30; i++) {
					vLoopIdx = i + 1;
					vLoopIdx = "0" + vLoopIdx;
					vLoopIdx = vLoopIdx.substring(vLoopIdx.length - 2, 3);

					if (Number(vLoopIdx) <= Number(dma_static.get("exec_bugt_seq_max")) + 1) {
						grd_exec_bugt_detail.setColumnVisible("bugt_item_amt_" + vLoopIdx, true );
						if (Number(vLoopIdx) == Number(dma_static.get("exec_bugt_seq_max")) + 1) {
							grd_exec_bugt_detail.setColumnReadOnly("bugt_item_amt_" + vLoopIdx, false);
							dma_static.set("edit_exec_bugt_seq", vLoopIdx); // 에디트할 실행예산차수를 설정
						}
					} else {
						grd_exec_bugt_detail.setColumnVisible("bugt_item_amt_" + vLoopIdx, false);
					}
				}

				tbx_req_no.setValue("");
				btn_copy_budget.setDisabled(false);
				dma_static.set("cud_tag", "I");
				
			} else {
				txa_resn_contnt.setValue(dma_info.get("resn_contnt"));
				
				for (var i = 0; i < 30; i++) {
					vLoopIdx = i + 1;
					vLoopIdx = "0" + vLoopIdx;
					vLoopIdx = vLoopIdx.substring(vLoopIdx.length - 2, 3);

					if (Number(vLoopIdx) <= Number(dma_static.get("exec_bugt_seq_max"))) {
						grd_exec_bugt_detail.setColumnVisible("bugt_item_amt_" + vLoopIdx, true );
					} else {
						grd_exec_bugt_detail.setColumnVisible("bugt_item_amt_" + vLoopIdx, false);
					}
				}

				// 2. 실행예산 상태가 "RDC001"(저장(결재전)), "RDC003"(반려) 인 경우...
				// 실행예산차수 MAX 차수에 대해 수정 모드로 설정한다.
				if (dma_info.get("apr_state") <= "XAD015") {
					vLoopIdx = "0" + dma_static.get("exec_bugt_seq_max");
					vLoopIdx = vLoopIdx.substring(vLoopIdx.length - 2, 3);
					grd_exec_bugt_detail.setColumnReadOnly("bugt_item_amt_" + vLoopIdx, false);
					dma_static.set("edit_exec_bugt_seq", vLoopIdx); // 에디트할 실행예산차수를 설정
					dma_static.set("cud_tag", "U");
				} else if (dma_info.get("apr_state") >= "XAD020") {
					vLoopIdx = "0" + dma_static.get("exec_bugt_seq_max");
					vLoopIdx = vLoopIdx.substring(vLoopIdx.length - 2, 3);
					grd_exec_bugt_detail.setColumnReadOnly("bugt_item_amt_" + vLoopIdx, true);
					dma_static.set("edit_exec_bugt_seq", vLoopIdx); // 에디트할 실행예산차수를 설정
					dma_static.set("cud_tag", "R");
					
					if (dma_param.get("xml_id") == "RF33") { //연구관리>사업예산
						com.biz.checkRole({ serviceId : scwin.data.service_id, roleCodes : com.getLoginInfo('roleCode')}, function(e) {
							scwin.data.isRoleYn = e.isRoleYn;
							if (dma_info.get("apr_state") == "XAD100" && scwin.data.isRoleYn) {
								btn_reg_bbl.show("");
							}								
						});	
					}
				}
			}//예실반영이 아닐 경우			
		}//실행예산이 편성된 자료가 있을 경우
		
		var  stPrdTerm= tbx_prd_term.getValue();
		stPrdTerm = stPrdTerm.substring(0,4);
		
		var inFrmAmt = 0;
		var incomAmt = "";
		
		if (Number(stPrdTerm) == 2020) {
			incomAmt = ibx_total_amt.getValue();
		} else {
			incomAmt =  ibx_incom_amt.getValue();
		}
				
		if (cbx_frm_rt.getValue() != "FRT030") {
			if (cbx_frm_rt.getValue() == "FRT010") {
		    	inFrmAmt = Number(incomAmt) * 0.1;
			} else if (cbx_frm_rt.getValue() == "FRT020") {
				inFrmAmt = Number(incomAmt) * 0.05;
			}
			ibx_frm_amt.setValue(((Math.round(inFrmAmt / 100)) * 100));
		} else {
			inFrmAmt = Number(incomAmt);
			ibx_frm_amt.setValue(inFrmAmt);
		}
		
		if (dma_info.get("req_no") != "") {
			dma_static.set("apr_state", dma_info.get("apr_state"));
			dma_static.set("req_no", dma_info.get("req_no"));
		}
		
		if (parseInt(dma_static.get("exec_bugt_seq_max")) > 0){
			// 실행예산차수별 실행예산 내역 조회							
			scwin.searchAccntBugtData();
		} else {
			dma_static.set("exec_bugt_seq_max", "0");
			dma_static.set("grid_data_edit", "N");	
			
			scwin.compControll();		
		}
	};
	
	/**
	 * 실행예산차수별 실행예산 내역 조회 클릭
	 */		
	scwin.searchAccntBugtData = function() {
		dma_search.set("exec_bugt_seq_max", dma_static.get("exec_bugt_seq_max"));
		
		com.execute(sbm_selectResPrjAccntBugtList, {
			successCallback : function(e) { // 성공 콜백
				console.log(e.responseJSON);
				var resObj = e.responseJSON.resExcPrjBdgetVoList;
				for (var i = 0; i < dma_static.get("exec_bugt_seq_max"); i++) {	
					var itemObj = resObj[i]["detailList"];
					for (var j = 0; j < itemObj.length; j++) {
						var getAmt = itemObj[j]["amt"]; //금액
						var getGridColumnId = itemObj[j]["grid_column_id"]; //그리드 컬럼 ID
						if (getGridColumnId != "XX") {
							dlt_exec_bugt_detail.setCellData(j, "bugt_item_amt_" + getGridColumnId, getAmt, false);
						}
					}	
				}	
				dma_static.set("grid_data_edit", "N");	
				
				scwin.compControll();
					
				if (dma_static.get("apr_flag") == "Y") {
					scwin.data.approval = true;
					dma_save.set("approval", "true");
					scwin.saveAppr();
				}
			}
		});		
	};

	/**
	 * 목록 버튼 클릭
	 */			
	scwin.btn_list_onclick = function(e) {
		scwin.backToList();
	};
	
	/**
	 * 목록 페이지로 이동
	 */	
	scwin.backToList = function() {
		var data = dma_param.getJSON();
		data.his_back = "Y";
		data.yr = dma_param.get("bugt_yr");
		com.moveList('JF32', data);		
	};

	/**
	 * 예실반영버튼 클릭 이벤트
	 */	
	scwin.btn_reg_bbl_onclick = function(e) {
		var execBugtSeqMax = ibx_exec_bugt_seq_max.getValue();

		var msg = "계정번호 [" + dma_info.get("accnt_no") + "]의 " + Number(execBugtSeqMax.trim()) + "차 실행예산 내역을 예실반영 하시겠습니까?";
		if ( !confirm(msg) ) return;
		
		dma_save.set("accnt_no", dma_info.get("accnt_no"));
		dma_save.set("syspayno", com.getLoginInfo("sn"));
		dma_save.set("prcs_clsf", "PX");		
		com.execute(sbm_saveBugtApplyPrcs, {
			successCallback : function(e) {
				console.log(e.responseJSON);
				scwin.search();
			}
		});		
	};

	/**
	 * 인쇄버튼 클릭 이벤트
	 */			
	scwin.btn_print_onclick = function(e) {
		com.oz.openPopup( {
		    formAlias : dma_info.get("req_no").substring(0,3),
		    reqNo : dma_info.get("req_no"),
		    snapFrame : grp_base_info.getID()
		});			
	};
	
	/**
	 * 결재신청버튼 클릭 이벤트
	 */		
	scwin.btn_apr_onclick = function(e) {
		scwin.data.approval = true;	
		dma_static.set("apr_flag", "Y");
		dma_static.set("cud_tag", "U");
		dma_save.set("approval", "true");	
		
		scwin.saveApr();		
	};
	
	/**
	 * 결재신청 실행
	 */	
	scwin.saveApr = function() {
		dma_static.set("apr_flag", "N");

		if (tbx_req_no.getValue().trim() == "") {
			alert("결재신청을 위한 신청번호가 존재하지 않거나, 조회되지 않았습니다.\n실행예산 편성/변경 내역을 다시 조회하신 후, 결재신청하세요.");
			return
		}

		if (!confirm("결재신청 하시겠습니까?")) {
			return;
		}
		
		dma_save.set("accnt_no", tbx_accnt_no.getValue() );  // 계정번호
		dma_save.set("exec_bugt_seq", Number(dma_static.get("edit_exec_bugt_seq")));  // 실행예산차수
		dma_save.set("state", "RDC002");  // 신청상태([RDC002]'결재중')
		dma_save.set("accnt_state", "RAK031" );  // 계정상태([RAK031]'실행예산변경중')
		dma_save.set("updt_syspayno"  , com.getLoginInfo("sn"));
		dma_save.set("info_cd", tbx_accnt_no_nm.getValue()); //infoCd
		
		var accntRspns = ibx_accnt_rspns.getValue();
		var accnt_rspns_str2 = tbx_accnt_rspns_nm.getValue().trim() + " (" + accntRspns.substring(2,8) + ")";
		
		// 실행예산 편성/변경 내역(HEADER)
		dma_save.set("resch_prd_term", tbx_prd_term.getValue().trim());  // 과제기간(시작일~종료일)
		dma_save.set("accnt_state_nm", tbx_accnt_state_nm.getValue().trim());  // 계정상태
		dma_save.set("accnt_no_nm", tbx_accnt_no_nm.getValue().trim());  // 과제명
		dma_save.set("accnt_rspns_str", accnt_rspns_str2  );  // 계정책임자
		dma_save.set("posi_nm", tbx_accnt_rspns_posi.getValue());  // 직급
		dma_save.set("dept_nm", tbx_accnt_rspns_dept.getValue());  // 소속
		dma_save.set("resn_contnt", txa_resn_contnt.getValue().trim());  // 변경사유
		dma_save.set("apr_exec_bugt_seq", Number(dma_static.get("edit_exec_bugt_seq")) + "차"   );  // 해당 실행예산차수
		dma_save.set("req_no"          , tbx_req_no.getValue());  // 결재신청번호
		dma_save.set("cryfwd_amt"      , ibx_cryfwd_amt.getValue());
		dma_save.set("incom_amt"       , ibx_incom_amt.getValue());
		dma_save.set("insd_tech_sup_incom", ibx_insd_tech_sup_incom.getValue());
		dma_save.set("trans_amt" , ibx_trans_amt.getValue());
		dma_save.set("totl_amt"        , ibx_total_amt.getValue());		
		dma_save.set("bugt_ctrl_psn"   , ibx_bugt_ctrl_psn.getValue());	
		dma_save.set("accnt_rspns"     , ibx_accnt_rspns.getValue());	
		dma_save.set("attach_file_no", dma_info.get("attach_file_no"));
		
		var sAmtSumColId      = 0 ;  // 해당 실행예산차수
		var getEnfrcCauseAmt  = "";  // 집행금액(원인금액포함)
		var getAmt     = "";  // 실행예산금액
		var getPreAmt  = "";  // 실행예산금액
		var nAmtSum    = 0 ;  // (누적)실행예산금액
		var nPreAmtSum = 0 ;  // (누적)실행예산금액
		var nEnfrcCauseAmtSum = 0;

		var sPreAmtSumColId    = 0 ;
		var tmpAmtColId = 0;
		var sAmtSumColId = "bugt_item_amt_" + dma_static.get("edit_exec_bugt_seq");  // 해당 실행예산차수

		if (Number(dma_static.get("edit_exec_bugt_seq")) > 1) {
			tmpAmtColId = Number(dma_static.get("edit_exec_bugt_seq")) - 1;
			if (tmpAmtColId > 9) {
				sPreAmtSumColId = "bugt_item_amt_" + tmpAmtColId;  // 해당 실행예산차수
			} else {
				sPreAmtSumColId = "bugt_item_amt_0" + tmpAmtColId;  // 해당 실행예산차수
			}
		} else {
			sPreAmtSumColId = "bugt_item_amt_0";
		}		
	
		dlt_apr_bdget_detail.setJSON(dlt_exec_bugt_detail.getAllJSON(), false);
		
		for (var i = 0; i < dlt_apr_bdget_detail.getRowCount(); i++) {
			getEnfrcCauseAmt = Number(dlt_apr_bdget_detail.getCellData(i, "enfrc_cause_amt"));  // 집행금액(원인금액포함)
			getAmt = Number(dlt_apr_bdget_detail.getCellData( i , sAmtSumColId ));  // 실행예산금액(해당차수)
			if (sPreAmtSumColId == "bugt_item_amt_0") {
				getPreAmt = 0;
			} else {
				getPreAmt = Number(dlt_apr_bdget_detail.getCellData(i, sPreAmtSumColId));  // 실행예산금액(해당차수)
			}

			nAmtSum = nAmtSum + getAmt;  // (누적)실행예산금액
			nPreAmtSum = nPreAmtSum  + getPreAmt;  // (누적)실행예산금액
			nEnfrcCauseAmtSum = nEnfrcCauseAmtSum + Number(getEnfrcCauseAmt);  // (누적)집행금액(원인금액포함)

			dlt_apr_bdget_detail.setCellData(i, "amt", getAmt);
			dlt_apr_bdget_detail.setCellData(i, "pre_amt", getPreAmt);
		}			

		dma_save.set("tot_enfrc_cause_amt" , nEnfrcCauseAmtSum);  // (누적)집행금액(원인금액포함)
		dma_save.set("tot_amt" , nAmtSum          );  // (누적)실행예산금액
		dma_save.set("tot_pre_amt", nPreAmtSum   );  // (누적)실행예산금액

		com.execute(sbm_saveApr, {
			successCallback : function(e) {
				console.log(e.responseJSON);
				var resObj = e.responseJSON.elData;		
				com.movePageMenu("A001", {"reqNo" :  dma_info.get("req_no")});		
			},
			exceptionCallback : function(e) {
				alert("과제상태 및 신청상태 수정에 실패하였습니다.");
				return false;
			}
		});			
	}

