	/**
	 * 이월금변경버튼 클릭 이벤트
	 */		
	scwin.btn_cfyfwd_amt_onclick = function(e) {
		if (dma_static.get("exec_bugt_seq_max") == "") {
			dma_static.set("exec_bugt_seq_max", 0);
		}
		
		if (dma_static.get("exec_bugt_seq_max") == 0) {
			alert("1차수 이상 예산편성 진행 후 변경이 가능합니다.");
			return false;
		}
		
		if (ibx_cryfwd_amt.getValue() == "") {
			alert("변경하실 이월금액을 입력해주세요.");
			return false;
		} else {
			dma_save.set("cryfwd_amt", ibx_cryfwd_amt.getValue());  // 실행예산차수
			dma_save.set("accnt_no", tbx_accnt_no.getValue() );  // 계정번호
			dma_save.set("exec_bugt_seq", dma_static.get("exec_bugt_seq_max"));  // 계정번호
			dma_save.set("updt_syspayno", com.getLoginInfo("sn"));
			scwin.updateCryfwdAmt();
		}	
	}; 

