				var resObj = e.responseJSON.elData;	
				alert("이월금액이 변경되었습니다.");
			}
		});	
	};
		
	/**
	 * 이월금액 변경 이벤트
	 */	
	scwin.ibx_cryfwd_amt_onchange = function(info) {
		scwin.setTotalAmt();
	};	

	/**
	 * 계정대체(차감) 변경 이벤트
	 */		
	scwin.ibx_trans_amt_onchange = function(info) {
		scwin.setTotalAmt();
	};	
	
	/**
	 * 총액 변경처리
	 */		
	scwin.setTotalAmt = function() {
		if (ibx_cryfwd_amt.getValue() == "") {
			ibx_cryfwd_amt.setValue(0);
		}
		if (ibx_incom_amt.getValue() == "") {
			ibx_incom_amt.setValue(0);
		}
		if (ibx_insd_tech_sup_incom.getValue() == "") {
			ibx_insd_tech_sup_incom.setValue(0);
		}
		if (ibx_trans_amt.getValue() == "") {
			ibx_trans_amt.setValue(0);
		}

		var cryfwdAmt = parseInt(ibx_cryfwd_amt.getValue());
		var incomAmt = parseInt(ibx_incom_amt.getValue());
		var insdTechSupIncom = parseInt(ibx_insd_tech_sup_incom.getValue());
		var transAmt = parseInt(ibx_trans_amt.getValue());  // 20160118 추가 계정대체(차감)

		ibx_total_amt.setValue(cryfwdAmt+incomAmt+insdTechSupIncom - transAmt);		
	};	
		
	/**
	 * 이전차수복사버튼 클릭 이벤트
	 */	
	scwin.btn_copy_budget_onclick = function(e) {
		// 실행예산(1차)에 대한 내역만 존재할 경우...
		if (Number(dma_static.get("edit_exec_bugt_seq")) == 1) {
			alert("이전 차수의 실행예산 내역이 없습니다.");
			return;
		}

		if (sbx_state.getValue() != "RDC005") {
			alert("이전 차수의 실행예산이 예실반영되지 않아서 [이전차수복사]기능을 사용할 수 없습니다.");
			return;
		}
		var getBugtItemAmt   = 0  ;  // n-1차수 실행예산
		var vBugtItemAmtN = Number(dma_static.get("edit_exec_bugt_seq")) - 1; // n-1차수 ID
		vBugtItemAmtN = "0" + vBugtItemAmtN;
		vBugtItemAmtN = vBugtItemAmtN.substring(vBugtItemAmtN.length - 2, 3);

		for (var i = 0; i < dlt_exec_bugt_detail.getRowCount(); i++) {
		  getBugtItemAmt = dlt_exec_bugt_detail.getCellData(i, "bugt_item_amt_" + vBugtItemAmtN , false);  // n-1차수 실행예산
		  dlt_exec_bugt_detail.setCellData(i, "bugt_item_amt_" + dma_static.get("edit_exec_bugt_seq"), getBugtItemAmt, false);
		}

		// 이전차수복사 처리후에 모든 행의 상태(Status)를 "U"(Update) 상태로 바꾼다.
		dlt_exec_bugt_detail.modifyAllStatus("U");
		tbx_req_no.setValue("");
		btn_save.setDisabled(false);		
	};

	/**
	 * 삭제버튼 클릭 이벤트
	 */		
	scwin.btn_del_onclick = function(e) {
		var cryfwdAmt = parseInt(ibx_cryfwd_amt.getValue());
		if (cryfwdAmt > 0){
			alert("이월금액이 등록되어있어 삭제 할 수 없습니다. \n문의:기업성장지원실");
		}else {
			if ( !confirm("삭제하시겠습니까?") ){
				return;
			}  

			dma_save.set("accnt_no", tbx_accnt_no.getValue());
			dma_save.set("exec_bugt_seq", dma_static.get("edit_exec_bugt_seq"));
			dma_save.set("accnt_state", "RAK041"); // 계정상태([RAK031]'실행예산변경중')
			
			scwin.remove();
		}		
	};

	/**
	 * 삭제실행
	 */		
	scwin.remove = function() {
		com.file.deleteAll(function() {		
			com.execute(sbm_deleteApr, {
				successCallback : function(e) {
					console.log(e.responseJSON);
					var resObj = e.responseJSON.elData;	
					com.alert("실행예산 삭제가 완료되었습니다.",function() {
						scwin.search();
					});	
				}
			});	
		});		
	};

	/**
	 * 저장버튼 클릭 이벤트
	 */		
	scwin.btn_save_onclick = function(e) {
		dma_save.set("approval", "false");	
		scwin.data.approval = false;
		
		if (!scwin.preSave()) {
			return;
		}
		if (dma_static.get("apr_flag") != "Y") {
			if (!confirm("저장하시겠습니까?"))  return;
		}	
		scwin.save();
	};	

	/**
	 * 저장전 확인
	 */			
	scwin.preSave = function() {
		if (ibx_cryfwd_amt.getValue() == "") {
			alert("이월금액을 입력하세요.");
			ibx_cryfwd_amt.focus();
			return false;
		}

		if (ibx_incom_amt.getValue() == '') {
			alert("당해연도 수입금액을 입력하세요.");
			ibx_incom_amt.focus();
			return false;
		}

		var totalAmt = Number(ibx_total_amt.getValue());
		var bugtAmtSum = 0;
		
		var itmClsf       = "";              // 비목구분
		var bugtItem       = "";             // 세부비목
		
		var nFae01Sum     = 0;
		var totAmt        = 0;              
		var frmAmt = ibx_frm_amt.getValue();
		var chkRt = cbx_frm_rt.getText();
		 
		var incomAmt = ibx_incom_amt.getValue();
		
		for (var i=0; i<dlt_exec_bugt_detail.getRowCount(); i++) {
			bugtAmtSum = bugtAmtSum + Number(dlt_exec_bugt_detail.getCellData(i , "bugt_item_amt_" + dma_static.get("edit_exec_bugt_seq")));
			
			itmClsf    = dlt_exec_bugt_detail.getCellData(i, "itm_clsf");  // 대비목(명)
			bugtItem   = dlt_exec_bugt_detail.getCellData(i, "bugt_item_nm");  // 세부비목
			totAmt     = dlt_exec_bugt_detail.getCellData(i , "bugt_item_amt_" + dma_static.get("edit_exec_bugt_seq")) ; 
			
			if (bugtItem.trim() == "내부인건비(Ⅰ)") {
				nFae01Sum = nFae01Sum + Number(totAmt);
			}  
		}

		
		if (scwin.data.roleCode.indexOf("1001202") == -1) {
			if (cbx_frm_rt.getValue() != "FRT030") { // 내부인건비(1) 최소 편성비율이 해당없음이 아닐경우
				if (nFae01Sum < frmAmt) { // 내부인건비(1)합계가 기준편성보다 작을경우
					if (incomAmt > 10000000) {  // 총액이 1천만원 미만인 경우는 제외
						alert("실행예산 [내부인건비1] 편성금액은 \n최소 편성비율 " + chkRt + "을 준수해주세요.");
						return false;
					}
				}
			}
		}

		if (tbx_accnt_no.getValue() != "PX210001" && tbx_accnt_no.getValue() != "PES22010") {
			if (totalAmt < bugtAmtSum) {
				alert("실행예산 편성금액은 [이월금액 + 당해연도 수입금액]을 초과할 수 없습니다.");
				return false;
			}
		}

		if (tbx_accnt_no.getValue().substring(0,2) == "PD"  &&  tbx_accnt_no.getValue() != "PD221004" && tbx_accnt_no.getValue() !="PD221002" ) {
			if ( ibx_usg_clsf.getValue() == 'FEF001' && totalAmt * 0.4 < bugtAmtSum) {
				alert("시설활용 PD계정은 40%만 집행편성 가능합니다. 추가 문의는 연구장비운영실로 부탁드립니다.");
				return false;
			} else if ( ibx_usg_clsf.getValue() == 'FEF002' && totalAmt * 1.0 < bugtAmtSum) {
				alert("장비활용 PD계정은 100%만 집행편성 가능합니다. 추가 문의는 연구장비운영실로 부탁드립니다.");
				return false;
			} else if ( ibx_usg_clsf.getValue() == 'FEF003' && totalAmt * 0.4 < bugtAmtSum) {
				alert("교육및인력활용 PD계정은 40%만 집행편성 가능합니다. 추가 문의는 연구장비운영실로 부탁드립니다.");
				return false;
			} else if ( ibx_usg_clsf.getValue() == 'FEF004' && totalAmt * 1.0 < bugtAmtSum) {
				alert("기타 PD계정은 100%만 집행편성 가능합니다. 추가 문의는 연구장비운영실로 부탁드립니다.");
				return false;
			} else if ( ibx_usg_clsf.getValue() == 'FEF005' && totalAmt * 0.4 < bugtAmtSum) {
				alert("기술자문(일반) PD계정은 40%만 집행편성 가능합니다. 추가 문의는 연구장비운영실로 부탁드립니다.");
				return false;
			} else if ( ibx_usg_clsf.getValue() == 'FEF006' && totalAmt * 0.6 < bugtAmtSum) {
				alert("기술자문(유상) PD계정은 60%만 집행편성 가능합니다. 추가 문의는 연구장비운영실로 부탁드립니다.");
				return false;
			}
		}
		return true;	
	};

	/**
	 * 저장실행
	 */		
	scwin.save = function() {
		dma_save.set("accnt_no", tbx_accnt_no.getValue());
		dma_save.set("exec_bugt_seq", dma_static.get("edit_exec_bugt_seq"));
		dma_save.set("req_no", dma_info.get("req_no"));
		dma_save.set("resn_contnt", txa_resn_contnt.getValue());
		dma_save.set("req_psn",  com.getLoginInfo("sn"));
		dma_save.set("req_ymd",  scwin.data.today);
		dma_save.set("state",    "RDC001");
		dma_save.set("cryfwd_amt", ibx_cryfwd_amt.getValue());
		dma_save.set("incom_amt", ibx_incom_amt.getValue());
		dma_save.set("insd_tech_sup_incom", ibx_insd_tech_sup_incom.getValue());
		dma_save.set("trans_amt", ibx_trans_amt.getValue()); // 계정대체(차감) 20160118 추가
		dma_save.set("syspayno", com.getLoginInfo("sn"));
		dma_save.set("crud_tag", dma_static.get("cud_tag"));  // Insert or Update Tag
		dma_save.set("service_id", scwin.data.service_id);
		dma_save.set("doc_id", scwin.data.doc_id);
		
		debugger;
		console.log("scwin.save ==> " ,dma_save.getJSON());
		var getAmt = "";  // 실행예산 금액 보관 변수
		var bugtItemNm = "";		

		for (var i = 0; i < dlt_exec_bugt_detail.getRowCount(); i++ ) {
			bugtItemNm = dlt_exec_bugt_detail.getCellData(i , "bugt_item_nm", false); 
			getAmt = dlt_exec_bugt_detail.getCellData(i , "bugt_item_amt_" + dma_static.get("edit_exec_bugt_seq"), false);  // 실행예산 금액
			dlt_exec_bugt_detail.setCellData(i, "amt", getAmt);
			dlt_exec_bugt_detail.setCellData(i, "exec_bugt_seq", Number(dma_static.get("edit_exec_bugt_seq")));
			dlt_exec_bugt_detail.setCellData(i, "accnt_no", tbx_accnt_no.getValue());

			if (dma_static.get("cud_tag") == "I") {
				dlt_exec_bugt_detail.setCellData(i, "cud_type", "C" );
			} else {
				//내부인건비3가 추가됨으로써 수정이 안되어서 XX일 경우 추가
				dlt_exec_bugt_detail.setCellData(i, "cud_type", "U" );
			}
		}		
		com.file.transport(function(attachObj) {	
			dma_save.set("attach_file_no", attachObj.upload_A);
			
			var attaches = [];
			if (attachObj.upload_A !="") {
				attaches.push(attachObj.upload_A);
			}
			dma_save.set("validAttachNo", attaches.join(","));				
			com.execute(sbm_saveData, {
				successCallback : function(e) {
					console.log(e.responseJSON);
					var resObj = e.responseJSON.elData;		
					if (dma_static.get("apr_flag") != "Y") {
						alert("저장하였습니다.");
					}
					scwin.search();
				},
				exceptionCallback : function(e) {
					alert("저장에 실패하였습니다. 다시 시도 바랍니다.");
					return false;
				} 
			});	
		});
	};


	/**
	 * 화면 콤포넌트 컨트롤
	 */	
	scwin.compControll = function() {
	    com.biz.disabledButtons(dma_static.get("apr_state"), dma_static.get("req_no"));

		if (dma_info.get("req_no") !="" && dma_info.get("apr_state") >= "XAD020") {
			var disableArrary = ["txa_resn_contnt","btn_copy_budget","cbx_frm_rt","btn_cfyfwd_amt","btn_reg_bbl"];
			scwin.setDisable(disableArrary, true);
			grd_exec_bugt_detail.setGridReadOnly(true);	
		} else {
			if (dma_info.get("req_no") !="" && dma_info.get("apr_state") !="XAD100" && dma_info.get("req_psn") != "" && dma_info.get("req_psn") != com.getLoginInfo("sn")) {
				btn_del.setDisabled(true);
				btn_save.setDisabled(true);
				btn_apr.setDisabled(true);
			}
		}
	};
	
	/**
	* 컴포넌트 disable true/false 처리
	*/	
	scwin.setDisable = function(arr, flag) {
		for (var i = 0; i < arr.length; i++) {
			$p.getComponentById(arr[i]).setDisabled(flag);
		}
	};

