		com.pop.open(com.pop.TYPE.VIEW_APR, data);	
	};	
	
	/**
	 * 계정기간 display format 날짜형식으로 지정
	 */			
	scwin.dateFormat = function(val) {
		var res = val;
		var dateArray = [];
		if (val != null && val.length >= 17) {
			dateArray = val.split("~");
			res = com.formatDate(dateArray[0].trim(), "dash") + " ~ " + com.formatDate(dateArray[1].trim(), "dash");
		}
		return res;
	};
