head.script:
    /****************************************************
 	 * 화면 설명 : 화학물질폐기신청
 	 * 변경 이력 : 2023-09-06 	김상미	최초 작성
 	 * 서비스아이디 : D305
 	 ****************************************************/ 
	scwin.data={};
	
 	/**
	 * 페이지 로딩 이벤트
	 */  	        
	scwin.onpageload = function() {
		var param = com.getParameter();
		console.log(param);
		
		//화면에 노출하지 않을 컴포넌트 숨김 처리
		ibx_mobile.hide();
		ibx_main_rspns_mobile.hide();
		ibx_sub_rspns_mobile.hide();
		ibx_barcode_scan_value.hide();
		grp_req_info.hide();
		grp_chmstry.hide();
		grp_hidden.hide();		
		
		//규제정보 disable 처리
		cbx_cmi.setDisabled(true);
		cbx_is_law.setDisabled(true);
		cbx_smm.setDisabled(true);			
		sbx_cl.setDisabled(true);
		sbx_cl_detail.setDisabled(true);
		sbx_cl_detail2.setDisabled(true);
		
		scwin.data.service_id = "D305";
		scwin.data.doc_id="D35";
		
		scwin.data.checkFlag = "";
		scwin.data.bar_code = [];
		scwin.data.reading = false;
		scwin.data.busFlag = "";
		scwin.data.barcodeChk = "";
				
		com.setValidGroup(grp_base_info, [
			  {id:"ica_req_ymd", mandatory :true}
			, {id:"ibx_lab_nm", mandatory :true}
			, {id:"sbx_exhst", mandatory :true}
			, {id:"cbx_wste_clsf", mandatory :true}								
		]);			
		
		document.addEventListener("keypress", function(e) {
		  //보통 바코드 리더기가 읽기가 끝나면 Enter 를 입력하므로...
		  console.log("키누름 시작");
		  console.log(e.keyCode);
		  console.log(e.key);
		  console.log(barCode.length);
		  console.log(e.keyCode == 13);				  
		  if (e.keyCode == 13) {
		    if (scwin.data.bar_code.length > 10) {
		      console.log("코드 시작");
		      ibx_barcode_scan_value.focus();
		      ibx_barcode_scan_value.setValue(scwin.data.bar_code.join(""));
		      console.log(scwin.data.bar_code);
		      /// code ready to use
		      scwin.data.bar_code = [];
		    }
		  } else {
		    scwin.data.bar_code.push(e.key); //while this is not an 'enter' it stores the every key
		  }
		});		
		scwin.initPage(param);
	};
	
	scwin.onpageunload = function() {
		
	};
		
	/**
	 * 화면 초기 세팅
	 */		
	scwin.initPage = function(param) {
		com.biz.disabledButtons(dma_info.get("apr_state"), dma_info.get("dsuse_req_no"));		
	
		//권한 확인
		com.biz.checkRole({ serviceId : scwin.data.service_id, roleCodes : com.getLoginInfo('roleCode')}, function(e) {
			scwin.data.isRoleYn = e.isRoleYn;
		});		
		scwin.data.roleCode = com.getLoginInfo('roleCode');

		cbx_wste_clsf.setSelectedIndex(1);
		scwin.data.today = com.getServerDateTime();	
		ica_req_ymd.setValue(scwin.data.today);
				
		dma_param.setJSON(param);
		
		if (dma_param.get("param4").trim() =="") {
			scwin.data.busFlag = "W"; //업무
		} else {
   			if ( dma_param.get("apr_state").trim() == "" ) {
   				scwin.data.busFlag = "C";	//결재양식함에서 호출한 경우
   			} else if ( dma_param.get("apr_state").trim() == "XAD070" ) {
   				scwin.data.busFlag = "S";	//결재양식함에서 호출한 경우
   			} else {
   				if ( dma_param.get("req_no").trim() != "" ) {
  					scwin.data.busFlag = "G";	//작성중문서함에서 호출한 경우
   				}
   			}		
		}
	
		if (dma_param.get("param4") == "apr") {
			dma_param.set("dsuse_req_no", dma_param.get("req_no"));
			dma_param.set("apr_state",dma_param.get("apr_state"));		
		}
			
		// 공통코드
		var codeOptions = [	{ code : "PFA", compID : "grd_deuse_req_info:unit_clsf"} //단위
		                  , { code : "SFE", compID : "grd_deuse_req_info:wste_knd"} //폐기물종류
		                  , { code : "SFG", compID : "sbx_cl"} //위험물안전관리법 1번째 selectBox
		               ];	
		               	
		com.setCmnCd(codeOptions, function() {
			if (dma_param.get("dsuse_req_no")!="") {
				//조회 파라메터용 dma세팅 
				dma_search.set("dsuse_req_no", dma_param.get("dsuse_req_no"));
				tbx_req_no.setValue(dma_param.get("dsuse_req_no"));
				scwin.search();
			} else {
				dma_param.set("sn", com.getLoginInfo("sn"));
				dma_param.set("cn", com.getLoginInfo("cn"));
				dma_param.set("uid", com.getLoginInfo("uid"));
				dma_param.set("roleCode", scwin.data.roleCode);
				dma_param.set("empno", com.getLoginInfo("empno"));
				dma_param.set("celphonenumber", com.getLoginInfo("phone"));
	
				ibx_req_emp_no.setValue(com.getLoginInfo("empno"));
				ibx_req_nm.setValue(com.getLoginInfo("cn"));
				ibx_hdn_req_syspayno.setValue(com.getLoginInfo("sn"));
				ibx_hdn_dept_cd.setValue(com.getLoginInfo("deptCode"));
				ibx_dept_name.setValue(com.getLoginInfo("deptName"));
				ibx_hdn_dept_new_ymd.setValue(com.getLoginInfo("deptNewYmd"));
				ibx_mobile.setValue(com.getLoginInfo("phone"));
				
				com.alert("화학제품 폐기 신청은 연구실 단위로만 가능합니다.", function() {
					grd_product.setColumnVisible("companion_rs_btn", false); //폐기신청 최초 조회 시 반려건이 없으므로 반려사유 컬럼 히든처리
					ibx_req_nm.setValue(com.getLoginInfo('cn'));
					ibx_mobile.setValue(com.getLoginInfo('phone'));
					
					dma_search.set("space_cd", dma_param.get("space_cd"));	
					
					if (dma_param.get("useReqNoArrInfo")!="") {
						var useReqNoArr = dma_param.get("useReqNoArrInfo").split(",");
						var prductCdArr = dma_param.get("prductCdArrInfo").split(",");
						var reqNoArr = dma_param.get("reqNoArrInfo").split(",");
						
						useReqNoArr.forEach(function(item, index) {
							dlt_use_req_no.insertRow(index);
							dlt_use_req_no.setCellData(index, "use_req_no", item);
						});	
	
						prductCdArr.forEach(function(item, i) {
							dlt_product_cd.setCellData(i, "prduct_cd", item);
						});	
						reqNoArr.forEach(function(item, i) {
							dlt_req_no.setCellData(i, "req_no", item);
						});						
						
						dma_search.set("prduct_cd_chk", 'Y');
						dma_search.set("req_no_chk", 'Y');			
						dma_search.set("space_cd", ibx_hdn_space_cd.getValue());					
					}							
								
					//연구실정보 조회
					scwin.searchLab(true);				
				});
			}			
		});
	};
			
	/**
	 * 조회 실행
	 */	
	scwin.search = function() {
		com.execute(sbm_selectSafCheProductDsuseInfo, {
			successCallback : function(e) { // 성공 콜백
				console.log(e.responseJSON);
				var companion_chk = false;
				cbx_wste_clsf.checkAll( false );
				if (dma_info.get("wste_clsf").indexOf("SFD010") != -1) {
				 	cbx_wste_clsf.setValue("SFD010");
				}
				if (dma_info.get("wste_clsf").indexOf("SFD020") != -1) {
					cbx_wste_clsf.setValue("SFD020");
				}

				ica_req_ymd.setValue( dma_info.get("reqst_ymd"));
				
				rad_gubun.setSelectedIndex(0);
				
				grd_product.setFocusedCell( 0 , "use_req_no" );
				
				for (var i = 0 ; i < dlt_product.getRowCount(); i++) {
					if (dlt_product.getCellData(i, "disposal_state" ) == "SFI001" || dlt_product.getCellData(i, "disposal_state" ) == "SFI005") {
						grd_product.setDisabled( "cell" , i , "grid_chk" , false );
					} else {
						grd_product.setDisabled( "cell" , i , "grid_chk" , true );
					}
					
					if (dlt_product.getCellData(i, "companion_rs" ).trim() == "" || dlt_product.getCellData(i, "companion_rs" ).trim() == null) {
						//반려 사유가 공백또는 널값일 경우 아무것도 진행하지 않는다.
					} else {
						companion_chk = true;
					}
				}
				
				// 반려건이 있으면 반려건과 미반려건을 구분해주고 폐기 재신청 버튼을 보여준다.
				if (companion_chk == true) {
					for (var i = 0 ; i < dlt_product.getRowCount(); i++) {
						if (dlt_product.getCellData(i, "companion_rs" ) == "" || dlt_product.getCellData(i, "companion_rs" ) == null) {
							dlt_product.setCellData( i , "companion_rs_btn" , "미반려건");
							grd_product.setCellDisabled( i , "companion_rs_btn" , true );					
						}	
					}
					
					grd_product.setColumnVisible( "companion_rs_btn" , true );					
					// 반려건이 없으면 컬럼과 폐기 재신청 버튼을 숨긴다.
				} else {
					grd_product.setColumnVisible( "companion_rs_btn" , false );
				}	
				scwin.searchProdChmstry();					
				scwin.compControll();
			}
		});
	};

	/**
	 * 연구실 조회 실행
	 */	
	scwin.searchLab = function(flag) {
		com.execute(sbm_selectLabInfo, {
			successCallback : function(e) {	
					
				//제품이 이미 선택되어 있다면 본인 연구실 제품여부 판단
				for (var i = 0 ; i < dlt_product.getRowCount() ; i++) {
					if (dlt_product.getCellData( i , "space_cd" ) != "") {
						if (dma_lab_info.get("space_cd") != dlt_product.getCellData( i , "space_cd" )) {
							com.alert("연구실에 속해 있지 않은 제품이 등록되어 있습니다. 제품에 맞는 연구실을 선택해주시기 바랍니다.");
							return;
						}
					}
				}
			
				ibx_lab_nm.setValue(dma_lab_info.get("lab_nm"));
				ibx_cnter_nm.setValue(dma_lab_info.get("centr_nm"));
				ibx_dept_nm.setValue(dma_lab_info.get("dept_nm"));
				ibx_address.setValue(dma_lab_info.get("address"));
				ibx_main_rspns_nm.setValue(dma_lab_info.get("main_secrt_rspns_nm"));
				ibx_main_rspns_empno.setValue(dma_lab_info.get("main_secrt_rspns_empno"));
				ibx_main_rspns_mobile.setValue(dma_lab_info.get("main_secrt_rspns_mobile"));
				ibx_hdn_main_syspayno.setValue(dma_lab_info.get("main_secrt_rspns_syspayno"));
				ibx_sub_rspns_nm.setValue(dma_lab_info.get("sub_secrt_rspns_nm"));
				ibx_sub_rspns_empno.setValue(dma_lab_info.get("sub_secrt_rspns_empno"));
				ibx_sub_rspns_mobile.setValue(dma_lab_info.get("sub_secrt_rspns_mobile"));
				ibx_hdn_sub_syspayno.setValue(dma_lab_info.get("sub_secrt_rspns_syspayno"));
				ibx_hdn_lab_no.setValue(dma_lab_info.get("lab_no"));
				ibx_hdn_space_cd.setValue(dma_lab_info.get("space_cd"));

				scwin.searhDsuseDe(flag);
			}
		});			
	};
	
	/**
	 * 배출일자 조회
	 */	
	scwin.searhDsuseDe = function(flag) {
		if (ibx_hdn_space_cd.getValue() !="") {
			dma_search.set("space_cd", ibx_hdn_space_cd.getValue());
			dma_search.set("req_ymd", ica_req_ymd.getValue());
			dlt_deuse_dt.removeAll();
			com.execute(sbm_selectDsuseDeManage, {
				successCallback : function(e) {
					console.log(e.responseJSON);
					dlt_deuse_dt.setJSON(e.responseJSON.safChePrdDsuseInfoVoList);
					sbx_exhst.removeAll("");
					if (dlt_deuse_dt.getRowCount() == 0) {
						if (dma_info.get("apr_state") < "XAD020") {
							com.alert("선택하신 신청일자에는 배출일자가 없습니다.");
						}
					} else {
						sbx_exhst.addItem("","-선택-", 0);
						for (var i=0; i < dlt_deuse_dt.getRowCount(); i++) {
							var exhstYmd = dlt_deuse_dt.getCellData(i, "exhst_ymd");
							var odr = dlt_deuse_dt.getCellData(i, "odr");
							var value = exhstYmd + odr;
							var label = com.formatDate(exhstYmd,"dash") + "(" + odr + "차)";
							
							sbx_exhst.addItem(value, label, i+1);
						}
						var exhstSet = dma_info.get("wste_exhst_ymd") + dma_info.get("odr");
						sbx_exhst.setValue(exhstSet);
					}
					
					if (flag) { //화면 오픈시 조회하는 경우
						if (dma_param.get("reqNoArrInfo") !="") {							
							scwin.searchPrductqy();
						} else {
							dlt_product.insertRow(0);
							scwin.data.checkFlag = "F";
							grd_product.setFocusedCell(0, "use_req_no");
							return;
						}
					}
				}
			});			
		} else {
			if (flag) { //화면 오픈시 조회하는 경우
				if (dma_param.get("reqNoArrInfo") !="") {
					scwin.searchPrductqy();
				} else {
					dlt_product.insertRow(0);
					scwin.data.checkFlag = "F";
					grd_product.setFocusedCell(0, "use_req_no");
					return;
				}
			}		
		}
	};	
	
	/**
	 * 제품정보조회
	 */	
	scwin.searchPrductqy = function() {	
		com.execute(sbm_selectUseNoProductList, {
			successCallback : function(e) {
				dlt_lab_product.setJSON(e.responseJSON.safChePrdDsuseInfoVoList, false);
				var disposalState = dlt_lab_product.getCellData(0, "disposal_state");
				var barCodeSpaceCd = dlt_lab_product.getCellData(0, "space_cd");
				var selectSpaceCd = ibx_hdn_space_cd.getValue();
				
				if (dlt_lab_product.getRowCount() ==0) {
					com.alert("해당 제품은 이미 폐기된 제품입니다.");
					ibx_barcode_scan_value.setValue("");
					scwin.data.barcodeChk = "";
					return false;
				}
				
				if (disposalState == "SFI001" || disposalState == "SFI002" || disposalState == "SFI003") {
						//폐기저장, 결재신청,폐기확정 일떄만 등록 못하도록 변경
						com.alert("해당 제품은 이미 폐기신청한 제품입니다.");
						dlt_lab_product.removeAll();
						ibx_barcode_scan_value.setValue("");
						scwin.data.barcodeChk = "";
						return false;
				}
				
				//연구실이 입력되어있다면 동일 연구소 제품인지 체크
				if (selectSpaceCd.trim() != "") {
					if (selectSpaceCd != barCodeSpaceCd) {
						com.alert("선택한 연구실의 제품이 아닙니다. 선택한 연구실을 확인해주시기 바랍니다.");
						dlt_lab_product.removeAll();
						ibx_barcode_scan_value.setValue("");
						scwin.data.barcodeChk = "";
						return;
					}
				}	
				
				//그리드에 row를 붙이기전 선택된 그리드 제거
				var removeCell = grd_product.getFocusedRowIndex();
				if (dlt_product.getCellData( removeCell , "use_req_no" ) == "") {
					dlt_product.removeRow( removeCell );
				}
				for (var i = 0; i < dlt_lab_product.getRowCount(); i++) {
					dlt_lab_product.setCellData(i, "prduct_knd", "SFD060");
				}
				dlt_product.setJSON(dlt_lab_product.getAllJSON(), true);

				grd_product.setFocusedCell( dlt_product.getRowCount() - 1 , "use_req_no" );//그리드 제일 밑으로 포커스 이동
				
				grd_product.setColumnVisible( "companion_rs_btn" , false ); //폐기신청 최초 조회 시 반려건이 없으므로 반려사유 컬럼 히든처리
				
				scwin.searchProdChmstry();						
				cbx_wste_clsf.setValue("SFD020");	
			}
		});	
	};

	/**
	 * 제품 화학물질 조회
	 */		
	scwin.searchProdChmstry = function() {
		var arrPrduct = dlt_product.getColData("prduct_cd");
		dma_search.set("prduct_cd", dlt_product.getCellData(grd_product.getFocusedRowIndex(), "prduct_cd"));
		ibx_barcode_scan_value.focus();
		dlt_chmstry.removeAll();
		com.execute(sbm_selectProdChmstryData, {
			successCallback : function(e) {
				grp_chmstry.show("");
				dlt_chmstry.setJSON(e.responseJSON.safCheChmstryInfoVoList);
				
				if (dlt_chmstry.getRowCount() > 0) {
					dma_search.set("cas_no", dlt_chmstry.getCellData(0,"cas_no"));
					scwin.selectLawData();
				} else {
					tbx_cmi_name.setValue("");
					cbx_cmi.reset();
					cbx_is_law.reset();
					sbx_cl.setValue("");
					sbx_cl_detail.setValue("");
					sbx_cl_detail2.setValue("");
				}
				
				//바코드 이벤트로 세팅 후 초기화
				ibx_barcode_scan_value.setValue("");
				scwin.data.barcodeChk = "";
				
				if (scwin.data.checkFlag == "F") {
					//사용번호가 세팅되면 제품조회 버튼 안눌리게 변경처리
					for (var i = 0 ; i < dlt_product.getRowCount() ; i++) {
						if (dlt_product.getCellData( i , "use_req_no" ) != "") {
							grd_product.setCellDisabled( i , "prduct_search" , true );
						}
					}				
				}
				//바코드 이벤트로 세팅 후 초기화		
				scwin.selectDsuseProdList();		
			}
		});			
	};
	
	/**
	 * 물질명칭, 관리법,보건법, 안전관리법 조회
	 */
	scwin.selectLawData = function() {
		com.execute(sbm_selectLawData, {
			successCallback : function(e) {
				cbx_cmi.reset();
				cbx_is_law.reset();

				var infoArray = new Array();
				var infoArray2 = new Array();			
				tbx_cmi_name.setValue(dma_chmstry_info.get("chmcls_nm_kr"));			
					
				var chmclsManageLawArray = dma_chmstry_info.get("chmcls_manage_law").split(",");
				var industSafhelLawArray = dma_chmstry_info.get("indust_safhel_law").split(",");
				
				for (var i = 0; i < cbx_cmi.getItemCount(); i++) {
					var flag = false;
					
					for (var j =0; j < chmclsManageLawArray.length; j++) {
						if (i+1 == chmclsManageLawArray[j]) {
							flag = true;
						}
					}
					if (flag == true) {
						cbx_cmi.setSelectedIndex(i);
					}
				}
				
				for (var i = 0; i < cbx_is_law.getItemCount(); i++) {
					var flag = false;
					for (var j =0; j < industSafhelLawArray.length; j++) {
						if (i+1 == industSafhelLawArray[j]) {
							flag = true;
						}
					}
					if (flag == true) {
						cbx_is_law.setSelectedIndex(i);
					}
				}
				
				sbx_cl.setValue(dma_chmstry_info.get("cl"));
				sbx_cl_detail.setValue(dma_chmstry_info.get("cl_detail"));
				sbx_cl_detail2.setValue(dma_chmstry_info.get("cl_detail2"));
												
			}
		});		
	};

	/**
	 * 화학물질정보조회 스냅샷용 조회쿼리
	 */		
	scwin.selectDsuseProdList = function() {
		if (tbx_req_no.getValue() !="") {
			dma_search.set("dsuse_req_no", tbx_req_no.getValue());
			com.execute(sbm_selectDsuseProduct, {
