	/**
	 * 배출일자 조회 실행
	 */	
	scwin.searchExhst = function() {
		com.execute(sbm_selectDsuseDeManageInfoChk, {
			successCallback : function(e) { // 성공 콜백
				console.log(e.responseJSON);
				var result = e.responseJSON.safChePrdDsuseInfoVoList;
				
				var reqStartYmd = result[0].req_start_ymd.trim();
				var reqClsYmd = result[0].req_cls_ymd.trim();
				var regstNm = result[0].nm.trim();//안전보건관리자(등록자)
				var guidanceMatter = result[0].guidance_matter.trim();//안내사항
				
				var reqStartYmdFormat = com.formatDate(reqStartYmd);
				var reqClsYmdFormat = com.formatDate(reqClsYmd);
				
				ibx_req_exhst_ymd.setValue( reqStartYmdFormat + " ~ " + reqClsYmdFormat );
				ibx_reg_nm.setValue( regstNm );				
			} 
		});	
	};

	/**
	 * 폐기물분류 체크박스 변경 이벤트
	 */		
	scwin.cbx_wste_clsf_onchange = function(info) {
		grp_dsuse_req_info.hide();
		grp_product.hide();
		grp_chmstry.hide();
		var wsteClsfArray = cbx_wste_clsf.getValue( "," ).split(",");	
		
		for (var i = 0; i < wsteClsfArray.length; i++) {
			if (wsteClsfArray[i] == "SFD020") {
				grp_product.show();
				grp_chmstry.show();
			}
																	
			if (wsteClsfArray[i] == "SFD010") {
				grp_dsuse_req_info.show("");
			} 
		}		
	};
	
