	/**
	 * 폐기/취급 제품정보 그리드 cell 클릭 이벤트 
	 */
	scwin.grd_product_oncellclick = function(row,col,colId) {
		var dsuseAttachFileNo = dlt_product.getCellData( row, "dsuse_attach_file_no" );
		var msdsUrl = dlt_product.getCellData( row, "msds_url" );
		
		if (colId == "dsuse_attach_btn" ) {  
			var attachFileNo = dlt_product.getCellData(row, "dsuse_attach_file_no");
			//첨부파일 다운로드 공통팝업 오픈
			var data = {
			    attach_file_no : attachFileNo,
			    ref_yn : "Y",
			    mode : "view"
			};
			com.pop.open(com.pop.TYPE.ATTACH, data);
		}
		
		if (colId == "msds_url_btn" && msdsUrl.trim() != "") {
			
			window.open(msdsUrl,"MSDS URL","scrollbars=yes,toolbar=no,location=no,status=no,menubar=no,resizable=yes,width=900,height=600,left=100,top=100");
		}
		
		if (colId == "prduct_search") {
			if (ibx_hdn_space_cd.getValue() == "") {
				com.alert("연구실명을 입력해주시기 바랍니다.");
				return false;
  			} else {
				var prductCd = dlt_product.getCellData(row,dlt_product.getColumnIndex("prduct_cd"));
				var reqNo = dlt_product.getCellData(row,dlt_product.getColumnIndex("req_no"));
				//화학제품조회 팝업 오픈
				scwin.selectProductQy(reqNo);
  			}
  				
		}
		
		if (colId == "companion_rs_btn") {
			var data = {
				title_gubun:"select"
			    , use_req_no:dlt_product.getCellData(row, "use_req_no")
				, companion_rs:dlt_product.getCellData(row, "companion_rs")
			};
			biz.saf.pop.open(biz.saf.pop.TYPE.REQ_REJECT, data, function(ret) {
				if (ret) {
					console.log(ret);
				}
			});			
		}	
		scwin.searchProdChmstry();
		grd_product.setFocusedCell(row, col, true);	
	};
	
	/**
	 * 사용번호가 세팅되면 제품조회 버튼 안눌리게 변경처리
	 */
	scwin.grd_product_onrowindexchange = function(row,oldRow) {
		//사용번호가 세팅되면 제품조회 버튼 안눌리게 변경처리
		for (var i = 0 ; i < dlt_product.getRowCount() ; i++) {
			if (dlt_product.getCellData( i , "use_req_no" ) != "") {
				grd_product.setCellDisabled( i , "prduct_search" , true );
			}
		}		
	};	
	
