	/*
     * 저장 실행
     */		
	scwin.save = function() {
		com.execute(sbm_saveSafCheProdDsuse, {
			successCallback : function(e) {
				com.alert("저장하였습니다.");
				scwin.search();
				scwin.selectDsuseProdList();
			}
		});		
	}

	/*
     * 저장전 확인
     */	
	scwin.preSave = function() {
		if (!scwin.validate()) {
			return false;
		}
		if (cbx_wste_clsf.getValue().indexOf("SFD010") != -1) {
			if (dlt_deuse_req_info.getRowCount() < 1) {
				com.alert("폐기물 배출 신청 정보를 입력하세요.");
				return false;		
			} else {
				var validData = [
					{dataObj :dlt_deuse_req_info, 
						valInfoArr :[
							 {id : "wste_clsf", mandatory : true}
							,{id : "exhst_reqst_cpcty", mandatory : true}	
							,{id : "unit_clsf", mandatory : true}	
							,{id : "wste_knd", mandatory : true}			
						], isCheckModified : false
					},			
				];
				
				//필수 입력항목 검증
				if(!com.validateData(validData)){
					return false;
				}		
			}
		}
		
		if (dlt_product.getRowCount() == 0) {
			com.alert("폐기 제품 정보를 등록하세요.");
			return false;		
		}		
		
		return true;
	}
	
	/*
     * 검증
     */	
	scwin.validate = function() {
		if (!com.validateData(dma_info,[
			{ id : "reqst_ymd", mandatory : true},
			{ id : "wste_exhst_ymd", mandatory : true},
			{ id : "odr", mandatory : true},
			{ id : "lab_nm", mandatory : true},
			{ id : "wste_clsf", mandatory : true},
		], false)) return false;
		
		if (!com.isDate(ica_req_ymd.getValue(), false)) {
			return false;
		}
		
		return true;
	};
	
