	/**
	 * 연구실 space_cd 변경시 이벤트
	 */	
	scwin.ibx_hdn_space_cd_onchange = function(info) {
		if (this.getValue() !="") {
			var apyChk = "N";
			if (dma_info.get("apr_state") > "XAD015") {
				apyChk = "Y";
			}
			
			dma_search.set("aprChk", apyChk);
			dma_search.set("dsuse_req_no", tbx_req_no.getValue());
			dma_search.set("space_cd", ibx_hdn_space_cd.getValue());
			dma_search.set("req_ymd", ica_req_ymd.getValue());		
			scwin.searhDsuseDe();	
		}
	};

	/**
	 * 위험물안전관리법 selectbox 변경 이벤트
	 */		
	scwin.sbx_cl_onchange = function(info) {
		if (sbx_cl.getValue() !="") {
			var code = this.getValue();
			let codeOptions = [{ code: "SLC", compID:"sbx_cl_detail", filter: "mngmtItem1 == '" + code + "'"}];	
			com.setCmnCd(codeOptions, function() {
			    sbx_cl_detail.setValue("");
			});			
		}
	};

	/**
	 * 바코드 스캔 입력 값 변경 이벤트
	 */		
	scwin.ibx_barcode_scan_value_onchange = function(info) {
		//데이터 변경시 이벤트 처리
		var barcodeValue = ibx_barcode_scan_value.getValue().trim();	
		var convertValue = biz.saf.korTypeToEng(barcodeValue);	
		
		if (convertValue.length > 0) {
			if (convertValue.length != 17) {
				com.alert("사용번호가 정상적이지 않습니다. 다시 바코드를 찍어주시기 바랍니다.");
				ibx_barcode_scan_value.setValue( "" );
				return false;
			}
			
			//동일한 바코드로 입력하지 못하도록
			for (var i = 0 ; i < dlt_product.getRowCount() ; i++) {
				if (convertValue == dlt_product.getCellData( i , "use_req_no" )) {
					com.alert("이미 추가되어 있는 사용번호 입니다.");
					ibx_barcode_scan_value.setValue( "" );
					return false;
				}
			}
			scwin.data.barcodeChk = "Y";	
			dma_search.set("use_req_no", convertValue);
			//연구실 정보 조회
			scwin.searchSpaceInfoByUseNo();
		}		
	};

	/**
	 * 바코드 입력시 연구실정보 조회
	 */	
	scwin.searchSpaceInfoByUseNo = function() {
		com.execute(sbm_selectBarCodeLabInfo, {
			successCallback : function(e) {
				console.log(e.responseJSON);
				var result = e.responseJSON.safCheProdDsuseSpaceVoList;
				var reqChk = false;
				var userSyspayno = "";
				if (result != undefined && result.length > 0) {
					for (var i=0; i < result.length; i++) {
						userSyspayno = result[i].user_syspayno.tring();
						if (userSyspayno == ibx_hdn_req_syspayno.getValue()) {
							reqChk = true;
						}						
					}	
					
					if (!reqChk) {
						com.alert("폐기신청할 제품의 연구소에 소속되어 있지 않아 폐기신청 할 수 없습니다.");
						ibx_barcode_scan_value.setValue("");
						scwin.data.barcodeChk = "";
						return;
					}	
					
					if (ibx_cnter_nm.getValue() == "") {
						ibx_lab_nm.setValue(result[0].lab_nm);
						ibx_cnter_nm.setValue(result[0].centr_nm);
						ibx_dept_nm.setValue(result[0].dept_nm);
						ibx_address.setValue(result[0].address);
						ibx_main_rspns_nm.setValue(result[0].main_secrt_rspns_nm);
						ibx_main_rspns_empno.setValue(result[0].main_empno);
						ibx_sub_rspns_nm.setValue(result[0].sub_secrt_rspns_nm);
						ibx_sub_rspns_empno.setValue(result[0].sub_empno);
						ibx_hdn_lab_no.setValue(result[0].lab_no);
						ibx_hdn_space_cd.setValue(result[0].space_cd);						
					}
					
					dma_search.set("use_req_no", ibx_barcode_scan_value.getValue());				
				}
			}
		});			
	};	 
	
	/**
	 * 화면 콤포넌트 컨트롤
	 */	
	scwin.compControll = function() {
	    com.biz.disabledButtons(dma_info.get("apr_state"), dma_info.get("dsuse_req_no"));
	
		if (dma_info.get("apr_state") >= "XAD020") {
			btn_add_deuse.setDisabled(true);
			btn_del_deuse.setDisabled(true);
			btn_add_prod.setDisabled(true);
			btn_del_prod.setDisabled(true);
			ica_req_ymd.setDisabled(true);
			btn_search_req_emp.setDisabled(true);
			btn_search_space.setDisabled(true);
			
			for (var i = 0; i < dlt_product.getRowCount(); i++) {
				grd_product.setCellDisabled(i, "chk", true);
			}
			
			for (var i = 0; i < dlt_deuse_req_info.getRowCount(); i++) {
				grd_deuse_req_info.setCellDisabled(i, "chk", true);
			}
			sbx_exhst.setDisabled(true);
			cbx_wste_clsf.setDisabled(true);
			if (dma_info.get("apr_state") == "XAD100") {
				btn_print.setDisabled(false);
			}
		} else {
			if (ibx_req_emp_no.getValue() != com.getLoginInfo("empno")) {
				btn_save.setDisabled(true);
				btn_apr.setDisabled(true);
				btn_del.setDisabled(true);
			}
		}
	};	  

	/**
	 * 폐기/취급 제품정보 그리드 변경 이벤트
	 */	
	scwin.grd_product_onviewchange = function(info) {
			var changeCol = info.colId;
			if (scwin.data.checkFlag == "F") {
				if (changeCol != "chk" && changeCol != "prduct_knd") {
					var use_req_no = dlt_product.getCellData(info.row,"use_req_no");
					if (use_req_no.length>0) {
						scwin.barcodeEnd(info.row);
					}
				}
			} else {
				scwin.searchProdChmstry();
			}		
	};

	/**
	 * 바코드로 입력된 제품정보 조회
	 */		
	scwin.barcodeEnd = function(row) {
		var use_req_no = dlt_product.getCellData(row, "use_req_no" );
		dma_search.set("use_req_no", "D"+use_req_no);
		com.execute(sbm_selectBarCodeUseProductInfo, {
			successCallback : function(e) { // 성공 콜백
				if (dma_use_product_info.get("prduct_cd") !="") {
					dlt_product.setRowJSON(row, dma_use_product_info.getJSON(), true);
					dlt_product.insertRow();
					grd_product.setFocusedCell(row+1,"use_req_no");
					scwin.searchProdChmstry();
				}
			} 
		});			
	};
