head.script:
        
	scwin.onpageload = function() {
		var codeOptions = [ { code : "SEC", compID :"sbx_educ_grd" } // 교육목적 공통코드
						,	{ code : "SEE", compID :"rdo_educ_purp" } // 교육목적 공통코드
						,	{ code : "SEF", compID :"rdo_educ_form" } // 교육형태 공통코드
			];
		com.setCmnCd(codeOptions, function() {
		});
		
		// 첨부파일 컴포넌트 초기화
		com.file.init();
		
		scwin.in_it();
	};
	
	scwin.onpageunload = function() {
	};
	
	scwin.in_it = function(){
		// 초기 파라미터 세팅
		dma_search.setJSON(com.getParameter()); // mngmt_no , req_no
		dma_search.set('biz_clsf','SEA010'); // SEA010 // 교육
		
		if(dma_search.get('req_no') != ''){ // 상세 조회
			scwin.search();
		}else{ // 등록
			//btn_del.hide();
			//btn_apr.hide();
			com.biz.disabledButtons(); // btn_del , btn_apr , btn_save , btn_upd
			dma_info.set('cud_type', 'C');
			dma_info.set('biz_clsf', dma_search.get('biz_clsf'));
			
			// 첨부파일
			scwin.upload_setting("S151", "S151", "edit");
			
			rdo_educ_purp.setSelectedIndex(0);
			rdo_educ_form.setSelectedIndex(0);
		}
	};
	
	// 목록 버튼 클릭 시
	scwin.btn_list_onclick = function(e) {
		com.moveList("S150");
	};
	
	// 책임자 엔터시
	scwin.ibx_rspns_nm_onkeyup = function(e) {
		if(e.keyCode == 13){
			if(ibx_rspns_nm.getValue() == '') return;
			dma_user.set('gbn', 'rspns');
			dma_user.set('empno', ibx_rspns_nm.getValue());
			scwin.search_user();
		}else if((e.keyCode != 9) && (e.keyCode != 229)){
			dma_info.set('rspns_empno','');
			dma_info.set('rspns_syspayno','');
			dma_info.set('region_centr_nm','');
			dma_info.set('region_centr','');
			dma_info.set('dept_nm','');
			dma_info.set('dept_cd','');
			dma_info.set('dept_new_ymd','');
		}
	};
	
	// 인사정보 검색
	scwin.search_user = function() {
		dma_user.set('work_clsf', 'HAG010');
		com.execute(sbm_selectUserList, {
			successCallback : function(e) {
				dlt_user.setJSON(e.responseJSON.elData.humComUserSelVoList);
				scwin.search_user_after();
			}
		});
	};
	scwin.search_user_after = function(){
		var gbn = dma_user.get('gbn');
		var hum_cnt = dlt_user.getTotalRow();
		if(hum_cnt == 0){
			alert("조회정보가 없습니다!");
		}else if(hum_cnt == 1){
			if(gbn == 'rspns'){
				dma_info.set('rspns_nm'			,dlt_user.getCellData(0, 'nm'));
				dma_info.set('rspns_empno'		,dlt_user.getCellData(0, 'empno'));
				dma_info.set('rspns_syspayno'	,dlt_user.getCellData(0, 'syspayno'));
				dma_info.set('region_centr_nm'	,dlt_user.getCellData(0, 'dept_typ_nm'));
				dma_info.set('region_centr'		,dlt_user.getCellData(0, 'dept_typ'));
				dma_info.set('dept_nm'			,dlt_user.getCellData(0, 'dept_nm'));
				dma_info.set('dept_cd'			,dlt_user.getCellData(0, 'dept_cd'));
				dma_info.set('dept_new_ymd'		,dlt_user.getCellData(0, 'dept_new_ymd'));
			}else if(gbn == 'attnce'){
			}else if(gbn == 'contribute'){
			}
		}else{ // hum_cnt >1
			scwin.hum_popup(gbn);
		}
	};
	
	// 책임자 돋보기 클릭 시
	scwin.btn_rspns_onclick = function(e) {
