		scwin.hum_popup('rspns');
	};
	
	// 인사정보 팝업 호출
	scwin.hum_popup = function(gbn) {
		var nm = '';
		var empNo = '';
		var sPayNo = '';
		if(gbn == 'rspns'){ // 교육 정보 - 책임자
			nm = dma_info.get('rspns_nm');
			empNo = dma_info.get('rspns_empno');
			sPayNo = dma_info.get('rspns_syspayno');
		}
		
		var empno = '';
		if(empNo != '') empno = empNo;
		else if(nm != '') empno = nm;
		//var data = { nm : nm, empNo : empNo , sPayNo : sPayNo , work_clsf : "HAG010" }
		var data = { empno : empno , work_clsf : "HAG010" };
		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			scwin.hum_popup_callback(ret, gbn);
		});
	};
	// 인사정보 팝업 호출 콜백
	scwin.hum_popup_callback = function(ret, gbn) {
		if(gbn == 'rspns'){
			dma_info.set('rspns_nm',ret.nm);
			dma_info.set('rspns_empno',ret.empno);
			dma_info.set('rspns_syspayno',ret.syspayno);
			dma_info.set('region_centr_nm',ret.dept_typ_nm);
			dma_info.set('region_centr',ret.dept_typ);;
			dma_info.set('dept_nm',ret.dept_nm);
			dma_info.set('dept_cd',ret.dept_cd);
			dma_info.set('dept_new_ymd',ret.dept_new_ymd);
		}else if(gbn == 'attnce'){
			dlt_attnce.setCellData(scwin.selRow, 'attnce_nm', ret.nm);
			dlt_attnce.setCellData(scwin.selRow, 'dept_nm', ret.dept_nm);
			dlt_attnce.setCellData(scwin.selRow, 'posi_nm', ret.posi_nm);
			dlt_attnce.setCellData(scwin.selRow, 'attnce_mobile', ret.mobile);
			dlt_attnce.setCellData(scwin.selRow, 'attnce_email', ret.email);
			//dlt_attnce.setCellData(scwin.selRow, 'cust_no', ret.cust_no);
		}else if(gbn == 'contribute'){
			dlt_contribute.setCellData(scwin.selRow, 'nm', ret.nm);
			dlt_contribute.setCellData(scwin.selRow, 'empno', ret.empno);
			dlt_contribute.setCellData(scwin.selRow, 'dept_nm', ret.dept_nm);
			dlt_contribute.setCellData(scwin.selRow, 'ext_no', ret.ext_no);
			dlt_contribute.setCellData(scwin.selRow, 'syspayno', ret.syspayno);
		}
	};
	
	// 계정번호 팝업 호출 
	scwin.accnt_popup = function(){
		var data = { ref_yn : "Y" }; // ref_yn : 팝업조회여부(Y 가 조회안함)
		com.pop.open( com.pop.TYPE.ACNNO, data, function(ret){
			scwin.accnt_popup_callback(ret);
		});
	};
	// 계정번호 팝업 호출 콜백
