	// 추가 버튼 클릭
	scwin.btn_sched_add_onclick = function(e) {
		var row = com.addRow(grd_sched);
	};
	
	// 삭제 버튼 클릭
	scwin.btn_sched_del_onclick = function(e) {
		var rows = grd_sched.getCheckedIndex("chk");
		if(rows.length == 0){
			alert("삭제할 행을 선택해 주십시오");
			return;
		}
			
		rows.reverse().forEach(function(row) {
			dlt_sched.removeRow(row);
		});
	};
	
	// 목록 데이터 변경
	scwin.grd_sched_onviewchange = function(info) {
		var colId = grd_sched.getColumnID(info.colIndex);
		if(colId == "start_ymd" || colId == "start_tm" || colId == "end_ymd" || colId == "end_tm"){
			var start_ymd = dlt_sched.getCellData(info.rowIndex, 'start_ymd');
			var start_tm = dlt_sched.getCellData(info.rowIndex, 'start_tm');
			var end_ymd = dlt_sched.getCellData(info.rowIndex, 'end_ymd');
			var end_tm = dlt_sched.getCellData(info.rowIndex, 'end_tm');
			
			if(colId == "start_tm" && start_tm.length != 4){
				alert("시간은 4자리로 입력해 주십시오!");
				dlt_sched.setCellData(info.rowIndex, 'start_tm', '');
				return;
			}
			if(colId == "end_tm" && end_tm.length != 4){
				alert("시간은 4자리로 입력해 주십시오!");
				dlt_sched.setCellData(info.rowIndex, 'end_ymd', '');
				return;
			}
			if(start_ymd != '' && start_tm != '' && end_ymd != '' && end_tm != '') scwin.total_min_chk(start_ymd, start_tm, end_ymd, end_tm, info.rowIndex);
		}
	};
	
	// 소요시간 계산
	scwin.total_min_chk = function(start_ymd, start_tm, end_ymd, end_tm, rowIndex){
        var sYear = start_ymd.substr(0, 4);
        var sMonth = start_ymd.substr(4, 2);
        var sDay = start_ymd.substr(6, 2); 
        var sHour = start_tm.substr(0, 2); 
        var sTime = start_tm.substr(2, 2);
        sMonth = Number(sMonth)-1; 
        
        var eYear = end_ymd.substr(0, 4);
        var eMonth = end_ymd.substr(4, 2);
        var eDay = end_ymd.substr(6, 2);
        var eHour = end_tm.substr(0, 2); 
        var eTime = end_tm.substr(2, 2); 
        eMonth = Number(eMonth)-1;
        
        var fromDt = new Date(sYear,sMonth,sDay,sHour,sTime);
        var toDt = new Date(eYear,eMonth,eDay,eHour,eTime);
        var chkDate = (toDt.getTime() - fromDt.getTime()) / (1000 * 60) ;
        dlt_sched.setCellData(rowIndex, 'total_time', chkDate);
	};
	/////////// 주요프로그램 목록 END ///////////
	
