	// 목록 데이터 변경 시
	scwin.grd_attnce_onviewchange = function(info) {
		var colId = grd_attnce.getColumnID(info.colIndex);
		if(colId == "user_clsf"){ // 내외구분
			dlt_attnce.setCellData(info.rowIndex, 'attnce_nm', '');
			dlt_attnce.setCellData(info.rowIndex, 'dept_nm', '');
			dlt_attnce.setCellData(info.rowIndex, 'posi_nm', '');
			dlt_attnce.setCellData(info.rowIndex, 'attnce_mobile', '');
			dlt_attnce.setCellData(info.rowIndex, 'attnce_email', '');
			//dlt_attnce.setCellData(info.rowIndex, 'cust_no', '');
			
			dlt_attnce.setCellData(info.rowIndex, 'user_clsf_nm', '');
			
			if(info.newValue == '1'){ // 내부인
				scwin.selRow = info.rowIndex;
				grd_attnce.setReadOnly('cell', info.rowIndex, 'attnce_nm', true);
				grd_attnce.setReadOnly('cell', info.rowIndex, 'img_01', false);
				grd_attnce.setDisabled('cell', info.rowIndex, 'img_01', false);
				grd_attnce.setReadOnly('cell', info.rowIndex, 'dept_nm', true);
				grd_attnce.setReadOnly('cell', info.rowIndex, 'posi_nm', true);
				grd_attnce.setReadOnly('cell', info.rowIndex, 'attnce_mobile', true);
				grd_attnce.setReadOnly('cell', info.rowIndex, 'attnce_email', true);
				dlt_attnce.setCellData(info.rowIndex, 'user_clsf_nm', '내부인');
				scwin.hum_popup('attnce');
			}else if(info.newValue == '2'){ // 외부인
				grd_attnce.setReadOnly('cell', info.rowIndex, 'attnce_nm', false);
				grd_attnce.setReadOnly('cell', info.rowIndex, 'img_01', true);
				grd_attnce.setDisabled('cell', info.rowIndex, 'img_01', true);
				grd_attnce.setReadOnly('cell', info.rowIndex, 'dept_nm', false);
				grd_attnce.setReadOnly('cell', info.rowIndex, 'posi_nm', false);
				grd_attnce.setReadOnly('cell', info.rowIndex, 'attnce_mobile', false);
				grd_attnce.setReadOnly('cell', info.rowIndex, 'attnce_email', false);
				dlt_attnce.setCellData(info.rowIndex, 'user_clsf_nm', '외부인');
				//scwin.hum_popup('attnce');
			}else{
				grd_attnce.setReadOnly('cell', info.rowIndex, 'attnce_nm', false);
				grd_attnce.setReadOnly('cell', info.rowIndex, 'img_01', true);
				grd_attnce.setDisabled('cell', info.rowIndex, 'img_01', true);
				grd_attnce.setReadOnly('cell', info.rowIndex, 'dept_nm', false);
				grd_attnce.setReadOnly('cell', info.rowIndex, 'posi_nm', false);
				grd_attnce.setReadOnly('cell', info.rowIndex, 'attnce_mobile', false);
				grd_attnce.setReadOnly('cell', info.rowIndex, 'attnce_email', false);
				dlt_attnce.setCellData(info.rowIndex, 'user_clsf_nm', '');
			}
		}
	};
