	// 조회 실행
	scwin.search = function() {
		// SvcSUPEDUSPTSR01.pwkjson | SptTechEducInfoService.java // SUP_EDU_S155_00D 와 유사
		com.execute(sbm_selectSptTechEduc, {
			successCallback : function(e) {
				dma_info.set('cud_type', 'U');
				dma_info.setJSON(		e.responseJSON.elData.SptTechEducInfoVo); // 교육정보
				dlt_sched.setJSON(		e.responseJSON.elData.SptTechEducSchedVoList); // 주요프로그램
				dlt_reqdBugt.setJSON(	e.responseJSON.elData.SptTechEducReqdBugtVoList); // 소요예산
				dlt_contribute.setJSON(	e.responseJSON.elData.SptTechEducContributeVoList); // 기여자
				dlt_attnce.setJSON(		e.responseJSON.elData.SptTechEducAttnceVoList); // 교육참여자
				scwin.search_after();
			}
		});
	};
	scwin.search_after = function(){
		// 결재상태와 권한에 따라 화면제어
		scwin.setArrangeObj();
		// 주요프로그램 그리드의 '소요(분)' 계산
		for(var i=0;i<dlt_sched.getRowCount();i++){
			var start_ymd = dlt_sched.getCellData(i, 'start_ymd');
			var start_tm = dlt_sched.getCellData(i, 'start_tm');
			var end_ymd = dlt_sched.getCellData(i, 'end_ymd');
			var end_tm = dlt_sched.getCellData(i, 'end_tm');
			scwin.total_min_chk(start_ymd, start_tm, end_ymd, end_tm, i);
		}
	};
	
	// 결재상태와 권한에 따라 화면제어
	scwin.setArrangeObj = function() {
		// 권한
		if(dma_info.get('cud_type') == 'U'){
			com.biz.checkRole({serviceId : "S151", roleCodes : com.getLoginInfo("roleCode")}, function(e){
				if(e.isRoleYn){ // 관리권한 보유시
					scwin.roleChk = true;
				}
			});
			
			var attach_file_no_01 = dma_info.get('attach_file_no_01').trim();
			if(attach_file_no_01 == '') attach_file_no_01 = 'S151';
			var attach_file_no_02 = dma_info.get('attach_file_no_02').trim();
			if(attach_file_no_02 == '') attach_file_no_02 = 'S151';
			
			com.biz.disabledButtons(dma_info.get('apr_state'),dma_info.get('req_no')); // btn_del , btn_apr , btn_save , btn_upd
			if( (dma_info.get('rspns_syspayno') == com.getLoginInfo("sn") || dma_info.get('regst_syspayno') == com.getLoginInfo("sn") || scwin.roleChk)
				&& (dma_info.get('apr_state') == 'XAD010' || dma_info.get('apr_state') == 'XAD005' || dma_info.get('apr_state') == 'XAD015') ){
				// 관리관란여부 or 등록자 or 책임자 일 경우
				// 결재상태 : 작성중 , 저장중 , 반려 일 경우
				//btn_apr.show();
				//btn_del.show();
				//btn_save.show();
				scwin.upload_setting(attach_file_no_01, attach_file_no_02, "edit");
				btn_sched.show();
				btn_reqdBugt.show();
				btn_contribute.show();
				btn_attnce.show();
			}else{
				//btn_apr.hide();
				//btn_del.hide();
				//btn_save.hide();
				scwin.upload_setting(attach_file_no_01, attach_file_no_02, "view");
				btn_sched.hide();
				btn_reqdBugt.hide();
				btn_contribute.hide();
				btn_attnce.hide();
			}
		}
	};
	
	// 저장 버튼 클릭 시
	scwin.btn_save_onclick = function(e) {
		//cudType = "C";
		// 저장 전에 validation 체크 진행
		if(!scwin.pre_save()) return;
			
		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret){
			if(ret){
				scwin.aprYn = 'N';
				dma_info.set("approval", "false");
				scwin.save();
			}
		});
	};
	scwin.pre_save = function(){
		// 벨리데이션 체크
		// 교육 정보
		if(!scwin.pre_save_info()) return false;
		// 주요프로그램
		if(!scwin.pre_save_sched()) return false;
		// 소요예산
		if(!scwin.pre_save_reqdBugt()) return false;
		// 기여자
		if(!scwin.pre_save_contribute()) return false;
		// 참여자
		if(!scwin.pre_save_attnce()) return false;
		
		// 저장전 세팅
		dma_info.set("prcs_state", "C"); // 등록시 : C , 수정시 : U(조회시 U 로 변경됨)
		
		return true;
	};
	scwin.pre_save_info = function() {
		if(!com.validateData(dma_info,[
			{ id : "rspns_syspayno", mandatory : true}
		   ,{ id : "educ_procs", mandatory : true } 
		   ,{ id : "attnce_qty", mandatory : true }
		   ,{ id : "educ_start_ymd", mandatory : true } 
		   ,{ id : "educ_end_ymd", mandatory : true } 
		   ,{ id : "educ_grd", mandatory : true } 
		   ,{ id : "educ_purp", mandatory : true } 
		   ,{ id : "educ_form", mandatory : true } 
		   ,{ id : "educ_plce", mandatory : true }
		], false)) return false;
		
		// 첨부파일 필수 체크
		if(com.file.action.getTotalFileCount(upload_A.getID()) < 1){
			alert("교육계획서를 첨부해 주십시오!");
			return;
		}
		if(com.file.action.getTotalFileCount(upload_B.getID()) < 1){
			alert("결과보고서를 첨부해 주십시오!");
			return;
		}
		
		if(dma_info.get('educ_start_ymd') > dma_info.get('educ_end_ymd')){
	       com.alert("교육일자를 올바르게 입력해 주십시오!",function(){
		       ica_educ_start_ymd.focus();
	       });
	       return false;
		}
		
		return true;
	};
	scwin.pre_save_sched = function() {
		if(dlt_sched.getRowCount() == 0){
			com.alert("주요프로그램를 입력하세요",function(){
				btn_sched_add.focus();
	        });
			return false;
		};
		
		if(!com.validateData(dlt_sched,[
			{ id : "start_ymd", mandatory : true }
		   ,{ id : "start_tm", mandatory : true } 
		   ,{ id : "end_ymd", mandatory : true } 
		   ,{ id : "end_tm", mandatory : true } 
		   ,{ id : "contnt", mandatory : true } 
		   ,{ id : "lectr_nm", mandatory : true } 
		], false)) return false;
		
		return true;
	};
	scwin.pre_save_reqdBugt = function() {
		if(dlt_reqdBugt.getRowCount() == 0){
			com.alert("소요예산을 입력하세요",function(){
				btn_reqdBugt_add.focus();
	        });
			return false;
		};

		if(!com.validateData(dlt_reqdBugt,[
			{ id : "accnt_no", mandatory : true  }
		   ,{ id : "reqd_amt", mandatory : true } 
		], false)) return false;
		
		// 중복 계정번호 체크
		var accnt_no_chk = false;
		for(var i=0;i<dlt_reqdBugt.getRowCount();i++){
			var accnt_no = dlt_reqdBugt.getCellData(i, 'accnt_no');
			for(var j=0;j<dlt_reqdBugt.getRowCount();j++){
				if(i==j) continue;
				if(accnt_no == dlt_reqdBugt.getCellData(j, 'accnt_no')){
					accnt_no_chk = true;
				}
				
			}
		}
		if(accnt_no_chk){
			alert("계정번호는 동일 정보를 입력 할 수 없습니다!");
			return false;
		}
		
		return true;
	};
	scwin.pre_save_contribute = function() {
		if(dlt_contribute.getRowCount() == 0){
			com.alert("기여자를 입력하세요",function(){
				btn_contribute_add.focus();
	        });
			return false;
		};
		
		if(!com.validateData(dlt_contribute,[
			{ id : "syspayno", mandatory : true } 
		   ,{ id : "contribute_rt", mandatory : true }
		], false)) return false;
		
		var stake_dept = '';
		var totoal_contribute_rt = 0;
		for(var i=0;i<dlt_contribute.getRowCount();i++){
			var contribute_rt = dlt_contribute.getCellData(i, 'contribute_rt');
			if(contribute_rt > 100){
				com.alert("기여자 목록의 "+(i+1)+"번째 기여율이 100 을 넘을 수 없습니다!",function(){
					grd_contribute.setFocusedCell(i, 'contribute_rt', true);
		        });
				return false;
			}
			totoal_contribute_rt += Number(contribute_rt);
			
			if(stake_dept == '') stake_dept = dlt_contribute.getCellData(i, 'stake_dept');
		}
		/**if(totoal_contribute_rt != 100){
			alert("총 기여율은 100 이어야 합니다!");
			return false;
		}**/
		// 실적부서 하나이상 레크
		if(stake_dept == ''){
			alert("실적부서를 선택해 주십시오!");
			return false;
		}
		// 중복 기여자 체크
		var syspayno_chk = false;
		for(var i=0;i<dlt_contribute.getRowCount();i++){
			var syspayno = dlt_contribute.getCellData(i, 'syspayno');
			for(var j=0;j<dlt_contribute.getRowCount();j++){
				if(i==j) continue;
				if(syspayno == dlt_contribute.getCellData(j, 'syspayno')){
					syspayno_chk = true;
				}
				
			}
		}
		if(syspayno_chk){
			alert("기여자는 동일 인물을 입력 할 수 없습니다!");
			return false;
		}
		
		return true;
	};
	scwin.pre_save_attnce = function() {
		if(dlt_attnce.getRowCount() == 0){
			com.alert("참여자를 입력하세요",function(){
				btn_contribute_add.focus();
	        });
			return false;
		};
		
		if(!com.validateData(dlt_attnce,[
			{ id : "attnce_nm", mandatory : true }
		], false)) return false;
		
		return true;
	};
	scwin.save = function() {
		com.file.transport( function(attachObj){
			dma_info.set("attach_file_no_01", attachObj.upload_A);
			dma_info.set("attach_file_no_02", attachObj.upload_B);
			dma_info.set("regst_syspayno",  com.getLoginInfo("sn"));
			com.execute(sbm_saveSptTechEduc, {
				successCallback : function(e) {
					if(scwin.aprYn == "Y"){
						// 결재 화면으로 이동
						com.movePageMenu("A001", { "reqNo" :  dma_search.get("req_no")});
					}else{
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
							dma_search.set('req_no',e.responseJSON.elData.req_no);
							dma_search.set('mngmt_no',e.responseJSON.elData.mngmt_no);
							scwin.search();
						});
					}
				} ,
				exceptionCallback : function(resObj) {
					scwin.exceptionCallback(resObj);
				}
			});
		});	
	};
	
	// 삭제 버튼 클릭 시 
	scwin.btn_del_onclick = function(e) {
		// cudType = "D";
		com.confirm("message.xom.wq.022"/*삭제 하시겠습니까?*/, function(ret) {
			if(ret){
				// SvcSUPEDUSPTSD01.pwkjson | SptTechEducInfoService.java
				com.file.transport( function(attachObj){
					dma_info.set("attach_file_no_01", attachObj.upload_A);
					dma_info.set("attach_file_no_02", attachObj.upload_B);
					
					com.execute(sbm_deleteSptTechEduc, {
						successCallback : function(e) {
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
								com.moveList("S150", {mod :"delete"});
							});
						} ,
						exceptionCallback : function(resObj) {
							scwin.exceptionCallback(resObj);
						}
					});
				});	
			}
		});
	};
	
	// 결재신청 버튼 클릭 시
	scwin.btn_apr_onclick = function(e) {
		// SptTechEducReg.xsl
		// 저장 전에 validation 체크 진행
		if(!scwin.pre_save()) return;
		
		// 결제 신청일 경우 '당해년도' 실적인지 체크 후 SptTechSmnrReg.xml -> menuApproval()
		var today = com.getServerDateTime();
		var chk_val = today.substring(0,4) + "0101"
		if(dma_info.get('educ_start_ymd') <= chk_val){
			alert("당해년도 실적만 신청이 가능합니다. \n교육일자를 확인하여 주시기 바랍니다.");
			return;
		}
		
		scwin.aprYn = 'Y';
		dma_info.set("approval", "true");
		dma_info.set("reqd_amt_sum", grd_reqdBugt.getFooterData('reqd_amt_sum'));
		dma_info.set("contribute_rt_sum", grd_contribute.getFooterData('contribute_rt_sum'));
		// root/SptTechEducInfoVo/reqd_amt_sum , root/SptTechEducInfoVo/contribute_rt_sum,
		dma_info.set("educ_purp_nm", rdo_educ_purp.getText());
		dma_info.set("educ_form_nm", rdo_educ_form.getText());
		
		var user_clsf_nm;
		for(var i=0; i<dlt_attnce.getRowCount(); i++){
			if(dlt_attnce.getCellData(i, "user_clsf") == "1"){	//내부인
				dlt_attnce.setCellData(i, "user_clsf_nm", "내부인");
			} else if (dlt_attnce.getCellData(i, "user_clsf") == "2"){	//외부인
				dlt_attnce.setCellData(i, "user_clsf_nm", "외부인");
			}
		} 
		
		dma_info.set('cud_type', 'A');
		
		scwin.save();
		// menuApproval
	};
	
	// exceptionCallback
	scwin.exceptionCallback = function(resObj){
		var resultCd = gcm.sbm.getResCode(resObj);
		var msg = gcm.lang.getMsg(resultCd);
		var resJSON = resObj.responseJSON || null;
		var resultMsg = gcm.sbm.getResMsg(resObj); 
		
		if (gcm.util.isEmpty(msg) || msg == resultCd || msg.indexOf("{0}") != -1) { 
			// resultCd가 등록되지 않은 메시지 코드인 경우 resultMsg alert
			msg = resultMsg;
		}
		if (!com.isEmpty(resJSON.userHeader.errBusiness)) {
			msg = "["+ resJSON.userHeader.errBusiness +"] "+ msg;
		}
		if (resJSON.userHeader.errKind == "ALERT") {
			com.alert(msg);
		} else if (resJSON.userHeader.errKind == "ERROR") {
			com.error(msg);
		} else {
			com.warn(msg);
		}
	};
	
