head.script:
        
	scwin.onpageload = function() {
		// 첨부파일 컴포넌트 초기화
		com.file.init();
		// 초기세팅
		scwin.in_it();
	};
	
	scwin.onpageunload = function() {
	};
	
	scwin.in_it = function(){
		// 초기 파라미터 세팅
		dma_search.setJSON(com.getParameter()); // mngmt_no , req_no
		dma_search.set('biz_clsf','SEA020'); // SEA020 // 세미나	
		
		if(dma_search.get('req_no') != ''){ // 상세 조회
			scwin.search();
		}else{ // 등록
			// 반려정보 숨김
			reject_rmk_head.hide();
			reject_rmk.hide();
			//btn_del.setDisabled(true);
			//btn_apr.setDisabled(true);
			com.biz.disabledButtons(); // btn_del , btn_apr , btn_save , btn_upd
			dma_info.set('cud_type', 'C');
			dma_info.set('biz_clsf', dma_search.get('biz_clsf'));
			
			// 첨부파일
			scwin.upload_setting("S155", "S155", "edit");
			
		}
	};
	
	// 조회 실행
	scwin.search = function() {
		// SvcSUPEDUSPTSR01.pwkjson | SptTechEducInfoService.java 
		// AS-IS 		: SptEduInfoBiz.java
		// 세미나 상세 		: kr.re.kitech.biz.sup.edu.selectSptTechEducInfoSmnrDetail ( SptTechEducInfo_SQL_informix_MyBatis.xml )
		// AS-IS 		: kitech.sup.edu.xda.SptTechEducInfoSS03
		// 참여자 목록 		: kr.re.kitech.biz.sup.edu.selectSptTechSmnrAttnceList ( SptTechEducAttnce_SQL_informix_MyBatis.xml )
		// AS-IS 		: kitech.sup.edu.xda.SptTechEducAttnceMS01
		// 주요프로그램 목록 	: kr.re.kitech.biz.sup.edu.selectSptTechEducSchedList ( SptTechEducSched_SQL_informix_MyBatis.xml )
		// AS-IS 		: kitech.sup.edu.xda.SptTechEducSchedMS01
		// 소요예산 목록 	: kr.re.kitech.biz.sup.edu.selectSptTechEducReqdBugtList ( SptTechEducReqdBugt_SQL_informix_MyBatis.xml )
		// AS-IS		: kitech.sup.edu.xda.SptTechEducReqdBugtMS01
		// 기여자 목록		: kr.re.kitech.biz.sup.edu.selectSptTechEducContributeList ( SptTechEducContribute_SQL_informix_MyBatis.xml )
		// AS-IS 		: kitech.sup.edu.xda.SptTechEducContributeMS01
		com.execute(sbm_selectSptTechEduc, {
			successCallback : function(e) {
				dma_info.set('cud_type', 'U');
				dma_info.setJSON(		e.responseJSON.elData.SptTechEducInfoVo); // 세미나정보
				dlt_attnce.setJSON(		e.responseJSON.elData.SptTechEducAttnceVoList); // 세미나참여자
				dlt_sched.setJSON(		e.responseJSON.elData.SptTechEducSchedVoList); // 주요프로그램
				dlt_reqdBugt.setJSON(	e.responseJSON.elData.SptTechEducReqdBugtVoList); // 소요예산
				dlt_contribute.setJSON(	e.responseJSON.elData.SptTechEducContributeVoList); // 기여자
				scwin.search_after();
			}
		});
	};
	scwin.search_after = function(){
		// 결재상태와 권한에 따라 화면제어
		scwin.setArrangeObj();
		// 주요프로그램 그리드의 '소요(분)' 계산
		for(var i=0;i<dlt_sched.getRowCount();i++){
			var start_ymd = dlt_sched.getCellData(i, 'start_ymd');
			var start_tm = dlt_sched.getCellData(i, 'start_tm');
			var end_ymd = dlt_sched.getCellData(i, 'end_ymd');
			var end_tm = dlt_sched.getCellData(i, 'end_tm');
			scwin.total_min_chk(start_ymd, start_tm, end_ymd, end_tm, i);
		}
	};
	
	// 결재상태와 권한에 따라 화면제어
	scwin.setArrangeObj = function() {
		// TODO By SON -- 상태에 따른 반려정보 hide/show 
		// 담당자가 결재대기함에서 반려시 prcs_state 값을 X 로 변경하지도 않고, 반려 메세지를 spttecheducinfo.rmk 에 업데이트 하지 않음.....
		if(dma_info.get('prcs_state') != 'X'){
			reject_rmk_head.hide();
			reject_rmk.hide();
		}
		
		// 권한
		if(dma_info.get('cud_type') == 'U'){
			com.biz.checkRole({serviceId : "S155", roleCodes : com.getLoginInfo("roleCode")}, function(e){
				if(e.isRoleYn){ // 관리권한 보유시
					scwin.roleChk = true;
				}
			});
			
			var attach_file_no_01 = dma_info.get('attach_file_no_01').trim();
			if(attach_file_no_01 == '') attach_file_no_01 = 'S155';
			var attach_file_no_02 = dma_info.get('attach_file_no_02').trim();
			if(attach_file_no_02 == '') attach_file_no_02 = 'S155';
			// 상세조회시
			console.log('scwin.roleChk => ' + scwin.roleChk);
			com.biz.disabledButtons(dma_info.get('apr_state'),dma_info.get('req_no')); // btn_del , btn_apr , btn_save , btn_upd
			if( (dma_info.get('rspns_syspayno') == com.getLoginInfo("sn") || dma_info.get('regst_syspayno') == com.getLoginInfo("sn") || scwin.roleChk)
				&& (dma_info.get('apr_state') == 'XAD010' || dma_info.get('apr_state') == 'XAD005' || dma_info.get('apr_state') == 'XAD015') ){
				// 관리관란여부 or 등록자 or 책임자 일 경우
				// 결재상태 : 작성중 , 저장중 , 반려 일 경우
				//btn_apr.setDisabled(false);
				//btn_del.setDisabled(false);
				//btn_save.setDisabled(false);
				scwin.upload_setting(attach_file_no_01, attach_file_no_02, "edit");
				btn_attnce.show();
				btn_sched.show();
				btn_reqdBugt.show();
				btn_contribute.show();
			}else{
				//btn_apr.setDisabled(true);
				//btn_del.setDisabled(true);
				//btn_save.setDisabled(true);
				scwin.upload_setting(attach_file_no_01, attach_file_no_02, "view");
				btn_attnce.hide();
				btn_sched.hide();
				btn_reqdBugt.hide();
				btn_contribute.hide();
			}
		}
	};
	
	// 책임자 돋보기 클릭 시
	scwin.btn_rspns_onclick = function(e) {
		// 인사정보 팝업 호출
		scwin.hum_popup('rspns');
	};
	
	// 인사정보 팝업 호출
	scwin.hum_popup = function(gbn) {
		var nm = '';
		var empNo = '';
		var sPayNo = '';
		if(gbn == 'rspns'){ // 세미나 정보 - 책임자
			nm = dma_info.get('rspns_nm');
			empNo = dma_info.get('rspns_empno');
			sPayNo = dma_info.get('rspns_syspayno');
		}
		
		var empno = '';
		if(empNo != '') empno = empNo;
		else if(nm != '') empno = nm;
		//var data = { nm : nm, empNo : empNo , sPayNo : sPayNo , work_clsf : "HAG010" }
		var data = { empno : empno , work_clsf : "HAG010" };
		com.pop.open( com.pop.TYPE.HUM, data, function(ret){ // COM_Z002_00P.xml
			scwin.hum_popup_callback(ret, gbn);
		});
	};
	// 인사정보 팝업 호출 콜백
	scwin.hum_popup_callback = function(ret, gbn) {
		if(gbn == 'rspns'){
			dma_info.set('rspns_nm',ret.nm);
			dma_info.set('rspns_empno',ret.empno);
			dma_info.set('rspns_syspayno',ret.syspayno);
			dma_info.set('region_centr_nm',ret.dept_typ_nm);
			dma_info.set('region_centr',ret.dept_typ);;
			dma_info.set('dept_nm',ret.dept_nm);
			dma_info.set('dept_cd',ret.dept_cd);
			dma_info.set('dept_new_ymd',ret.dept_new_ymd);
		}else if(gbn == 'attnce'){
			dlt_attnce.setCellData(scwin.selRow, 'attnce_nm', ret.nm);
			dlt_attnce.setCellData(scwin.selRow, 'attnce_mobile', ret.mobile);
			dlt_attnce.setCellData(scwin.selRow, 'attnce_email', ret.email);
		}else if(gbn == 'contribute'){
			dlt_contribute.setCellData(scwin.selRow, 'nm', ret.nm);
			dlt_contribute.setCellData(scwin.selRow, 'empno', ret.empno);
			dlt_contribute.setCellData(scwin.selRow, 'dept_nm', ret.dept_nm);
			dlt_contribute.setCellData(scwin.selRow, 'ext_no', ret.ext_no);
			dlt_contribute.setCellData(scwin.selRow, 'syspayno', ret.syspayno);
		}
	};
	
