			scwin.attnce_qty_chk();
		});
	};
	
	// 복사 버튼 클릭
	scwin.btn_attnce_copy_onclick = function(e) {
		var rows = grd_attnce.getCheckedIndex("chk");
		if(rows.length == 0){
			alert("복사 할 행을 선택해 주십시오");
			return;
		}
		
		rows.forEach(function(row) {
			// var row = com.addRow(grd_attnce);
			dlt_attnce.setCellData(row, "chk", "N");
			
			var rowCnt = dlt_attnce.getRowCount();
			dlt_attnce.setRowJSON(rowCnt, dlt_attnce.getRowJSON(row));
			dlt_attnce.setCellData(rowCnt, "cust_no", "");
			dlt_attnce.setCellData(rowCnt, "seq", "");
			dlt_attnce.setCellData(rowCnt, 'attnce_nm', "");
			dlt_attnce.setCellData(rowCnt, 'attnce_mobile', "");
			dlt_attnce.setCellData(rowCnt, 'attnce_email', "");
			scwin.attnce_qty_chk();
		});
	};
	
