	scwin.onpageunload = function() {
	};
	
	scwin.in_it = function(){
		// 초기 파라미터 세팅
		dma_search.setJSON(com.getParameter()); // par_eval_req_no
		
		dma_search.set("par_eval_req_no", com.getParameter("req_no"));
		dma_eval.set("service_id", scwin.data.serviceId);		
		//dma_eval.set("manager_id", "gsaty3398");	// 유선희 사무원
		
		if(dma_search.get("par_eval_req_no") != ""){ // 상세 조회
			scwin.search();
		}else{ // 등록
			// inputReqPsn.setValue( cn );
			// inputReqDt.setValue( gfnGetToday() );
			com.biz.disabledButtons(); // btn_del , btn_apr , btn_save , btn_upd
			dma_eval.set("cud_type", "C");
			var today = com.getServerDateTime();
			dma_eval.set("regst_syspayno"	,com.getLoginInfo().sn);
			dma_eval.set("regst_nm"			,com.getLoginInfo().cn);
			dma_eval.set("regst_daytm"		,today);
			//scwin.search_cust_pro();
		}
	};
	
	// 조회 실행
	scwin.search = function() {
		// SvcSUPPAREVALSV01.pwkjson | SptParInfoEvalService.java 
		// 상세 			: kr.re.kitech.biz.sup.par.selectSptParInfoEvalDetail ( SptParInfoEval_SQL_informix_MyBatis.xml )
		// AS-IS 		: kitech.sup.par.xda.SptParInfoEvalSS01
		com.execute(sbm_selectSptParEvalDetail, {
			successCallback : function(e) {
				dma_eval.setJSON(		e.responseJSON.elData); // 파트너기업유지심사서(상세)정보
				scwin.search_after();
			}
		});
	};
	
	scwin.search_after= function() {
		// 결재상태와 권한에 따라 화면제어
		scwin.setArrangeObj();
		
		// 파트너기업유지심사서(상세)정보
		var mentoKeepYn = dma_eval.get("mento_keep_yn");
		btn_mento.setDisabled(true);
		ibx_mento_nm.setReadOnly(true);
		if(mentoKeepYn == "KEP1"){
			rdo_mento_keep_yn_01.setValue("Y");
			rdo_mento_keep_yn_02.setValue("N");
			rdo_mento_keep_yn_03.setValue("N");
		}else if(mentoKeepYn == "KEP2"){
			rdo_mento_keep_yn_01.setValue("N");
			rdo_mento_keep_yn_02.setValue("Y");
			rdo_mento_keep_yn_03.setValue("N");
		}else if(mentoKeepYn == "KEP3"){
			rdo_mento_keep_yn_01.setValue("N");
			rdo_mento_keep_yn_02.setValue("N");
			rdo_mento_keep_yn_03.setValue("Y");
			btn_mento.setDisabled(false);
			ibx_mento_nm.setReadOnly(false);
		} 
		
		scwin.search_cust_pro();
	};
	
	scwin.search_cust_pro = function() {
		// SvcSUPPAREVALSS01.pwkjson | SptParInfoEvalService.java 
		// 파트너기업 정보 		: kr.re.kitech.biz.sup.par.selectSptBaseDetail ( SptParBase_SQL_informix_MyBatis.xml )
		// AS-IS 			: kitech.sup.par.xda.SptParInfoEvalMS01
		// 담당자 목록 			: kr.re.kitech.biz.sup.par.selectSptParCustChargList ( SptParCustCharg_SQL_informix_MyBatis.xml )
		// AS-IS 			: kitech.sup.par.xda.SptParInfoEvalMS02
