			successCallback : function(e) {
				dma_base.setJSON(		e.responseJSON.elData.SptParBaseVo); 			// 파트너기업 정보
				dlt_cust_charg.setJSON(	e.responseJSON.elData.SptParCustChargVoList); 	// 담당자 목록
				dlt_cust_pro.setJSON(	e.responseJSON.elData.SptParCustProVoList);		// 기업지원프로그램 목록
				scwin.search_cust_pro_after();
			}
		});
