			rdo_mento_keep_yn_03.setValue("N");
			btn_mento.setDisabled(true);
			ibx_mento_nm.setReadOnly(true);
		}
	};
	// 탈퇴검토 라디오 변경시
	scwin.rdo_mento_keep_yn_02_onviewchange = function(info) {
		if(info.value == "Y"){
			rdo_mento_keep_yn_01.setValue("N");
			rdo_mento_keep_yn_03.setValue("N");
			btn_mento.setDisabled(true);
			ibx_mento_nm.setReadOnly(true);
		}
	};
	// 멘토변경 라디오 변경시
	scwin.rdo_mento_keep_yn_03_onviewchange = function(info) {
		if(info.value == "Y"){
			rdo_mento_keep_yn_01.setValue("N");
			rdo_mento_keep_yn_02.setValue("N");
			btn_mento.setDisabled(false);
			ibx_mento_nm.setReadOnly(false);
		}else{
			btn_mento.setDisabled(true);
			ibx_mento_nm.setReadOnly(true);
		}

	};

	// 추천멘토 엔터시
	scwin.ibx_mento_nm_onkeyup = function(e) {
		if(e.keyCode == 13){
			if(ibx_mento_nm.getValue() == "") return;
			dma_user.set("empno", ibx_mento_nm.getValue());
			scwin.search_user();
		}else if((e.keyCode != 9) && (e.keyCode != 229)){
			dma_eval.set("mento_empno", "");
			dma_eval.set("mento_syspayno", "");
		}
	};
	
	// 인사정보 실행
	scwin.search_user = function() {
		dma_user.set("work_clsf", "HAG010");
		com.execute(sbm_selectUserList, {
			successCallback : function(e) {
				dlt_user.setJSON(e.responseJSON.elData.humComUserSelVoList);
				scwin.search_user_after();
			}
		});
	};
	scwin.search_user_after = function(){
		var hum_cnt = dlt_user.getTotalRow();
		if(hum_cnt == 0){
			com.alert("조회정보가 없습니다!");
		}else if(hum_cnt == 1){
			dma_eval.set("mento_nm"			, dlt_user.getCellData(0, "nm"));
			dma_eval.set("mento_empno"		, dlt_user.getCellData(0, "empno"));
			dma_eval.set("mento_syspayno"	, dlt_user.getCellData(0, "syspayno"));
		}else{ // hum_cnt >1
			scwin.hum_popup();
		}
	};

	// 추천멘토 찾기 버튼 클릭
	scwin.btn_mento_onclick = function(e) {
		// 인사정보 팝업 호출
		scwin.hum_popup();
	};
	
	// 인사정보 팝업 호출
	scwin.hum_popup = function() {
		var nm = dma_eval.get("mento_nm");
		var empNo = dma_eval.get("mento_empno");
		var sPayNo = dma_eval.get("mento_syspayno");
		
		var empno = "";
		if(empNo != "") empno = empNo;
		else if(nm != "") empno = nm;
		//var data = { nm : nm, empNo : empNo , sPayNo : sPayNo , work_clsf : "HAG010" }
		var data = { empno : empno , work_clsf : "HAG010" }
		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			scwin.hum_popup_callback(ret);
		});
	};
	// 인사정보 팝업 호출 콜백
	scwin.hum_popup_callback = function(ret) {
		dma_eval.set("mento_nm"			, ret.nm);
		dma_eval.set("mento_empno"		, ret.empno);
		dma_eval.set("mento_syspayno"	, ret.syspayno);
	};
	
	// 사업자등록번호 엔터시
	scwin.ibx_bsns_psn_regst_no_onkeyup = function(e) {
		if(e.keyCode == 13){
			if(ibx_bsns_psn_regst_no.getValue() == "") return;
			dma_cust.set("bsns_psn_regst_no", ibx_bsns_psn_regst_no.getValue());
			dma_cust.set("biz_clsf", "SE"); 
			dma_cust.set("isPartner", "Y");
			scwin.search_cust();
		}else if((e.keyCode != 9) && (e.keyCode != 229)){
			dma_eval.set("cust_no"			,"");
			dma_eval.set("cmpy_nm_krchar"	,"");
			//dma_eval.set("bsns_psn_regst_no","");
			dma_eval.set("reprs_psn"		,"");
			dma_eval.set("cmpy_reprs_tel"	,"");
			dma_eval.set("appont_no"		,"");
			
			//scwin.search_cust_pro();
			grd_cust_pro.setDisabled("column", "utill_o", "N");
			grd_cust_pro.setDisabled("column", "utill_x", "N");
			
