	scwin.search_cust_after = function(){
		var cust_cnt = dlt_cust.getTotalRow();
		if(cust_cnt == 0){
			com.alert("조회정보가 없습니다!");
		}else if(cust_cnt == 1){
			dma_eval.set("cust_no"			,dlt_cust.getCellData(0, "vend_cd"));
			dma_eval.set("cmpy_nm_krchar"	,dlt_cust.getCellData(0, "vend_abbr"));
			dma_eval.set("bsns_psn_regst_no",dlt_cust.getCellData(0, "bsns_psn_regst_no"));
			dma_eval.set("reprs_psn"		,dlt_cust.getCellData(0, "reprs_psn_nm"));
			dma_eval.set("cmpy_reprs_tel"	,dlt_cust.getCellData(0, "tel_no"));
			dma_eval.set("appont_no"		,dlt_cust.getCellData(0, "appont_no"));
			
			scwin.search_cust_pro();
		}else{ // cust_cnt >1
			scwin.cust_popup();
		}
	};
	
	// 사업자등록번호 찾기버튼 클릭
	scwin.btn_cust_onclick = function(e) {
