	};
	
	scwin.cust_popup = function(e) {
		var data = { biz_clsf : "SE" , isPartner  : "Y" };
		data.bsns_psn_regst_no = ibx_bsns_psn_regst_no.getValue();
		com.pop.open( com.pop.TYPE.VEND, data, function(ret){ // COM_Z005_00P.xml
