			scwin.cust_popup_callback(ret);
		});
	};
	// 고객정보 팝업 호출 콜백
	scwin.cust_popup_callback = function(ret) {
		dma_eval.set("cust_no"				,ret.vend_cd);
		dma_eval.set("cmpy_nm_krchar"		,ret.vend_abbr);
		dma_eval.set("bsns_psn_regst_no"	,ret.bsns_psn_regst_no);
		dma_eval.set("reprs_psn"			,ret.reprs_psn_nm);
