		//if(ret.vend_cd != "" && ret.vend_cd != undefined) scwin.search_cust_pro();
	};
	
	// 저장 버튼 클릭
	scwin.btn_save_onclick = function(e) {
		if(!scwin.pre_save()) return;
			
		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret){
			if(ret){
				scwin.aprflag = "N";
				dma_eval.set("approval", "false");
				scwin.save();
			}
		});
	};
	scwin.pre_save = function(e) {
		if(!com.validateData(dma_eval,[
			{ id : "cust_no", mandatory : true } 
		   ,{ id : "eval_daytm", mandatory : true }
		], false)) return false;
		
		if(!com.validateData(dlt_cust_charg,[
			{ id : "affair_respn_psn", mandatory : true } 
		   ,{ id : "affair_respn_psn_dept", mandatory : true }
		   ,{ id : "affair_respn_psn_tel", mandatory : true }
		   ,{ id : "affair_respn_psn_mobile_no", mandatory : true }
		   ,{ id : "affair_respn_psn_email", mandatory : true }
		], false)) return false;

		/**if(dma_eval.get("cmpy_reprs_tel") == ""){
			com.alert("파트너 기업 탈퇴한 기업은 저장이 불가능 합니다."); 
			return false;
		}**/
		
		if(scwin.data.joinEx == "N"){
		 	com.alert("파트너 기업 탈퇴한 기업은 저장이 불가능 합니다."); 
			return false;
		}
		
		if(rdo_mento_keep_yn_01.getValue() == "Y") dma_eval.set("mento_keep_yn", "KEP1"); // 멘토 유지
		else if(rdo_mento_keep_yn_02.getValue() == "Y") dma_eval.set("mento_keep_yn", "KEP2"); // 탈퇴 검토
		else if(rdo_mento_keep_yn_03.getValue() == "Y") dma_eval.set("mento_keep_yn", "KEP3"); // 멘토 변경
		else{
			com.alert("파트너기업 유지 여부 정보를 선택 하세요."); 
			return false;
		}
		
		return true;
	};
	scwin.save = function(e) {
		// SvcSUPPAREVALSI01.pwkjson | SptParInfoEvalService.java  
		// 상세 			: kr.re.kitech.biz.sup.par.mergeSptParInfoEval ( SptParInfoEval_SQL_informix_MyBatis.xml )
		// AS-IS 		: kitech.sup.par.xda.SptParInfoEvalSI01 | kitech.sup.par.xda.SptParInfoEvalSU01
		// 담당자 목록 		: kr.re.kitech.biz.sup.par.mergeSptParCustCharg ( SptParCustCharg_SQL_informix_MyBatis.xml )
		// AS-IS 		: kitech.sup.par.xda.SptParCustInfoSU01
		
		//[{"id":"dma_eval","key":"SptParInfoEvalVo"},{"id":"dlt_cust_charg","key":"SptParCustChargVoList"},{"id":"dlt_cust_pro","key":"SptParCustProVoList"}]
		com.execute(sbm_saveSptParEvalDetail, {
			successCallback : function(e) {
				if(scwin.aprflag == "Y"){
					// 결재 화면으로 이동
					com.movePageMenu("A001", { "reqNo" :  dma_search.get("par_eval_req_no")});
				}else{
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
						dma_search.set("par_eval_req_no",e.responseJSON.elData.par_eval_req_no);
						scwin.search();
					});
				}
			} ,
			exceptionCallback : function(resObj) {
				scwin.exceptionCallback(resObj);
			}
		});
	};
	
	// 삭제 버튼 클릭
	scwin.btn_del_onclick = function(e) {
		com.confirm("message.xom.wq.022"/*삭제 하시겠습니까?*/, function(ret) {
			if(ret){
				// SvcSUPPAREVALSD01.pwkjson | SptTechEducInfoService.java
				com.execute(sbm_deleteSptParEvalDetail, {
					successCallback : function(e) {
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
							com.moveList("S422", {mod :"delete"});
						});
					} ,
					exceptionCallback : function(resObj) {
						scwin.exceptionCallback(resObj);
					}
				});
			}
		});
	};
	
	
	
	
	
	
