		dma_eval.set("rec_mento_sysno", ibx_mento_nm.getValue());    // 추천멘토 개인번호 
		dma_eval.set("rec_mento_reason", ibx_mento_nm.getValue());   // 멘토 변경사유 
		dma_eval.set("last_eval_ymd", dma_base.get("eval_daytm")); 	// 최종 유지심사일
		dma_eval.set("last_eval_nm", dma_base.get("eval_nm"));		// 최종 유지심사자
		
		scwin.save();
	};

