head.script:
	/****************************************************
 	 * 화면 설명 : 외부기술지원(유상)
 	 * 변경 이력 : 2023-06-02	이영희  최초 작성
 	 * 서비스아이디 : S303
 	 ****************************************************/  
    /*
     * 페이지 열기
     */          
	scwin.onpageload = function() {
		// 초기화
		var today = com.getServerDateTime();
		ica_from.setValue(com.addDate(today, -30));
		ica_to.setValue(today);

		// 엔터키 이벤트
		com.setEnterKeyEvent(grp_accnt_no, scwin.btn_accnt_no_onclick);
		com.setEnterKeyEvent(grp_supt_respn, scwin.btn_accnt_no_onclick); // 지원책임자
		com.setEnterKeyEvent(grp_rcpt_psn, scwin.btn_rcpt_psn_onclick); // 지원담당자

		// 기간 체크
		com.checkPeriod(ica_from, ica_to);	

		sn = com.getLoginInfo("sn");
		uid = com.getLoginInfo("uid");

		if(uid == "rpabot1" || uid == "rpabot2" || uid == "rpabot3"){
			btn_rpa_yn.setDisabled(false);
		}

		// 예외자 가지고 옴(엑셀내리기(장비)버튼)
		const isExcept = com.isExceptAwait('XEK051', 'S303', com.getLoginInfo("sn"));


		com.biz.checkRole({serviceId : "S303", roleCodes : com.getLoginInfo("roleCode")}, function(e){
			if(!e.isRoleYn){ // 관리권한 보유시	
				dma_search.set("syspayno", com.getLoginInfo("sn"));

				com.execute(sbm_selectChrgCnt, { // 실험실 담당자여부 조회
					successCallback : function(e){	
						debugger;
						if(!isExcept && WebSquare.util.parseInt(e.responseJSON.elData.cnt, 0) == 0){
							btn_equip_down.hide(); // 엑셀내리기(장비)버튼 안보이게
						}
					}
				});
			}
		});	


		// 공통코드
		var codeOptions = [ { code : "HCF", compID : "sbx_region_centr"}
		                  , { code : "STD", compID : "sbx_supt_clsf,grd_list:supt_clsf", isAll : true, ignoreUseYn: true, filter : "mngmtItem4 == 'Y'"} // 지원구분
		                  , { code : "SLC", compID : "sbx_supt_team,grd_list:supt_team"} // 지원팀
		                  , { code : "STG", compID : "sbx_supt_work_clsf"} // 지원사업구분
		                  , { code : "STE", compID : "sbx_prcs_state,grd_list:prcs_state"}
		                  , { code : "STC", compID : "grd_list:rst_book_issu"}
		               ];

		com.setCmnCd(codeOptions, function(){				
			//sbx_prcs_state.addItem("STE099", "결의완료", sbx_prcs_state.getItemCount(), true);
			dlt_cmnCd_STE.setRowJSON(dlt_cmnCd_STE.getRowCount(), {comCd : "STE099", comNm : "결의완료", useEx : "Y"}, false);

		});		
	};

	scwin.onpageunload = function() {
	};

	/*
     *  조회
     */
	scwin.search = function() {		
		dma_search.set("clsf", "");

		com.execute(sbm_selectData, {
			isImplExcel : true,
			successCallback : function(e){
				dlt_grd.setJSON(e.responseJSON.sptTecRcptInfoVoList);
				tbx_result.setValue(dlt_grd.getRowCount());

				for(var i=0; i<dlt_grd.getRowCount(); i++){
					switch(dlt_grd.getCellData(i, "prcs_state")){
						case "STE030" : // 지원접수 	
							grd_list.setCellColor(i, "prcs_state", "#FF0000");
							break;
						case "STE040" : // 지원취소
							grd_list.setCellColor(i, "prcs_state", "#C0C0C0");
							break;
						case "STE050" : // 지원완료
							grd_list.setCellColor(i, "prcs_state", "#0080FF");
							break;
						case "STE060" : // 계산서발행
							grd_list.setCellColor(i, "prcs_state", "#008040");
							break;
						default :
							grd_list.setCellColor(i, "prcs_state", "");
							break;
					}
				}
			},
			excelCallback : function(e) {
				// DB 다운로드
				var option = {
					fileName : "외부기술지원(유상).xlsx"
					, hiddenVisible : true
					, useDataFormat : true
					, providerInfo : {
						  service : "sptTecMntService"
						, method : "selectSptTecRcptExcelDown"
						, vo : "sup.tec.vo.SptTecSrcVo"
						, paramMap : dma_search
					}
					, removeColumns : "33,34,35,36,37"
				};
				com.downloadGridExcel(grd_excel, option);
			}
		});
	};

	scwin.rpa_update = function(kolas_no, rpa_result_yn){
		dma_search.set("rpa_result_yn", rpa_result_yn);
		dma_search.set("kolas_no", kolas_no);
		com.execute(sbm_rpa_update, {
			successCallback : function(e){	
				dma_search.set("kolas_no", "");
			}
		});
	};

	/*
     * 사용자 검색
     */
	scwin.getEmpInfo = function(empNo, clsf){
		var data = {
			empno : empNo,
			work_clsf : "HAG010",
			ref_yn : (empNo == "")? "Y" : "N"
		};

		com.pop.open(com.pop.TYPE.HUM, data, function(ret){
			if(clsf == "RESPN"){
				ibx_supt_respn_empno.setValue(ret.empno);
				ibx_supt_respn_nm.setValue(ret.nm);
				dma_search.set("supt_respn_syspayno", ret.syspayno);
			}else if(clsf == "PSN"){
				ibx_rcpt_psn_empno.setValue(ret.empno);
				ibx_rcpt_psn_nm.setValue(ret.nm);
				dma_search.set("rcpt_psn_syspayno", ret.syspayno);
			}
		});
	};

	/*
     * 사용자 초기화
     */
	scwin.clearEmpInfo = function(clsf){
		if(clsf == "RESPN"){
			dma_search.set("supt_respn_syspayno", "");
			ibx_supt_respn_nm.setValue("");
		}else{
			dma_search.set("rcpt_psn_syspayno", "");
			ibx_rcpt_psn_nm.setValue("");
		}
	};

	/*
     *  엑셀내리기(장비) 또는 엑셀 다운로드
     */
	scwin.downLoadExcel = function(clsf, fileNm){
		dma_search.set("clsf", clsf);

		var option = {
			  fileName : fileNm + ".xlsx"
			, hiddenVisible : true
			, useDataFormat : true
			, providerInfo : {
				  service : "sptTecMntService"
				, method : "selectSptTecRcptExcelDown"
				, vo : "sup.tec.vo.SptTecSrcVo"
				, paramMap : dma_search
			}
		};

		if(clsf == "EQUIP"){
			com.downloadGridExcel(grd_excel_equip, option);
		}else{
			com.downloadGridExcel(grd_excel, option);
		}		
	};

	// ------------------------------------ event 함수 ---------------------------------------------//
	/*
     *  조회버튼 클릭 이벤트
     */
	scwin.btn_search_onclick = function(e) {
		scwin.search();
	};

	/*
     *  계정번호 변경 이벤트
     */
	scwin.ibx_accnt_no_onchange = function(info) {
		if (this.getValue() == "") {
			ibx_accnt_no_nm.setValue("");
		}
	};

	/*
     *  계정번호 검색 버튼 클릭 이벤트
     */
	scwin.btn_accnt_no_onclick = function(e) {
		var data={
			accnt_no : ibx_accnt_no.getValue(),
			ref_yn : (ibx_accnt_no.getValue() == "")? "Y" : "N"
		};

		com.pop.open(com.pop.TYPE.ACNNO, data, function(ret){
			ibx_accnt_no.setValue(ret.accnt_no);
			ibx_accnt_rspns_nm.setValue(ret.accnt_rspns_nm);
		});
	};

	/*
     * 엑셀다운로드 버튼 클릭 이벤트
     */
	scwin.btn_excel_onclick = function(e) {
//		if(dlt_grd.getRowCount() == 0) return;
//		
//		var option = {
//			    fileName : "외부기술지원(유상).xlsx"
//			  , useDataFormat : true
//			  , hiddenVisible : true
//		};
//		com.downloadGridExcel(grd_list, option);

		scwin.downLoadExcel("","외부기술지원(유상)");
	};

	/*
     * RPA 버튼 클릭 이벤트
     */
	scwin.btn_rpa_yn_onclick = function(e) {
		var kolasArray = "";
		var rpaResultArray = "";
		for(var i=0;i<grd_list.getTotalRow();i++){
			if(grd_list.getCellData(i, "rpa_result_yn")!=""){
				kolasArray += grd_list.getCellData(i, "kolas_no")+";";
				rpaResultArray += grd_list.getCellData(i, "rpa_result_yn")+";";
			}
		}
		scwin.rpa_update(kolasArray,rpaResultArray);	
	};	

	/*
     * 그리드 클릭 이벤트
     */
	scwin.grd_list_oncellclick = function(row,col,colId) {
		if(colId == "kolas_no"){
			var data ={
				tech_supt_req_no : dlt_grd.getCellData(row, "tech_supt_req_no")
			};
			com.openTabMenu("S306", data, function(retObj){
				if(retObj) scwin.search();
			});	
		}else if(colId == "chk"){
			if(dlt_grd.getCellData(row, "chk") == "Y"){
				var arr = grd_list.getCheckedIndex("chk");
				for(var i=0; i<arr.length; i++){
					if(arr[i] != row){
						dlt_grd.setCellData(arr[i], "chk", "N");
					}
				}		
			}
		}else if(colId == "rpa_result_yn"){
			grd_list.setCellReadOnly(row, col, false);

		}

	};

	/*
     * 지역구분 변경 이벤트
     */
	scwin.sbx_region_centr_onviewchange = function(info) {
		if(ldt_cmnCd_SLC_0.getRowCount() > 0){
			$p.data.remove("ldt_cmnCd_SLC_0");
		}
		var regionCenter = this.getValue();
		if(regionCenter != ""){
			var codeOptions = [ { code : "SLC", compID :"sbx_supt_team", filter : "mngmtItem1 == '" + regionCenter + "'"}];		
			com.setCmnCd(codeOptions, function(){
				sbx_supt_team.setValue("");
			});	
		}else{
			sbx_supt_team.setNodeSet("data:dlt_cmnCd_SLC", "comNm", "comCd");
			sbx_supt_team.setValue("");
		}
	};

	/*
     * 지원책임자 검색 버튼 클릭 이벤트
     */
	scwin.btn_supt_respn_onclick = function(e) {
		scwin.getEmpInfo(ibx_supt_respn_empno.getValue(), "RESPN");
	};

	/*
     * 지원책임자 변경 이벤트
     */
	scwin.ibx_supt_respn_empno_onviewchange = function(info) {
		if(this.getValue() == ""){
			scwin.clearEmpInfo("RESPN");	
		}
	};

	/*
     * 지원담당자 검색 버튼 클릭 이벤트
     */
	scwin.btn_rcpt_psn_onclick = function(e) {
		scwin.getEmpInfo(ibx_rcpt_psn_empno.getValue(), "PSN");
	};

	/*
     * 지원담당자 변경 이벤트
     */
	scwin.ibx_rcpt_psn_empno_onviewchange = function(info) {
		if(this.getValue() == ""){
			scwin.clearEmpInfo("PSN");	
		}
	};

	/*
     * 신규 버튼 클릭 이벤트
     */
	scwin.btn_new_onclick = function(e) {
		com.openTabMenu("S306", {}, function(ret){
			if(ret){
				scwin.search();	
			}
		});
	};

	/*
     * 접수대장 버튼 클릭 이벤트
     */
	scwin.btn_rcptBook_onclick = function(e) {
		var node = "rcpt_ymd_st=" + ica_from.getValue() 
		          +"&rcpt_ymd_ed=" + ica_to.getValue()
		          +"&kolas_no=" + dma_search.get("kolas_no")
		          +"&cmpy_nm_krchar=" + dma_search.get("cmpy_nm_krchar")
		          +"&accnt_no=" + dma_search.get("accnt_no")
		          +"&supt_clsf=" + dma_search.get("supt_clsf")
		          +"&supt_team=" + dma_search.get("supt_team")
		          +"&region_centr=" + dma_search.get("region_centr")
		          +"&prcs_state=" + dma_search.get("prcs_state")
		          +"&rcpt_psn_empno=" + dma_search.get("rcpt_psn_empno")
		          ;
		com.oz.openPopup({
			ozrCode : "S014",      
			param: node
        });
	};

	/*
     * 계약서 버튼 클릭 이벤트
     */
	scwin.btn_cntrct_onclick = function(e) {
		var rows = grd_list.getCheckedIndex("chk");
		if(rows.length == 0){
			alert("계약서를 인쇄할 데이터를 선택하세요.");
			return;
		}

		var node = "tech_supt_req_no=" + dlt_grd.getCellData(rows[0], "tech_supt_req_no");
		com.oz.openPopup({
			ozrCode : (dlt_grd.getCellData(rows[0], "supt_clsf") != "STD030")? "S015" : "S016",      
			param: node
        });
	};

	/*
     * 견적서 버튼 클릭 이벤트
     */
	scwin.btn_cntrct2_onclick = function(e) {
		var rows = grd_list.getCheckedIndex("chk");
		if(rows.length == 0){
			alert("견적서를 인쇄할 데이터를 선택하세요.");
			return;
		}

		var node = "kolas_no=" + dlt_grd.getCellData(rows[0], "kolas_no");
		com.oz.openPopup({
			ozrCode : (dlt_grd.getCellData(rows[0], "supt_clsf") != "STD030")? "S021" : "S021",      
			param: node
        });
	};

	/*
     * 접수증 버튼 클릭 이벤트
     */
	scwin.btn_rcptNote_onclick = function(e) {
		var rows = grd_list.getCheckedIndex("chk");
		if(rows.length == 0){
			alert("접수증을 인쇄할 데이터를 선택하세요.");
			return;
		}

		var node = "tech_supt_req_no=" + dlt_grd.getCellData(rows[0], "tech_supt_req_no")
		         + "&cust_no=" + dlt_grd.getCellData(rows[0], "cust_no")
		         + "&prcs_state=" + dlt_grd.getCellData(rows[0], "prcs_state")
		         ;
		com.oz.openPopup({
			ozrCode : (dlt_grd.getCellData(rows[0], "supt_clsf") != "STD030")? "S017" : "S018",      
			param: node
        });
	};

	/*
     * 청구공문 버튼 클릭 이벤트
     */
	scwin.btn_offclDoc_onclick = function(e) {
		var rows = grd_list.getCheckedIndex("chk");
		if(rows.length == 0){
			alert("청구서를 발행할 데이터를 선택하세요.");
			return;
		}

		var node = "tech_supt_req_no=" + dlt_grd.getCellData(rows[0], "tech_supt_req_no")
		         + "&cust_no=" + dlt_grd.getCellData(rows[0], "cust_no")
		         + "&prcs_state=" + dlt_grd.getCellData(rows[0], "prcs_state")
		         ;

		var reportArr = [];
		// 청구서
		reportArr.push({
			ozrCode : "S019",
			ozrPath : "/sup/tec/SptTecReqMnt07.ozr",
			odiNm : "SptTecReqMnt07",
			displayNm : "청구서",
			param: "tech_supt_req_no=" + dlt_grd.getCellData(rows[0], "tech_supt_req_no")
		});
		// 접수증
		reportArr.push({
			ozrCode : (dlt_grd.getCellData(rows[0], "supt_clsf") != "STD030")? "S017" : "S018",
			ozrPath : (dlt_grd.getCellData(rows[0], "supt_clsf") != "STD030")? "/sup/tec/SptTecReqMnt01.ozr" : "/sup/tec/SptTecReqMnt06.ozr",
			odiNm : (dlt_grd.getCellData(rows[0], "supt_clsf") != "STD030")? "SptTecRcptInfo01" : "SptTecRcptInfo06",
			displayNm : "접수증",
			param: node
		});
        // 계약서
        reportArr.push({
			ozrCode : (dlt_grd.getCellData(rows[0], "supt_clsf") != "STD030")? "S015" : "S016",
			ozrPath : (dlt_grd.getCellData(rows[0], "supt_clsf") != "STD030")? "/sup/tec/SptTecReqMnt02.ozr" : "/sup/tec/SptTecReqMnt03.ozr",
			odiNm : (dlt_grd.getCellData(rows[0], "supt_clsf") != "STD030")? "SptTecRcptInfo02" : "SptTecRcptReg03",
			displayNm : "계약서",
			param: node
		});

		// 견적서
        reportArr.push({
			ozrCode : "S021",
			ozrPath : "/sup/tec/SptTecRcpEstimate01.ozr",
			odiNm : "SptTecRcpEstimate01",
			displayNm : "견적서",
			param: "kolas_no=" + dlt_grd.getCellData(rows[0], "kolas_no")
		});


        // 오즈 뷰어 팝업 호출
        com.oz.openPopup(reportArr, true);		
	};

	/*
     * 접수번호 변경 이벤트
     */
	scwin.ibx_kolas_no_onviewchange = function(info) {

		if(this.getValue() != ""){
			ica_from.setValue("");
		}else{
			ica_from.setValue(com.addDate(com.getServerDateTime(), -30));
		}
	};

	/*
     * 엑셀내리기(장비) 버튼 클릭 이벤트
     */
	scwin.btn_equip_down_onclick = function(e) {		
		scwin.downLoadExcel("EQUIP", "기술지원접수결과(장비)");
	};
