head.script:
	/*********************************************************************************
 	 * 화면 설명 : 외부기술지원(유상) 내역등록
 	 * 변경 이력 : 2023-06-07	이영희	최초 작성
 	            2023.11.08. 기술내역 저장 delete 후 insert 되도록 변경(비고를 seq=1로 찾는 쿼리 많아서)
 	 * 서비스아이디 : S306
 	 ********************************************************************************/  
 	 scwin.data={today : com.getServerDateTime(),
 	 			 saveFlag : false,
 	 			 roleChk : false,
 	 			 isCopy : false, // 복사여부
 	 			 mgmt_yn : "N", // 관리자 여부 
 	 			 mngrSave : false, // 관리자 저장버튼 클릭 여부
 	 			 cardPayYn : true // 카드결제 가능 여부
 	 };

 	/*
     * 페이지 열기
     */   
	scwin.onpageload = function() {
		// 카드결제 허용가능기간 체크
		if(scwin.data.today.substring(4, 8) >= "1220" || scwin.data.today.substring(4, 8) <= "0101"){
			//const values = await com.getExceptAwait('XEK056', 'S306');

			var fromDate = values[0];
			var toDate = values[1];

			var toDay = scwin.data.today;
			if(toDay >= fromDate && toDay <= toDate){
				scwin.data.cardPayYn = false;
			}	
		}

		// 초기데이터 셋팅
		for(var i=0; i<24; i++){
			dlt_time.setRowJSON(i, {"time" : (i<10)? ("0"+i) : i});	
		}

		btn_conf.hide(); // 확정
		btn_mngSave.hide(); // 관리자저장
		btn_techSave.hide(); // 기술내역저장

		//btn_del.hide();
		ibx_resid_no.hide();
		// 상담정보(사용하지 않을 것 같아서 숨김처리)
		grp_cunsl.hide();
		grp_cunsl_title.hide();
		//acr_open.hide(); // 상담정보 오픈

		btn_card_cancl.hide();
		btn_sms.hide();
		btn_card_app.hide();
		// 2023.07.07. 기업지원성장실 박혜리 통화(거래명세서 버튼만 사용하고 있다고 해서 숨김처리 - display:none), 거래명세서 버튼 추가
		// 2023.10.19. SMS결제 후 카드처리내역 확인 후 저장위해 필요 다시 보이게 수정
		btn_card_prcs.hide(); 

		var printObj = document.querySelectorAll('.print');
		printObj.forEach(function(o){
			o.style.display ="none";
		});	

		switch1.setSelectedID("case1");
		case1.show("");
		case2.hide();


		// 권한
		com.biz.checkRole({serviceId : "S306", roleCodes : com.getLoginInfo("roleCode")}, function(e){
			if(e.isRoleYn){ // 관리권한 보유시
				scwin.data.roleChk = true;

				scwin.data.mgmt_yn = "Y";
			}else{
				dma_search.set("syspayno", com.getLoginInfo("sn"));

				com.execute(sbm_selectChrgCnt, { // 실험실 담당자여부 조회
					successCallback : function(e){	
						if(WebSquare.util.parseInt(e.responseJSON.elData.cnt, 0) > 0){
							scwin.data.mgmt_yn = "Y";
						}
					}
				});	
			}
		});

		// enterkey event
		com.setEnterKeyEvent(grp_accnt_no, scwin.btn_accnt_no_onclick);
		com.setEnterKeyEvent(grp_rcpt_psn, scwin.btn_rcpt_psn_onclick);
		com.setEnterKeyEvent(grp_supt_respn, scwin.btn_supt_respn_onclick);
		com.setEnterKeyEvent(grp_vend, scwin.btn_vend_onclick);

		var param = com.getParameter();
		if(param.openerTabId != undefined){
			scwin.data.openerTabId = param.openerTabId;
		}

		if(param.param4 == "apr"){
			dma_search.set("tech_supt_req_no", param.req_no);
		}else{
			dma_search.set("tech_supt_req_no", param.tech_supt_req_no);
		}

		var uid = com.getLoginInfo("uid");
		var deptCode = com.getLoginInfo("deptCode");

		if(uid == "rpabot1" || uid == "rpabot2" || uid == "rpabot3" || deptCode == "H650"){
			RPA_TH.visible(true);
			RPA_TD.visible(true);
		}
		else{
			RPA_TH.visible(false);
			RPA_TD.visible(false);
		}

		// 공통코드
		var codeOptions = [ { code : "FAA", compID : "sbx_depst_bank"}
						  , { code : "HCF", compID : "sbx_region_centr"}
		                  //, { code : "SLC", compID : "sbx_supt_team"} // 지원팀
		                  , { code : "STG", compID : "sbx_supt_work_clsf"} // 지원사업구분
		                  //, { code : "STC", compID : "rdo_rst_book_issu"}
		                  , { code : "SCE", compID : "rdo_techsup_req_clsf"}
		                  , { code : "STY", compID : "rpa_evid_clsf"}
		               ];
		if(dma_search.get("tech_supt_req_no") != ""){
			codeOptions.push({ code : "STD", compID : "sbx_supt_clsf", isAll : true, ignoreUseYn: true, filter : "mngmtItem4 == 'Y'"});
		}else{
			codeOptions.push({ code : "STD", compID : "sbx_supt_clsf", filter : "mngmtItem4 == 'Y'"});
		}

		com.setCmnCd(codeOptions, function(){	
		});			

		// 산업기술분류(대분류) 조회
		dma_search.set("clsf", "SUP001");
		com.execute(sbm_selectTecBigClsf, {
			successCallback : function(e) { // 성공 콜백
				dlt_tecBigClsf.setJSON(e.responseJSON.resIndTeccVoList);

				// 산업기술분류(중분류) 조회
				com.execute(sbm_selectTecMidClsf, {
					successCallback : function(e) { // 성공 콜백
						dlt_tecMidClsf.setJSON(e.responseJSON.resIndTeccVoList);

						// 산업기술분류(소분류) 조회
						com.execute(sbm_selectTecSmlClsf, {
							successCallback : function(e) { // 성공 콜백
								dlt_tecSmlClsf.setJSON(e.responseJSON.resIndTeccVoList);

								if(dma_search.get("tech_supt_req_no") != ""){
									scwin.search();	
								}else{
									scwin.init(param);
								}
							}
						});	
					}
				});	
			}
		});			
	};

	scwin.onpageunload = function() {

	};

	/*
     * 기술지원신청번호가 없을 경우 초기화
     */
	scwin.init = function(param){		
		ica_rcpt_ymd.setValue(scwin.data.today); // 기술지원확정일자
		ica_cunsl_rcpt_ymd.setValue(scwin.data.today); // 상담접수일자
		rdo_proof_yn.setValue("STO010"); // 세금계산서

		dma_detl.set("regst_empno", com.getLoginInfo("empno"));
		dma_detl.set("regst_nm", com.getLoginInfo("cn"));
		dma_detl.set("rst_book_yn", "N"); // 성적서 발행여부
		dma_detl.set("prcs_state", "STE030"); // 처리상태
		dma_detl.set("cud_type", "C");

		if(com.getLoginInfo("deptCode") != ""){
			com.pop.open(com.pop.TYPE.DEPT, {dept_cd : com.getLoginInfo("deptCode")}, function(ret){
				sbx_region_centr.setValue(ret.dept_typ);
				sbx_region_centr.trigger("viewchange");
			});	
		}	

		// 제우스 장비 예약현황 상세정보를 통한 기술지원 내역 불러오기 (접수버튼 클릭)
		if(!scwin.data.isCopy && param.zeusCls != undefined && param.zeusCls != ""){
			dma_search.set("resvno", param.resvNo); // Z-23-018077

			com.execute(sbm_selectZeusData, {
				successCallback : function(e){
					dma_detl.set("resvno", param.resvNo);

					$p.data.remove("ldt_cmnCd_SLC_0");
					var codeOptions = [ { code : "SLC", compID :"sbx_supt_team", filter : "mngmtItem1 == '" + dma_detl.get("region_centr") + "'"}];		
					com.setCmnCd(codeOptions, function(){
						sbx_supt_team.setValue(dma_detl.get("supt_team"));
					});	
				}
			});
		}
	};

	/*
     * 조회
     */ 
	scwin.search = function(){
		com.execute(sbm_selectData, {
			successCallback : function(e){
				// STE040 지원취소일 경우에만 체크
				cbx_prcs_state.setValue(dma_detl.get("prcs_state"));
				var prcs_state = dma_detl.get("prcs_state");
				//const values = await com.getExceptAwait('XEK055', 'S306');

				/*
				if(values.indexOf(prcs_state) > 0){
					rpa_yn.setDisabled("true");
					rpa_evid_clsf.setDisabled("true");
					rpa_result_yn.setDisabled("true");			
				}
				*/

				$p.data.remove("ldt_cmnCd_SLC_0");
				var codeOptions = [ { code : "SLC", compID :"sbx_supt_team", filter : "mngmtItem1 == '" + dma_detl.get("region_centr") + "'"}				                    
		                         ];		
				com.setCmnCd(codeOptions, function(){
					let refDataComp = sbx_supt_team.modelControl.getDataComp();
        			refDataComp.setFilterCondition(
	        		refDataComp.getFilterCondition()
		        		+ " || (comCd == 'SLC090')"
		    		);

		    		sbx_supt_team.setValue(dma_detl.get("supt_team"));

				});

				cbx_cmpy_typ.setValue(dma_cust.get("cmpy_typ"));
				if(cbx_cmpy_typ.getValue() == "RZB006"){
					ibx_resid_no.setValue(dma_cust.get("bsns_psn_regst_no")); // 개인
				}else{
					ibx_bsns_psn_regst_no.setValue(dma_cust.get("bsns_psn_regst_no"));
				}

				var sn = com.getLoginInfo("sn");
				var empno = com.getLoginInfo("empno");
				if(sn == dma_detl.get("cunsl_psn_syspayno") || sn == dma_detl.get("rcpt_psn_syspayno") || empno == dma_detl.get("regst_empno") || empno == dma_detl.get("supt_respn_empno")){
					scwin.data.mgmt_yn = "Y";
				}

				// 버튼 및 폼제어
				scwin.setForm();
			}
		});
	};

	/*
     * 버튼 및 폼제어
     */
	scwin.setForm = function(){
		// 기술지원 실적
		sbx_supt_clsf.trigger("viewchange");
		//grp_vend.setDisabled(true);
		btn_copy.setDisabled(false);

		if(scwin.data.mgmt_yn == "Y"){
			btn_bill.setDisabled(false); // 계산서 발행
			btn_rev_issu.setDisabled(false); // 역발행
			btn_card_app.setDisabled(false); // 카드결제
			btn_sms.setDisabled(false);
		}

		if(/1005102/.test(com.getLoginInfo("roleCode"))){
			cbx_prcs_state.setDisabled(false); // 지업지원 수행취소
		}


		if((dma_detl.get("prcs_state") >= "STE060" && dma_detl.get("prcs_state") != "STE065") || dma_detl.get("card_appl_no") != ""){
			btn_save.setDisabled(true);
			btn_del.setDisabled(true);		

			if(scwin.data.roleChk){ // 관리자 권한인 경우
				btn_mngSave.show("");
			}else{
				btn_techSave.show(""); // 기술내역 저장
			}

			grp_accnt_no.setDisabled(true);
			btn_rslt_del.setDisabled(true);		
			rdo_proof_yn.setDisabled(true);	

//			var workObj = document.querySelectorAll('.bt_work');
//			workObj.forEach(function(o){
//				var comp = com.getComponent(o.id);
//				if (comp) comp.setDisabled(true);
//			});	

//			grp_btn_work.setDisabled(true);

			// 기술지원 실적내역 그리드 읽기만 가능하도록
			grd_rslt.setColumnReadOnly(0, true);
			for(var i=2; i<10; i++){
				grd_rslt.setColumnReadOnly(i, true);
			}
		}else{
			btn_save.setDisabled(false);
			btn_del.setDisabled(false);

			grp_accnt_no.setDisabled(false);
			btn_rslt_del.setDisabled(false);		
			rdo_proof_yn.setDisabled(false);	
			btn_sms.setDisabled(false);

			// 바우처인 경우
			if(/STG050|STG060/.test(dma_detl.get("supt_work_clsf"))){			
				if(dma_detl.get("decide_yn") != "Y"){
					btn_conf.show("");
				}
			}
		}

		if(dma_detl.get("prcs_state") == "STE040"){
			btn_save.setDisabled(true);
			btn_del.setDisabled(false);			

			var printObj = document.querySelectorAll('.print');
			printObj.forEach(function(o){
				o.style.display ="none";
			});	

			var workObj = document.querySelectorAll('.bt_work');
			workObj.forEach(function(o){
				o.style.display ="none";
			});	
		}else{
			// 인쇄버튼
			var printObj = document.querySelectorAll('.print');
			printObj.forEach(function(o){
				o.style.display ="";
			});	

			if(dma_detl.get("bill_no") != ""){
				rdo_proof_yn.setDisabled(true);	
				btn_tran_spec.setDisabled(false); // 거래명세서 버튼		

			}else if(dma_detl.get("card_appl_no") != ""){
				rdo_proof_yn.setDisabled(true);	
				btn_tran_spec.setDisabled(false); // 거래명세서 버튼				
			}
		}		

		if(dma_detl.get("rst_book_yn") == "Y"){ // 성적서가 발행된 경우 저장 불가
			btn_save.setDisabled(true);
		}

		if(dma_detl.get("supt_clsf") != "STD030"){
			for(var i=0; i<dlt_rslt.getRowCount(); i++){
				if(dlt_rslt.getCellData(i, "rst_req_cnt") > '0'){
					grd_rslt.setCellReadOnly(i, "rst_book_treat_nm", true);
				}else{
					grd_rslt.setCellReadOnly(i, "rst_book_treat_nm", false);
				}	
			}	

			grp_rslt_help.show("");
			grd_equip.setColumnVisible("equip_use_start_hour", true);
			grd_equip.setColumnVisible("equip_use_start_minute", true);
			grd_equip.setColumnVisible("equip_use_cls_hour", true);
			grd_equip.setColumnVisible("equip_use_cls_minute", true);
			grd_equip.setColumnVisible("treat_qty", true);
		}else{
			grp_rslt_help.hide();

			grd_equip.setColumnVisible("equip_use_start_hour", false);
			grd_equip.setColumnVisible("equip_use_start_minute", false);
			grd_equip.setColumnVisible("equip_use_cls_hour", false);
			grd_equip.setColumnVisible("equip_use_cls_minute", false);
			grd_equip.setColumnVisible("treat_qty", false);
		}

		// 어디서 호출하는지 알수가 없음 - 주석처리(필요시 주석해제)
//		if(com.getParameter("tmpScr") == "Y"){
//			grp_button.hide();
//			grp_below_button.hide();
//		}
	};

	/*
     * 사용자 검색
     */
	scwin.getEmpInfo = function(empNo, clsf){
		var data = {
			empno : empNo,
			work_clsf : "HAG010",
			ref_yn : (empNo == "")? "Y" : "N"
		};

		com.pop.open(com.pop.TYPE.HUM, data, function(ret){
			if(clsf == "RESPN"){
				ibx_supt_respn_empno.setValue(ret.empno);
				ibx_supt_respn_nm.setValue(ret.nm);
				dma_detl.set("supt_respn_syspayno", ret.syspayno);
			}else if(clsf == "PSN"){
				ibx_rcpt_psn_empno.setValue(ret.empno);
				ibx_rcpt_psn_nm.setValue(ret.nm);
				dma_detl.set("rcpt_psn_syspayno", ret.syspayno);

				sbx_region_centr.setValue(ret.dept_typ);
			}
		});
	};

	/*
     * 사용자 초기화
     */
	scwin.clearEmpInfo = function(clsf){
		if(clsf == "RESPN"){
			ibx_supt_respn_nm.setValue("");
			dma_detl.set("supt_respn_syspayno", "");
		}else{
			ibx_rcpt_psn_nm.setValue("");
			dma_detl.set("rcpt_psn_syspayno", "");
		}
	};

	/*
     * 파일 다운로드
     */
	scwin.downFileinfo = function downFileinfo(file_nm,file_no){
		var url = "/SvcAttachDown.do?fileName=web_folder/bizdoc/sup/"+file_no+"&realName="+encodeURIComponent(file_nm)+"&bizKind=OLD";		
		window.open(url, '_blank').focus();
	};

	/*
     * 업체검색
     */
	scwin.getVendInfo = function(vendNm, clsf){
		var data={
			vend_abbr : vendNm,
			ref_yn : "Y",
			biz_clsf : "S"
		};

		if(clsf == "CUST"){
			data.regst_syspayno = com.getLoginInfo("sn");
		}

		com.pop.open(com.pop.TYPE.VEND, data, function(ret){
			if(ret){
				if(clsf == "CUST"){
					dma_detl.set("cust_no", ret.vend_cd);
					dma_cust.set("cust_no", ret.vend_cd);
					dma_cust.set("cmpy_nm_krchar", ret.vend_abbr);
					dma_cust.set("reprs_psn", ret.reprs_psn_nm);
					dma_cust.set("bsns_psn_regst_no", ret.bsns_psn_regst_no);

					var bsns_psn_regst_no =  ret.bsns_psn_regst_no.replaceAll("-","").trim();
					ibx_bsns_psn_regst_no.setValue(bsns_psn_regst_no);
					dma_cust.set("cmpy_reprs_tel", ret.tel_no);
					dma_cust.set("cmpy_fax_no", ret.fax);
					dma_cust.set("cmpy_post_no", ret.postmt_no);
					dma_cust.set("cmpy_addr", ret.main_addr);
					dma_cust.set("cmpy_addr_detls", ret.detls_addr);
					dma_cust.set("bizcatg", ret.bizcatg); // 업태
					dma_cust.set("item", ret.biztyp); // 업종
					if(ret.supt_clsf == ""){
						dma_cust.set("supt_clsf", "00000000110000000000");
					}else{
						dma_cust.set("supt_clsf", ret.supt_clsf);
					}

					for(var i=0; i<dlt_rslt.getRowCount(); i++){
						dlt_rslt.setCellData(i, "cust_no", 	ret.vend_cd);
					}

					for(var i=0; i<dlt_equip.getRowCount(); i++){
						dlt_equip.setCellData(i, "cust_no", ret.vend_cd);
					}

					if(ret.vend_cd != ""){
						dma_search.set("vend_cd", ret.vend_cd);
						com.execute(sbm_selectBill, {
							successCallback : function(e){
								if(e.responseJSON.sptTecRcptBillVoList != undefined){
									var options = {
										id : "SupChrgBillChkPop",
										popupName : "청구계산서 발행내역",
										width : 450, height: 400
									};
									com.openPopup("/ui/sup/pop/SUP_POP_S306_01P.xml", options, {listData : e.responseJSON.sptTecRcptBillVoList});
								}
							}
						});
					}
				}else{
					dma_detl.set("rst_book_issu_cust_no", ret.vend_cd);
					dma_detl.set("rst_book_issu_cust_nm", ret.vend_abbr);
				}
			}
		});
	};

	/*
     * 세액 및 확정금액 구하기
     */ 
	scwin.getTaxAmt = function(row, suplyValue){
		var taxAmt = 0;
		if(!/STG050|STG060/.test(sbx_supt_work_clsf.getValue())){
			taxAmt = Math.ceil(suplyValue/10);
		}
		dlt_rslt.setCellData(row, "decsn_tax_amt", taxAmt);
		dlt_rslt.setCellData(row, "decsn_amt", suplyValue + taxAmt);
	};

	/*
     * 할인율 재계산
     */ 
	scwin.getDiscntRt = function(){
		var totAmt = grd_rslt.getFooterData("req_amt_sum");
		var totSuplyValue = grd_rslt.getFooterData("decsn_suply_value_sum");
		if(totAmt > 0){
			var discntRt = Math.round(100-(totSuplyValue * 100 / totAmt),2);
			ibx_discnt_rt.setValue(discntRt);
		}
	};

	/*
     * 실적등록 금액 재계산
     */
	scwin.setDecsnAmt = function(row, col, index){
		var obj = (index == "01")? grd_rslt : grd_revsn_rslt; // index 01-실적등록, 02 교정실적등록
		var colId = obj.getColumnID(col);

		var discntRt = WebSquare.util.parseFloat(dma_detl.get("discnt_rt"), 0);

		if(/qty|unit_price/.test(colId)){
			var qty = WebSquare.util.parseInt(dlt_rslt.getCellData(row, "qty"), 0);
			var unitPrice = WebSquare.util.parseInt(dlt_rslt.getCellData(row, "unit_price"), 0);
			var reqAmt = unitPrice * qty;

			if(index == "02"){
				var retroAmt = com.parseInt(reqAmt * 0.1);
				dlt_rslt.setCellData(row, "retro_fee", retroAmt); // 소급료
				var suplyValue = Math.round(reqAmt * (100 - discntRt)/100 + retroAmt);
			}else{
				var suplyValue = Math.round(reqAmt * (100 - discntRt)/100);
			}

			dlt_rslt.setCellData(row, "req_amt", reqAmt);
			dlt_rslt.setCellData(row, "decsn_suply_value", suplyValue);
			// 세액 및 확정금액 구하기
			scwin.getTaxAmt(row, suplyValue);

		}else if(/req_amt/.test(colId)){
			var reqAmt = WebSquare.util.parseInt(dlt_rslt.getCellData(row, "req_amt"), 0);
			if(index == "02"){
				var retroAmt = com.parseInt(reqAmt * 0.1);
				dlt_rslt.setCellData(row, "retro_fee", retroAmt); // 소급료
				var suplyValue = Math.round(reqAmt * (100 - discntRt)/100 + retroAmt);
			}else{
				var suplyValue = Math.round(reqAmt * (100 - discntRt)/100);
			}

			dlt_rslt.setCellData(row, "decsn_suply_value", suplyValue);
			// 세액 및 확정금액 구하기
			scwin.getTaxAmt(row, suplyValue);

		}else if(/decsn_suply_value/.test(colId)){
			var suplyValue = WebSquare.util.parseInt(dlt_rslt.getCellData(row, "decsn_suply_value"), 0);
			// 세액 및 확정금액 구하기
			scwin.getTaxAmt(row, suplyValue);

		}else if(/decsn_tax_amt/.test(colId)){
			var taxAmt = WebSquare.util.parseInt(dlt_rslt.getCellData(row, "decsn_tax_amt"), 0);
			dlt_rslt.setCellData(row, "decsn_amt", dlt_rslt.getCellData(row, "decsn_suply_value") + taxAmt);

		}else if(/decsn_amt/.test(colId)){
			var decsnAmt = WebSquare.util.parseInt(dlt_rslt.getCellData(row, "decsn_amt"), 0);
			var suplyValue 	= Math.floor(decsnAmt*10/11);
			dlt_rslt.setCellData(row, "decsn_suply_value", suplyValue);
			dlt_rslt.setCellData(row, "decsn_tax_amt", decsnAmt - suplyValue);

		}else if(/treat_qty/.test(colId)){
			dma_detl.set("treat_qty", obj.getFooterData("treat_qty_sum"));

		}else if(/retro_fee/.test(colId)){
			var retroAmt = WebSquare.util.parseInt(dlt_rslt.getCellData(row, "retro_fee"), 0);
			var suplyValue = Math.round(dlt_rslt.getCellData(row, "req_amt") * (100 - discntRt)/100 + retroAmt);

			dlt_rslt.setCellData(row, "decsn_suply_value", suplyValue);
			// 세액 및 확정금액 구하기
			scwin.getTaxAmt(row, suplyValue);			
		}

		if(/decsn_suply_value|decsn_amt|retro_fee/.test(colId)){
			// 할인율 재계산
			scwin.getDiscntRt();
		}	

		if(colId != "chk"){
			// 총액계산
			scwin.setTotalAmt(index);
		}
	};

	/*
     * 총액 재계산
     */
	scwin.setTotalAmt = function(index){
		if(index == "01"){
			dma_detl.set("req_amt", grd_rslt.getFooterData("req_amt_sum"));
			dma_detl.set("decsn_suply_value", grd_rslt.getFooterData("decsn_suply_value_sum"));
			dma_detl.set("decsn_tax_amt", grd_rslt.getFooterData("decsn_tax_amt_sum"));
			dma_detl.set("decsn_amt", grd_rslt.getFooterData("decsn_amt_sum"));
			dma_detl.set("treat_qty", grd_rslt.getFooterData("treat_qty_sum"));
		}else{ // 교정
			dma_detl.set("req_amt", grd_revsn_rslt.getFooterData("decsn_suply_value_sum"));
			dma_detl.set("decsn_suply_value", grd_revsn_rslt.getFooterData("decsn_suply_value_sum"));
			dma_detl.set("decsn_tax_amt", grd_revsn_rslt.getFooterData("decsn_tax_amt_sum"));
			dma_detl.set("decsn_amt", grd_revsn_rslt.getFooterData("decsn_amt_sum"));
			dma_detl.set("treat_qty", grd_revsn_rslt.getFooterData("treat_qty_sum"));
		}
	};

	/*
     * 그리드 행추가
     */ 
	scwin.addGridRow = function(clsf){
		if(clsf == "EQUIP"){
			var row = com.addRow(grd_equip);
			dlt_equip.setCellData(row, "cud_type", "C");
			dlt_equip.setCellData(row, "equip_use_start_daytm", scwin.data.today);
			dlt_equip.setCellData(row, "equip_use_cls_daytm", scwin.data.today);
		}else{
			if(clsf == "RSLT"){
				var row = com.addRow(grd_rslt);
			}else{
				var row = com.addRow(grd_revsn_rslt);
			}

			dlt_rslt.setCellData(row, "cud_type", "C");

			if(/STC010|STC020/.test(rdo_rst_book_issu.getValue())){
				dlt_rslt.setCellData(row, "rst_book_issu", "1");
			}
		}
	};

	/*
     * 그리드 행삭제
     */ 
	scwin.deleteGridRow = function(clsf){
		if(/RSLT|RESVN/.test(clsf)){
			var obj = (clsf =="RSLT")? grd_rslt : grd_revsn_rslt; 

			var rowCnt = dlt_rslt.getRowCount();
			var rows = obj.getCheckedIndex("chk");
			if(rows.length == 0){
				alert("삭제할 행을 선택해 주십시오");
				return;
			}

			if(rows.length == dlt_rslt.getRowCount()){
				if(!confirm("기술지원내역을 모두 삭제하시면 \n등록된 장비정보도 함께 삭제됩니다. \n삭제하시겠습니까?")) return;				
			}

			rows.reverse().forEach(function(row) {
				if(dlt_rslt.getRowStatus(row) == "C" || dlt_rslt.getCellData(row, "cud_type") == "C"){
					dlt_rslt.removeRow(row);
					dlt_rslt.reform();
				}else{
					if(dma_detl.get("bill_no") != "" && dma_detl.get("card_appl_no") != ""){
						if(dlt_rslt.getCellData(row, "unit_price") > '0'){
							alert("단가가 있을때는 삭제 할 수 없습니다.");
							return;
						}

						if(dlt_rslt.getCellData(row, "rst_req_cnt") > '0'){
							alert("성적서 발급되어 삭제 할 수 없습니다.");
							return;
						}
					}
					dlt_rslt.setCellData(row, "cud_type", "D");
					dlt_rslt.removeRow(row);
				}
			});

			if(rows.length == rowCnt){
				for(var i=dlt_equip.getRowCount()-1; i>=0; i--){
					if(dlt_equip.getRowStatus(i) == "C" || dlt_equip.getCellData(i, "cud_type") == "C"){
						dlt_equip.removeRow(i);
						dlt_equip.reform();
					}else{
						dlt_equip.setCellData(i, "cud_type", "D");
						dlt_equip.removeRow(i);
					}
				}

				obj.setHeaderValue("hChk", "0");
			}			

			// 총액 재계산
			scwin.setTotalAmt((clsf == "RSLT")? "01" : "02");

		}else if(clsf == "EQUIP"){
			var rows = grd_equip.getCheckedIndex("chk");
			if(rows.length == 0){
				alert("삭제할 행을 선택해 주십시오");
				return;
			}

			rows.reverse().forEach(function(row) {
				if(dlt_equip.getRowStatus(row) == "C" || dlt_equip.getCellData(row, "cud_type") == "C"){
					dlt_equip.removeRow(row);
					dlt_equip.reform();
				}else{
					dlt_equip.setCellData(row, "cud_type", "D");
					dlt_equip.removeRow(row);
				}
			});
		}
	};

	/*
     * 그리드 행복사
     */
	scwin.copyGridRow = function(clsf){
		if(/RSLT|RESVN/.test(clsf)){
			var obj = (clsf =="RSLT")? grd_rslt : grd_revsn_rslt; 
			var rows = obj.getCheckedIndex("chk");
			if(rows.length == 0){
				alert("복사할 행을 선택해 주십시오");
				return;
			}

			var rowCnt = dlt_rslt.getRowCount();
			rows.forEach(function(row) {
				dlt_rslt.setCellData(row, "chk", "0");

				dlt_rslt.setRowJSON(rowCnt, dlt_rslt.getRowJSON(row));
				dlt_rslt.setCellData(rowCnt, "cud_type", "C");
				dlt_rslt.setCellData(rowCnt, "seq", "");

				rowCnt++;
			});

			// 총액 재계산
			scwin.setTotalAmt((clsf == "RSLT")? "01" : "02");

		}else{
			var rows = grd_equip.getCheckedIndex("chk");
			if(rows.length == 0){
				alert("복사할 행을 선택해 주십시오");
				return;
			}	

			var rowCnt = dlt_equip.getRowCount();
			rows.forEach(function(row) {
				dlt_equip.setCellData(row, "chk", "0");

				dlt_equip.setRowJSON(rowCnt, dlt_equip.getRowJSON(row));
				dlt_equip.setCellData(rowCnt, "cud_type", "C");
				dlt_equip.setCellData(rowCnt, "seq", "");
				dlt_equip.setCellData(rowCnt, "equip_cd", "");
				dlt_equip.setCellData(rowCnt, "aset_nm", "");

				rowCnt++;
			});
		}
	};

	/*
     * 임시저장전 체크
     */
	scwin.preTmpSave = function(){
		dma_detl.set("supt_team", sbx_supt_team.getValue());

		var validData = [
			{dataObj : dma_detl,
			 valInfoArr :[
					    { id : "rcpt_ymd", mandatory : true}
					   ,{ id : "region_centr", mandatory : true } 
					   ,{ id : "rcpt_psn_syspayno", mandatory : true }
					   ,{ id : "supt_respn_syspayno", mandatory : true}
					   ,{ id : "supt_clsf", mandatory : true}
					   ,{ id : "supt_team", mandatory : true}
					   ,{ id : "accnt_no", mandatory : true}
					   ,{ id : "accnt_respn_psn", mandatory : true } 
					   ,{ id : "rst_book_issu", mandatory : true } 
					   ,{ id : "supt_work_clsf", mandatory : true}
				   ],
				isCheckModified : false
			},
			{dataObj : dma_cust,
			 valInfoArr :[
					    { id : "cmpy_nm_krchar", mandatory : true}
					   ,{ id : "reprs_psn", mandatory : true}
					   ,{ id : "bsns_psn_regst_no", mandatory : true}
					   ,{ id : "cmpy_reprs_tel", mandatory : true}
					   ,{ id : "cmpy_post_no", mandatory : true}
					   ,{ id : "cmpy_addr", mandatory : true}
					   ,{ id : "bizcatg", mandatory : true}
					   ,{ id : "item", mandatory : true}
				   ],
				isCheckModified : false
			}
		];

		if (!com.validateData(validData)) return false;
		return true;
	};

	/*
     * 저장전 체크
     */ 
	scwin.preSave = function(){		
		if(!scwin.preTmpSave()) return false;

		var validData =[{ id : "depst_bankaccnt_no", mandatory : true}
					   ,{ id : "depst_bank_cd", mandatory : true}
					   ,{ id : "treat_prcs", mandatory : true}
					   ,{ id : "use_usg", mandatory : true}
					   ,{ id : "cunsl_req_psn_email", mandatory : true}
					   ,{ id : "complt_schdl_ymd", mandatory : true}
		];

		// 기술지원분류가 '교정'일 경우
		if(sbx_supt_clsf.getValue() == "STD030"){
			validData.push({ id : "revsn_cycle", mandatory : true});
			validData.push({ id : "revsn_plc", mandatory : true});
		}else{
			if(dma_detl.get("decsn_amt") != grd_rslt.getFooterData("decsn_amt_sum")){
				alert("총액이 기술지원내역등록의 확정총액의 합과 일치하지 않습니다.");
				return false;	
			}
		}

		if(cbx_rst_book_issu_info.getValue() == "Y"){
			validData.push({ id : "rst_book_issu_cust_no", mandatory : true});
		}

		// 성적서발행인 경우 시험방법 필수 추가 2019-11-22
		if(rdo_rst_book_issu.getValue() != "STC030"){
			validData.push({ id : "test_way_and_unusual_item", mandatory : true});
		}

		if(WebSquare.util.parseFloat(ibx_discnt_rt.getValue(), 0) != 0 && dma_detl.get("rmk").trim() == ""){
			alert ("할인율이 적용 될 경우엔 비고를 필수로 입력해 주시기 바랍니다");
			return false;
		}

		if(Number(ibx_discnt_rt.getValue()) < 100){
			if(com.parseInt(dma_detl.get("decsn_suply_value")) == 0){
				alert("공급가액이 0원입니다.");
				return false;
			}

			if(!/STG050|STG060/.test(sbx_supt_work_clsf.getValue())){
				if(com.parseInt(dma_detl.get("decsn_tax_amt")) == 0){
					alert("부가세액이 0원입니다.");
					return false;
				}
			}
		}		

		if (!com.validateData(dma_detl, validData, false)) return false;

		if(dlt_rslt.getRowCount() == 0){
			 alert ("기술지원 내역을 입력해 주시기 바랍니다.");
			 return false;
		}

		if(sbx_supt_clsf.getValue() == "STD030"){
			if (!com.validateData(dlt_rslt,[
				{ id : "gdnm", mandatory : true},
				{ id : "std", mandatory : true},
				{ id : "renwl_no", mandatory : true},
				{ id : "mfg_vend", mandatory : true},
				{ id : "qty", mandatory : true},
				{ id : "unit_price", mandatory : true},
				{ id : "req_amt", mandatory : true},
			], false)
			) return false;
		}else{
			if (!com.validateData(dlt_rslt,[
				{ id : "test_req_nm", mandatory : true},
				{ id : "test", mandatory : true},
				{ id : "qty", mandatory : true},
				{ id : "unit_price", mandatory : true},
				{ id : "req_amt", mandatory : true},
				{ id : "decsn_suply_value", mandatory : true},
				{ id : "decsn_tax_amt", mandatory : true}
			], false)
			) return false;
		}

		var issuCnt = 0;
		for(var i=0; i<dlt_rslt.getRowCount(); i++){
			if(dlt_rslt.getCellData(i, "rst_book_issu") == "1"){
				if(dlt_rslt.getCellData(i, "rst_book_treat_nm") == ""){
					alert("[기술지원내역] 품목/시료명(성적서발행용)을 입력하세요.");
					return false;
				}

				issuCnt++;
			}
		}

		if(rdo_rst_book_issu.getValue() != "STC030" && issuCnt == 0){
			alert ("성적서를 발행하려는 경우 기술지원내역에서 성적서발행을 한개 이상 체크해주시기 바랍니다.");
			return false;
		}	

		if(dma_detl.get("cust_no") == "" && cbx_cmpy_typ.getValue() != "RZB006"){ // 개인이 아닌 경우
			if(!com.isBizID(ibx_bsns_psn_regst_no.getValue())){
				alert("사업자번호가 올바르지 않습니다.");
				return false;
			}
		}

		if(rpa_yn.getValue() == "Y" && rpa_evid_clsf.getValue() == ""){
			alert("RPA 수행여부가 Y일때는 수행항목 선택은 필수입니다");
			return;
		}


		// 장비실적등록
		if(dlt_equip.getRowCount() > 0){
			if (!com.validateData(dlt_equip,[
					{ id : "equip_cd", mandatory : true},
					{ id : "equip_use_start_daytm", mandatory : true},
					{ id : "equip_use_cls_daytm", mandatory : true},
					{ id : "job_time", mandatory : true},
					{ id : "big_item_clsf", mandatory : true},
					{ id : "middle_item_clsf", mandatory : true},
					{ id : "small_item_clsf", mandatory : true},
				], false)
			) return false;
		}

		return true;		
	};	

	/*
     * 저장버튼 클릭시에만 체크
     */ 
	scwin.checkUserSave = function(){
		if(dma_detl.get("accnt_no").substring(0, 2) == "PA"){
			if(dma_detl.get("equip_use_yn") != "Y"){
				alert ("PA계정은 사용 장비 등록이 필수입니다. 담당부서로 문의바랍니다.");
				return false;
			}
		}

		return true;
	};

	/*
     * 저장
     */ 
	scwin.save = function(){
		com.execute(sbm_saveData, {
			successCallback : function(e){
				scwin.data.saveFlag = true;

				com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
					dma_search.set("tech_supt_req_no", e.responseJSON.elData.tech_supt_req_no);						
					scwin.search();
				});
			}
		});
	};

	/*
     * 입금의뢰 팝업호출
     */
	scwin.openDepstReqPop = function(billNo){
		if(dma_detl.get("req_no") != ""){
			var data = {
				depst_mngmt_no : dma_detl.get("req_no"),
				biz_clsf : "S",
				ref_yn : "Y"
			};
		}else{
			var data = {
				bankaccnt_no : dma_detl.get("depst_bankaccnt_no"),
				tax_bill_no : billNo,
				depst_clsf : "FBD021", // 기술지원비
				biz_clsf : "S",
				ref_yn : "Y"
			}; 
		}

		com.pop.open(com.pop.TYPE.DEPST_REQ, data, function(ret){
			if(ret){
				scwin.search();	
			}
		});

	};

	/*
     * 카드결제 팝업호출 파라미터
     */ 
	scwin.getCardParam = function(gubunCd){
		var data={
			gubun_cd : gubunCd,
			kolas_no : dma_detl.get("kolas_no"),
			tech_supt_req_no : dma_detl.get("tech_supt_req_no"),
			cmpy_nm_krchar : dma_cust.get("cmpy_nm_krchar"),
			bsns_psn_regst_no : dma_cust.get("bsns_psn_regst_no"),
			reprs_psn : dma_cust.get("reprs_psn"),
			addr : dma_cust.get("cmpy_addr") + dma_cust.get("cmpy_addr_detls"),
			cunsl_req_psn : dma_detl.get("cunsl_req_psn"), // 의뢰자성명
			decsn_amt : dma_detl.get("decsn_amt"),
			card_appl_no : dma_detl.get("card_appl_no"),
			card_issu_ymd : dma_detl.get("card_issu_ymd"),
		};

		return data;	
	};

	// ------------------------------------ event 함수 ---------------------------------------------//
	/*
     * 목록 버튼 클릭 이벤트
     */ 
	scwin.btn_pre_onclick = function(e) {
		if(scwin.data.saveFlag){
			com.closeTab({});
		}else{
			com.closeTab();
		}
	};

