     * 지원책임자 변경 이벤트
     */
	scwin.ibx_supt_respn_empno_onviewchange = function(info) {
		if(this.getValue() == ""){
			scwin.clearEmpInfo("RESPN");	
		}
	};

	/*
     * 지원책임자 검색 버튼 클릭 이벤트
     */
	scwin.btn_rcpt_psn_onclick = function(e) {
		scwin.getEmpInfo(ibx_rcpt_psn_empno.getValue(), "PSN");
	};

	/*
     * 지원담당자 변경 이벤트
     */
	scwin.ibx_rcpt_psn_empno_onviewchange = function(info) {
		if(this.getValue() == ""){
			scwin.clearEmpInfo("PSN");	
		}
	};

	/*
     * 계정번호 검색 버튼 클릭 이벤트
     */
	scwin.btn_accnt_no_onclick = function(e) {
		var data={
			accnt_no : ibx_accnt_no.getValue().toUpperCase(),
			clsf : "sup",
			ref_yn : (ibx_accnt_no.getValue() == "")? "Y" : "N"
		};

		com.pop.open(com.pop.TYPE.ACNNO, data, function(ret){
			if(ret.cls_ymd < ica_rcpt_ymd.getValue()){
				alert("계정종료일이 지난 계정입니다. 확인바랍니다.");	
				return;
			}

			if(ret.srpt_cd == "SRP030"){
				alert("해당 계정은 집행전용 계정으로 기술지원 접수용도로 사용할 수 없습니다.\n 관련내용은 연구장비실로 문의바랍니다.");
				return;
			}

			if(ret.tax_cd == "FTX002"){
				alert("과세계정에 한해서만 선택 할 수 있습니다.");
				return;
			}

			if(!/PA|PX|PD|PG/.test(ret.accnt_no.substring(0,2))){
				alert("계정은 PA, PD, PX, PG로 시작하는 계정을 선택해야 합니다.");
				return;
			}

			ibx_accnt_no.setValue(ret.accnt_no);			
			ibx_accnt_rspns_nm.setValue(ret.accnt_rspns_nm);
			dma_detl.set("accnt_respn_psn", ret.accnt_rspns);
			dma_detl.set("cls_ymd", ret.cls_ymd);
		});
	};

	/*
     * 계정번호 변경 이벤트
     */
	scwin.ibx_accnt_no_onviewchange = function(info) {
		if(this.getValue() == ""){
			ibx_accnt_rspns_nm.setValue("");
			dma_detl.set("accnt_respn_psn", "");
		}
	};

	/*
     * 기술지원구분 변경 이벤트
     */
	scwin.sbx_supt_clsf_onchange = function(info) {
		if(this.getValue() == "STD030"){
			grp_revsn.show("");

			switch1.setSelectedID("case2");
			case1.hide();
			case2.show("");
		}else{
			grp_revsn.hide();

			switch1.setSelectedID("case1");
			case1.show("");
			case2.hide();
		}
	};

	/*
     * 지역구분 변경 이벤트
     */
	scwin.sbx_region_centr_onviewchange = function(info) {
		$p.data.remove("ldt_cmnCd_SLC_0");

		var codeOptions = [ { code : "SLC", compID :"sbx_supt_team", filter : "mngmtItem1 == '" + this.getValue() + "'"}];		
		com.setCmnCd(codeOptions, function(){
			sbx_supt_team.setValue("");

			let refDataComp = sbx_supt_team.modelControl.getDataComp();
        	refDataComp.setFilterCondition(
	        	refDataComp.getFilterCondition()
		        		+ " || (comCd == 'SLC090')"
		    	); 
			});	

	};	

