	/*
     * 청구공문 버튼 클릭 이벤트
     */
	scwin.btn_offclDoc_onclick = function(e) {
		var node = "tech_supt_req_no=" + dma_detl.get("tech_supt_req_no")
		         + "&cust_no=" + dma_detl.get("cust_no")
		         + "&prcs_state=" + dma_detl.get("prcs_state")
		         ;

		var reportArr = [];
		// 청구서
		reportArr.push({
			ozrCode : "S019",
			ozrPath : "/sup/tec/SptTecReqMnt07.ozr",
			odiNm : "SptTecReqMnt07",
			displayNm : "청구서",
			param: "tech_supt_req_no=" + dma_detl.get("tech_supt_req_no")
		});
		// 접수증
		reportArr.push({
			ozrCode : (dma_detl.get("supt_clsf") != "STD030")? "S017" : "S018",
			ozrPath : (dma_detl.get("supt_clsf") != "STD030")? "/sup/tec/SptTecReqMnt01.ozr" : "/sup/tec/SptTecReqMnt06.ozr",
			odiNm : (dma_detl.get("supt_clsf") != "STD030")? "SptTecRcptInfo01" : "SptTecRcptInfo06",
			displayNm : "접수증",
			param: node
		});
        // 계약서
        reportArr.push({
			ozrCode : (dma_detl.get("supt_clsf") != "STD030")? "S015" : "S016",
			ozrPath : (dma_detl.get("supt_clsf") != "STD030")? "/sup/tec/SptTecReqMnt02.ozr" : "/sup/tec/SptTecReqMnt03.ozr",
			odiNm : (dma_detl.get("supt_clsf") != "STD030")? "SptTecRcptInfo02" : "SptTecRcptReg03",
			displayNm : "계약서",
			param: node
		});

		// 견적서
        reportArr.push({
			ozrCode : "S021",
			ozrPath : "/sup/tec/SptTecRcpEstimate01.ozr",
			odiNm : "SptTecRcpEstimate01",
			displayNm : "견적서",
			param: "kolas_no=" + dma_detl.get("kolas_no")
		});

        // 오즈 뷰어 팝업 호출
        com.oz.openPopup(reportArr, true);	
	};

