	/*
     * 복사 버튼 클릭 이벤트
     */
	scwin.btn_copy_onclick = function(e) {
		if(cbx_prcs_state.getValue() == "STE040"){
			if(!confirm("기술지원 취소건입니다. 복사하시겠습니까?")) return;
		}
		scwin.data.isCopy = true;
		btn_mngSave.hide(); // 관리자저장
		btn_techSave.hide(); // 기술내역저장

		for(var i=0; i<dlt_equip.getRowCount(); i++){
			if(dlt_equip.getCellData(i, "aset_state") == "AAI050"){
				alert("지원장비 : " + dlt_equip.getCellData( i, "equip_cd") + " 불용장비는 기술지원 불가능합니다.");
				return;
			}
		}

		if(dma_detl.get("discnt_rt") == '100'){
			var msg = "할인율이 100% 입니다. \n 할인율이 100% 일 경우, 저장후 삭제가 불가능합니다.\n 할인율을 삭제하시고  복사하시겠습니까? \n 취소를 누르시면 할인율이 100% 로 복사됩니다.";
			if(confirm){
				dma_detl.set("discnt_rt", 0);
			}
		}

		var today = scwin.data.today;
		if(dma_detl.get("cls_ymd") < today){
			alert("계정종료일이 지난 계정입니다. 확인바랍니다.");
			dma_detl.set("accnt_no", "");
			dma_detl.set("accnt_respn_nm", "");
			dma_detl.set("accnt_respn_psn", "");
		}

		// 기술지원접수번호, 상담접수번호, kolas 번호, 계산서번호, 계산서발행일자  초기화
		dma_detl.set("tech_supt_req_no", "");
		dma_detl.set("cunsl_rcpt_no", "");
		dma_detl.set("kolas_no", "");
		dma_detl.set("bill_no", "");
		dma_detl.set("bill_issu_ymd", "");
		dma_detl.set("depst_bankaccnt_no", "");
		sbx_depst_bank.setValue("");
		dma_detl.set("card_appl_no", "");
		dma_detl.set("card_issu_ymd", "");
		dma_detl.set("req_no", "");
		dma_detl.set("apr_state", "");
		dma_detl.set("proof_yn", "STO010"); // 세금계산서
		dma_detl.set("cud_type", "C");	
		dma_detl.set("prcs_state", "STE030");
		dma_detl.set("rpa_yn", "");
		dma_detl.set("rpa_result_yn", "");
		dma_detl.set("rpa_evid_clsf", "");
		cbx_prcs_state.setValue("");

		// 초기화
		scwin.init();

		// 기술지원 실적내역 그리드 입력가능하도록
		grd_rslt.setColumnReadOnly(0, false);
		for(var i=2; i<10; i++){			
			grd_rslt.setColumnReadOnly(i, false)
		}
		for(var i=0; i<dlt_rslt.getRowCount(); i++){
			dlt_rslt.setCellData(i, "rst_req_cnt", 0);	
			dlt_rslt.setCellData(i, "cud_type", "C");
		}

		for(var i=0; i<dlt_equip.getRowCount(); i++){
			dlt_equip.setCellData(i, "cud_type", "C");
		}

		scwin.setForm();
	};

	/*
     * 고객명 검색 버튼 클릭 이벤트
