	scwin.btn_vend_onclick = function(e) {
		com.alert("공지 ‘[기업지원실] (재안내) 코로나19 대응을 위한 \n연구장비 이용료 감면 시행 안내’에 따라 지원고객이 중소기업인 \n경우 10% 감면 혜택을 반드시 적용하여 주시기 바랍니다. ", function(){
			scwin.getVendInfo(ibx_vend_nm.getValue(), "CUST");
		});	
	};

