	/*
     * 계좌번호 검색 버튼 클릭 이벤트
     */
	scwin.btn_bankaccnt_no_onclick = function(e) {
		var data={
			biz_clsf : "S",
			ref_yn : "Y"
		};
		com.pop.open(com.pop.TYPE.BNKAC, data, async function(ret){
			const values = await com.getExceptAwait('XEK052', 'S306');
			if(values.indexOf(ret.bankaccnt_no) == -1){
				alert("수익금계좌만 사용 할 수 있습니다.");
				return;
			}

			dma_detl.set("depst_bankaccnt_no", ret.bankaccnt_no);
			dma_detl.set("depst_bank_cd", ret.bank);
		});
	};

	/*
     * 성적서발급고객 검색 버튼 클릭 이벤트
     */
	scwin.btn_rst_book_issu_cust_onclick = function(e) {
