	};

	/*
     * 기술지원실적등록 변경 이벤트
     */
	scwin.grd_rslt_onviewchange = function(info) {
		var row = info.rowIndex;
		var col = info.colIndex;

		if(/test_req_nm|test|rst_book_issu|rmk|rst_book_treat_nm/.test(grd_rslt.getColumnID(col))) return;

		scwin.setDecsnAmt(row, col, "01");
	};

	/*
     * 시험료지원구분 변경 이벤트(1)
     */
	scwin.sbx_supt_work_clsf_onviewchange = function(info) {
		if(dma_detl.get("cust_no") == "" && cbx_cmpy_typ.getValue()!="RZB006"){
			alert("고객정보를 먼저 입력하세요.");
			this.setValue("");
			return;	
		}

		if(/STG020/.test(info.newValue)){ // 인천장비지원사업
			spn_discnRt.setValue("지원율(%)");
		}else{
			spn_discnRt.setValue("할인율(%)");
		}

		if(/STG020/.test(info.newValue)){ // 인천장비지원사업
			ibx_discnt_rt.setValue(50);
			dma_detl.set("rmk", sbx_supt_work_clsf.getText());

		}else if(/STG030/.test(info.newValue)){ // 중기부 연구기반활용사업
			ibx_discnt_rt.setValue("");

		}else if(/STG040/.test(info.newValue)){ // 코로나19
			ibx_discnt_rt.setValue(10);
			dma_detl.set("rmk", "코로나19 10%할인");

		}else if(/STG050|STG060/.test(info.newValue)){
			dma_search.set("vend_cd", dma_detl.get("cust_no"));
			com.execute(sbm_selectVoucher, {
				successCallback : function(e){
					debugger;
					if(e.responseJSON.voucher_yn != "Y"){
						alert('(기업협력지원실) 케이-바우처로 제공되는 기술지원은 \n기존 납부한 기업 회비에서 바우처 사용금액을 차감하는 형태로 \n할인률을 제외한 금액은 추후 별도로 여입 예정이며, \n할인 금액은 PJF23010 계정 재료비 명목으로 사용가능(문의 : 041-5898-201)');
						this.setValue("");

						ibx_discnt_rt.setValue(0);
						dma_detl.set("depst_bankaccnt_no", "");
						dma_detl.set("depst_bank_cd", "");
					}else{
						ibx_discnt_rt.setValue(16.6667); //바우처의 경우 16.6667 고정
						dma_detl.set("depst_bankaccnt_no", "391-003560-04-531");
						dma_detl.set("depst_bank_cd", "FAA003");
					}					
				}
			});
		}else if(/STG070/.test(info.newValue)){ // 인천반도체지원사업
			ibx_discnt_rt.setValue(30);

		}else if(/STG080/.test(info.newValue)){ // 중진공 MOU 협약
			ibx_discnt_rt.setValue(0);
			dma_detl.set("rmk", "중진공 MOU 협약할인");
		}

		for(var i=0; i<dlt_rslt.getRowCount(); i++){
			scwin.setDecsnAmt(i, grd_rslt.getColumnIndex("unit_price"), (sbx_supt_clsf.getValue() == "STD030")? "02" : "01");
		}
	};

	/*
     * 시험료지원구분 변경 이벤트(2)
     */
	scwin.sbx_supt_work_clsf_onchange = function(info) {
		if(info.newValue == null || info.newValue == "") return;
		if(dma_detl.get("tech_supt_req_no") == "") return;

		if(/STG050|STG060/.test(this.getValue())){
			btn_sms.hide();
			btn_card_app.hide();
			btn_card_prcs.hide();
			btn_bill.hide();
			btn_depst.hide();
		}else{
			if(scwin.data.cardPayYn){
				btn_sms.show("");
				btn_card_app.show("");
				btn_card_prcs.show("");
			}
			btn_bill.show("");
			btn_depst.show("");
		}
	};

	/*
     * 기술지원실적등록(교정) 변경 이벤트
     */
	scwin.grd_revsn_rslt_onviewchange = function(info) {
		var row = info.rowIndex;
		var col = info.colIndex;

		scwin.setDecsnAmt(row, col, "02");
	};

	/*
     * 기술지원실적등록(교정) 변경 이벤트
     */
	scwin.cbx_cmpy_typ_onchange = function(info) {
		if(this.getValue() == "RZB006"){
			spn_bsns_regst_psn_no.setValue("주민등록번호");
			ibx_bsns_psn_regst_no.hide();
			ibx_resid_no.show("");

			ibx_vend_nm.setDisabled(false);
		}else{
			spn_bsns_regst_psn_no.setValue("사업자 등록번호");
			ibx_resid_no.hide();
			ibx_bsns_psn_regst_no.show("");

			ibx_vend_nm.setDisabled(true);
		}			
	};

	/*
     * 계산서발행 버튼 클릭 이벤트
     */
	scwin.btn_bill_onclick = function(e) {
		if(dma_detl.get("depst_bankaccnt_no") == ""){
			alert("계좌발급 후 계산서 발행해 주세요.");
			return;
		}

		var data={
			biz_clsf : "S",
			type : "C",
			tax_type : "01", // 비과세 계정일 경우에는 03
			vend_cd : dma_detl.get("cust_no"),
			tech_supt_req_no : dma_detl.get("tech_supt_req_no"),
			cls_ymd : dma_detl.get("cls_ymd"),
			accnt_no : dma_detl.get("accnt_no"),
			rcpt_psn_syspayno : dma_detl.get("rcpt_psn_syspayno"),
			tax_bill_no : dma_detl.get("bill_no"),
			ref_yn : "Y"
		};

		com.pop.open(com.pop.TYPE.RVN_BILL, data, function(ret) {
			if(ret){	
				scwin.data.saveFlag = true;	
				scwin.search();

				if(ret.demnd_recv_clsf == "2" && dma_detl.get("req_no") == ""){
					scwin.openDepstReqPop(ret.tax_bill_no);
				}
			}
		});
	};

	/*
     * 기술지원장비등록 변경 이벤트
     */
	scwin.grd_equip_onviewchange = function(info) {
		var row = info.rowIndex;
		var col = info.colIndex;

		if(grd_equip.getColumnID(col) == "job_time"){
			var startDay = dlt_equip.getCellData(row, "equip_use_start_daytm");
			var clsDay = dlt_equip.getCellData(row, "equip_use_cls_daytm"); 

			if(startDay == ""){
				alert("작업시작일자가 없습니다.");
				dlt_equip.setCellData(row, "job_time", 0);
				return;
			}

			if(clsDay == ""){
				alert("작업종료일자가 없습니다.");
				dlt_equip.setCellData(row, "job_time", 0);
				return;
			}

			if(dlt_equip.getCellData(row, "equip_use_start_hour") == "" || dlt_equip.getCellData(row, "equip_use_start_minute") == ""){
				alert("작업시작시간이 존재하지 않습니다.");
				dlt_equip.setCellData(row, "job_time", 0);
				return;
			}

			if(dlt_equip.getCellData(row, "equip_use_cls_hour") == "" || dlt_equip.getCellData(row, "equip_use_cls_minute") == ""){
				alert("작업종료시간이 존재하지 않습니다.");
				dlt_equip.setCellData(row, "job_time", 0);
				return;
			}


			var sYear = startDay.substr(0,4);
			var sMonth = startDay.substr(4,2);
			var sDay = startDay.substr(6,2);								 
			var startDate = new Date(sYear, sMonth -1, sDay, dlt_equip.getCellData(row, "equip_use_start_hour"), dlt_equip.getCellData(row, "equip_use_start_minute"), 0);  // date로 변경	(변경전 0,0,0)

			var cYear = clsDay.substr(0,4);
			var cMonth = clsDay.substr(4,2);
			var cDay = clsDay.substr(6,2);
			var clsDate = new Date(cYear, cMonth -1, cDay, dlt_equip.getCellData(row, "equip_use_cls_hour"), dlt_equip.getCellData(row, "equip_use_cls_minute"), 59);  // date로 변경 (변경전 23,59,59)

			var diff = Math.ceil((clsDate - startDate) / (1000*60*60));
			if(diff <= 0){
				dlt_equip.setCellData(row, "job_time", 0);
				alert("종료일자가 작을수 없습니다.");
				return;
			}

			var jobTime = com.parseInt(dlt_equip.getCellData(row, "job_time" ));
			if(jobTime > diff){
				dlt_equip.setCellData(row, "job_time", 0);
				alert("사용 시간이 작업시간보다 많을수 없습니다. 작업시간[" + diff + "]");
				return;
			}
		}else if(grd_equip.getColumnID(col) == "big_item_clsf"){
			if(info.newValue != ""){
				dma_search.set("bigcls", info.newValue);
				com.execute(sbm_selectTecMidClsf, {
					successCallback : function(e){				
						dlt_tecMidClsf.removeAll();
						dlt_tecMidClsf.setJSON(e.responseJSON.resIndTeccVoList);						
					}
				});

//				dlt_tecMidClsf.removeColumnFilter("cd");
//				dlt_tecMidClsf.setColumnFilter({type:'row', colIndex:'bigcls', key:info.newValue, condition:'and'});
//				dlt_tecMidClsf.reform();
			}
		}else if(grd_equip.getColumnID(col) == "middle_item_clsf"){
			if(info.newValue != ""){
				dma_search.set("mid_clsf", info.newValue);
				com.execute(sbm_selectTecSmlClsf, {
					successCallback : function(e){				
						dlt_tecSmlClsf.removeAll();
						dlt_tecSmlClsf.setJSON(e.responseJSON.resIndTeccVoList);						
					}
				});
			}
		}
	};

	/*
     * 기술지원실적등록 추가 버튼 이벤트
     */
	scwin.btn_rslt_add_onclick = function(e) {
