	/*
     * 기술지원장비등록 추가 버튼 이벤트
     */
	scwin.btn_equip_add_onclick = function(e) {
		scwin.addGridRow("EQUIP");
	};

	/*
     * 기술지원장비등록 그리드 클릭 이벤트
     */
	scwin.grd_equip_ontextimageclick = function(row,col) {
		if(grd_equip.getColumnID(col) == "equip_cd"){
			var data={
				biz_clsf : "S",
				aset_clsf : "ABA050",// 기술장비
				region_centr : sbx_region_centr.getValue(),
				ref_yn : "Y"
			};

			com.pop.open(com.pop.TYPE.ASET, data, function(ret){
				var retArr = (Array.isArray(ret))? ret : new Array(ret);

				var cnt = 0;				
				for(var i=0; i<retArr.length; i++){
					var retData = retArr[i];
					var dupleFlag = false;
					for(var j=0; j<dlt_equip.getRowCount(); j++){
						if(retData.aset_no == dlt_equip.getCellData(j, "equip_cd")){
							alert("선택한 자료가 [" + (j+1) + "]행과 중복되고 있습니다.");
							dupleFlag = true;
							break;
						}			
					}

					if(!dupleFlag){
						var data ={
							equip_cd : retData.aset_no,
							aset_nm : retData.aset_nm,
							equip_use_start_daytm : scwin.data.today,
							equip_use_start_hour : "09",
							equip_use_start_minute : "00",
							equip_use_cls_daytm : scwin.data.today,
							equip_use_cls_hour : "18",
							equip_use_cls_minute : "00",
						};
						if(cnt == 0){
							dlt_equip.setRowJSON(row+cnt, data, true);
						}else{
							dlt_equip.setRowJSON(row+cnt, data);
						}

						cnt++;
					}
				}
			});
		}
	};

	/*
     * 기술지원 완료선택 변경 이벤트
     */
	scwin.cbx_prcs_state_onviewchange = function(info) {
		if(this.getValue() == "STE040"){
			if(dma_detl.get("bill_no") != ""){
				alert("계산서가 발행되어 수행취소 변경이 불가능합니다. 계산서 취소후 수행취소 해주세요");
				cbx_prcs_state.setValue("");
				return;
			}

			if(dma_detl.get("card_appl_no") != ""){
				alert("카드승인내역이 존재하므로 수행취소 변경이 불가능합니다.");
				cbx_prcs_state.setValue("");
				return;
			}
		}
	};

	/*
     * 저장 버튼 클릭 이벤트
     */
	scwin.btn_save_onclick = function(e) {	
		if(cbx_prcs_state.getValue() == "STE040"){// 기술지원 취소
			if(confirm("기술지원을 취소하시겠습니까?")){
				dma_search.set("cunsl_rcpt_no", dma_detl.get("cunsl_rcpt_no"));

				com.execute(sbm_saveCancel, {
					successCallback : function(e){
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
							scwin.search();
						});
					}
				});	
			}
		}else{
			if(cbx_cmpy_typ.getValue() == "RZB006"){
				dma_cust.set("bsns_psn_regst_no", ibx_resid_no.getDisplayValue());
			}else{
				dma_cust.set("bsns_psn_regst_no", ibx_bsns_psn_regst_no.getDisplayValue());
			}

			if(!scwin.checkUserSave()) return;

			if(!scwin.preSave()) return;	

			if(!scwin.data.mngrSave){ // 관리자 저장이 아닌 경우
				// 할인율이 100% 인 경우, 협약에 의해 결의는 재정운영실에 따로 함.따라서 처리상태는 결의생성으로 업데이트 해줌
				if(com.parseInt(dma_detl.get("decsn_amt")) == 0){
					dma_detl.set("prcs_state", "STE070"); 	
				}else{
					if(dma_detl.get("cud_type") == "C"){
						dma_detl.set("prcs_state", "STE030");
					}
				}
			}

			if(dma_detl.get("equip_use_yn") == "Y" && dlt_equip.getRowCount() == 0){
				var strInfo = "장비사용실적이 등록되지 않았습니다.\n 장비실적은 결의생성전에는  등록되어야 합니다.\n"
						 	+ "장비사용내역이 없는 기술지원의 경우, 장비사용여부를 미사용으로 선택해주시기 바랍니다.\n"
						    + "장비실적없이 저장하시겠습니까?";

				com.confirm(strInfo, function(ret){
					if(ret) scwin.save();
				});
			}else{
				com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret){
					if(ret){
						scwin.save();
					}else{
						// 취소시 기술지원내역 등록 및 장비등록 삭제데이터 복구
						dlt_rslt.setJSON(dlt_rslt.getDeletedJSON(), true);
						dlt_rslt.reform();

						dlt_equip.setJSON(dlt_equip.getDeletedJSON(), true);
						dlt_equip.reform();
					}
				});	
			}			
		}
	};

	/*
     * 성적서 발행 라디오 이벤트
     */
	scwin.rdo_rst_book_issu_onchange = function(info) {
		if(this.getValue() == "STC010"){ //STC010 KOLAS
			rdo_rece_states_cd.show("");
		}else{
			rdo_rece_states_cd.hide();	
		}

		if(this.getValue() == "STC020"){ // STC020 일반 
			ibx_rst_book_rmk.setDisabled(false);
		}else{
			ibx_rst_book_rmk.setDisabled(true);
		}

		if(this.getValue() == "STC030"){ // 미발급
			spn_test_way_and_unusual_item.hide();

			grp_rst_book_1.setDisabled(true);
			grp_rst_book_2.setDisabled(true);
		}else{
			spn_test_way_and_unusual_item.show("");

			grp_rst_book_1.setDisabled(false);
			grp_rst_book_2.setDisabled(false);
		}
	};

	/*
     * 장비사용여부 라디오 이벤트
     */
	scwin.rdo_equip_use_yn_onchange = function(info) {
