	/*
     * 결제증빙구분 변경 이벤트
     */
	scwin.rdo_proof_yn_onchange = function(info) {
		if(this.getValue() == "STO020"){
			if(dma_detl.get("prcs_state") != "STE090"){ // 카드결의
				btn_card_cancl.show("");
				if(scwin.data.cardPayYn){
					btn_sms.show("");
				}
			}

			if(scwin.data.cardPayYn){
				btn_card_app.show("");
				btn_card_prcs.show("");
			}

			btn_bill.hide();
			btn_rev_issu.hide();
			btn_depst.hide();
			btn_srcDepst.hide();

			spn_bill_msg.hide();
			spn_depst_msg.hide();
		}else{	
			spn_bill_msg.show("");
			btn_srcDepst.show("");
			spn_depst_msg.show("");

			if(dma_detl.get("recp_yn") == "Y"){
				btn_bill.hide();
				btn_rev_issu.show("");
			}else if(dma_detl.get("recp_yn") == "N" && dma_detl.get("bill_no") != ""){
				btn_bill.show("");	
				btn_rev_issu.hide();				
			}else{
				btn_rev_issu.show("");
				btn_bill.show("");	
			}

			// 영수계산서 발행 후 입금의뢰 가능
			if(dma_detl.get("prcs_state") == "STE060" && dma_detl.get("demnd_recv_clsf") == "2"){
				btn_depst.show("");
				btn_depst.setDisabled(false);
			}

			btn_card_app.hide();
			btn_sms.hide();
		}
	};

	/*
     * 실시간입금조회 버튼 클릭 이벤트
     */
	scwin.btn_srcDepst_onclick = function(e) {
		var data={ref_yn : "Y", 
		          type : "R",
		          biz_clsf : "S",
		          bankaccnt_no : dma_detl.get("depst_bankaccnt_no"),
		          depst_knd : "FAN905"
		          };
		com.pop.open(com.pop.TYPE.DEPST, data);
	};

	scwin.rpa_yn_onchange = async function(info) {

		let todayDate = new Date();

		let year = todayDate.getFullYear(); // 년도
		let month = todayDate.getMonth() + 1;  // 월
		let date = todayDate.getDate();  // 날짜

		month = month - 2;
		date = 01;

		if(month<10){
			month = "0"+month;
		}
		else{
			month = month.toString();
		}

		if(date<10){
			date = "0"+date;
		}
		else{
			date = date.toString();
		}

		var modifyDate = year+month+date.toString();
		if(this.getValue() == "Y"){
			if(ica_rcpt_ymd.getValue()<modifyDate){
				alert("RPA 수행요청은 접수일이 현재일짜 기준, 전전월 1일 이전일 때 선택할 수 없습니다.");
				rpa_yn.setValue(info.oldValue);
			}
		}

		var prcs_state = dma_detl.get("prcs_state");

		if(rpa_yn.getValue() == "Y"){
			rpa_evid_clsf.setDisabled("false");
		}
		else{
			rpa_evid_clsf.setDisabled("true");
			rpa_evid_clsf.setValue(" -선택- ");
		}

		const values = await com.getExceptAwait('XEK055', 'S306');
		if(values.indexOf(prcs_state) > 0){
			alert("현재 기술지원 단계에서는 RPA관련 항목 수정불가함.");
			return;
		}


	};

	scwin.rpa_result_yn_onchange = async function(info) {
		if(rpa_result_yn.getValue() == "Y"){
			rpa_yn.setDisabled("true");
			rpa_evid_clsf.setDisabled("true");
			rpa_result_yn.setDisabled("true");
		}
		else{
			rpa_result_yn.setDisabled("false");	
			rpa_yn.setDisabled("false");
			rpa_evid_clsf.setDisabled("false");
		}

		var prcs_state = dma_detl.get("prcs_state");
		const values = await com.getExceptAwait('XEK055', 'S306');
		if(values.indexOf(prcs_state) > 0){
			alert("현재 기술지원 접수 단계에서는 RPA관련 항목 수정불가함.");
			return;
		}


	};


	scwin.ica_rcpt_ymd_onviewchange = function(info) {

		let todayDate = new Date();

		let year = todayDate.getFullYear(); // 년도
		let month = todayDate.getMonth() + 1;  // 월
		let date = todayDate.getDate();  // 날짜


		month = month - 2;
		date = 01;

		if(month<10){
			month = "0"+month;
		}
		else{
			month = month.toString();
		}

		if(date<10){
			date = "0"+date;
		}
		else{
			date = date.toString();
		}

		var modifyDate = year+month+date.toString();
		if(rpa_yn.getValue() == "Y"){
			if(this.getValue()<=modifyDate){
				alert("RPA 수행요청 건은 [기술지원접수일]을 현재기준 전전월 1일 이전으로 수정할 수 없습니다.");
				ica_rcpt_ymd.setValue(info.oldValue);
				return;
			}
		}

	};
